import { logger } from '../utils/logger';
import {
  ActionType,
  ActionDefinition,
  ActionResult,
  ActionProbability,
  ActionCategory,
  ActionTargetType,
  ActionMovesActor,
} from '../types/shared/actions';
import { Unit, UnitOrder } from './UnitManager';
import { SINGLE_MOVE } from './constants/MovementConstants';

// Action definitions based on freeciv classic ruleset
// @reference freeciv/common/actions.c
const ACTION_DEFINITIONS = {
  // Basic movement actions
  [ActionType.MOVE]: {
    id: ActionType.MOVE,
    name: 'Move',
    description: 'Move unit to target tile',
    category: ActionCategory.BASIC,
    requirements: [],
    targetType: ActionTargetType.TILE,
    consumes_actor: false,
    moves_actor: ActionMovesActor.MOVES_TO_TARGET,
  },

  [ActionType.ATTACK]: {
    id: ActionType.ATTACK,
    name: 'Attack',
    description: 'Attack enemy unit or city',
    category: ActionCategory.MILITARY,
    requirements: [{ type: 'unit_type', value: ['warrior', 'archer', 'spearman'], present: true }],
    targetType: ActionTargetType.UNIT,
    consumes_actor: false,
    moves_actor: ActionMovesActor.STAYS,
  },

  [ActionType.FORTIFY]: {
    id: ActionType.FORTIFY,
    name: 'Fortify',
    description: 'Fortify unit for defensive bonus',
    hotkey: 'F',
    category: ActionCategory.BASIC,
    requirements: [{ type: 'unit_type', value: ['warrior', 'archer', 'spearman'], present: true }],
    targetType: ActionTargetType.NONE,
    consumes_actor: false,
    moves_actor: ActionMovesActor.STAYS,
  },

  [ActionType.SENTRY]: {
    id: ActionType.SENTRY,
    name: 'Sentry',
    description: 'Put unit on sentry duty',
    hotkey: 'S',
    category: ActionCategory.BASIC,
    requirements: [],
    targetType: ActionTargetType.NONE,
    consumes_actor: false,
    moves_actor: ActionMovesActor.STAYS,
  },

  [ActionType.WAIT]: {
    id: ActionType.WAIT,
    name: 'Wait',
    description: 'Wait and preserve movement points',
    hotkey: 'W',
    category: ActionCategory.BASIC,
    requirements: [],
    targetType: ActionTargetType.NONE,
    consumes_actor: false,
    moves_actor: ActionMovesActor.STAYS,
  },

  [ActionType.GOTO]: {
    id: ActionType.GOTO,
    name: 'Go To',
    description: 'Set destination for unit movement',
    hotkey: 'G',
    category: ActionCategory.MOVEMENT,
    requirements: [],
    targetType: ActionTargetType.TILE,
    consumes_actor: false,
    moves_actor: ActionMovesActor.MOVES_TO_TARGET,
  },

  [ActionType.FOUND_CITY]: {
    id: ActionType.FOUND_CITY,
    name: 'Found City',
    description: 'Found a new city at this location',
    hotkey: 'B',
    category: ActionCategory.BUILD,
    requirements: [{ type: 'unit_flag', value: 'canFoundCity', present: true }],
    targetType: ActionTargetType.NONE,
    consumes_actor: true,
    moves_actor: ActionMovesActor.STAYS,
  },

  [ActionType.BUILD_ROAD]: {
    id: ActionType.BUILD_ROAD,
    name: 'Build Road',
    description: 'Build a road on this tile',
    hotkey: 'R',
    category: ActionCategory.BUILD,
    requirements: [{ type: 'unit_flag', value: 'canBuildImprovements', present: true }],
    targetType: ActionTargetType.NONE,
    consumes_actor: false,
    moves_actor: ActionMovesActor.STAYS,
  },

  [ActionType.AUTO_EXPLORE]: {
    id: ActionType.AUTO_EXPLORE,
    name: 'Auto Explore',
    description: 'Automatically explore unknown areas',
    hotkey: 'X',
    category: ActionCategory.AUTOMATION,
    requirements: [],
    targetType: ActionTargetType.NONE,
    consumes_actor: false,
    moves_actor: ActionMovesActor.STAYS,
  },

  // Add placeholder definitions for other actions
  [ActionType.SKIP_TURN]: {
    id: ActionType.SKIP_TURN,
    name: 'Skip Turn',
    description: 'Skip unit turn',
    category: ActionCategory.BASIC,
    requirements: [],
    targetType: ActionTargetType.NONE,
    consumes_actor: false,
    moves_actor: ActionMovesActor.STAYS,
  },

  // Simplified definitions for other actions (to be expanded)
  ...Object.fromEntries(
    Object.values(ActionType)
      .filter(
        actionType =>
          ![
            ActionType.MOVE,
            ActionType.ATTACK,
            ActionType.FORTIFY,
            ActionType.SENTRY,
            ActionType.WAIT,
            ActionType.GOTO,
            ActionType.FOUND_CITY,
            ActionType.BUILD_ROAD,
            ActionType.AUTO_EXPLORE,
            ActionType.SKIP_TURN,
          ].includes(actionType)
      )
      .map(actionType => [
        actionType,
        {
          id: actionType,
          name: actionType.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase()),
          description: `Perform ${actionType.replace(/_/g, ' ').toLowerCase()}`,
          category: ActionCategory.BASIC,
          requirements: [],
          targetType: ActionTargetType.NONE,
          consumes_actor: false,
          moves_actor: ActionMovesActor.STAYS,
        },
      ])
  ),
} as unknown as Record<ActionType, ActionDefinition>;

export class ActionSystem {
  private gameId: string;
  private gameManagerCallback?: {
    foundCity: (
      gameId: string,
      playerId: string,
      name: string,
      x: number,
      y: number
    ) => Promise<string>;
    requestPath: (
      playerId: string,
      unitId: string,
      targetX: number,
      targetY: number
    ) => Promise<{ success: boolean; path?: any; error?: string }>;
  };

  constructor(
    gameId: string,
    gameManagerCallback?: {
      foundCity: (
        gameId: string,
        playerId: string,
        name: string,
        x: number,
        y: number
      ) => Promise<string>;
      requestPath: (
        playerId: string,
        unitId: string,
        targetX: number,
        targetY: number
      ) => Promise<{ success: boolean; path?: any; error?: string }>;
    }
  ) {
    this.gameId = gameId;
    this.gameManagerCallback = gameManagerCallback;
  }

  /**
   * Get action definition by type
   */
  getActionDefinition(actionType: ActionType): ActionDefinition | null {
    return ACTION_DEFINITIONS[actionType] || null;
  }

  /**
   * Check if unit can perform action
   * @reference freeciv/common/actions.c action_prob()
   */
  canUnitPerformAction(
    unit: Unit,
    actionType: ActionType,
    targetX?: number,
    targetY?: number
  ): boolean {
    const actionDef = this.getActionDefinition(actionType);
    if (!actionDef) {
      return false;
    }

    // Check basic requirements
    for (const req of actionDef.requirements) {
      if (!this.checkRequirement(unit, req, targetX, targetY)) {
        return false;
      }
    }

    // Check action-specific conditions
    switch (actionType) {
      case ActionType.FORTIFY:
        // Can only fortify if not already fortified and has movement
        return !unit.fortified && unit.movementLeft > 0;

      case ActionType.SENTRY:
        // Can sentry if has movement
        return unit.movementLeft > 0;

      case ActionType.MOVE:
      case ActionType.GOTO:
        // Need target coordinates and movement points
        return targetX !== undefined && targetY !== undefined && unit.movementLeft > 0;

      case ActionType.FOUND_CITY:
        // Check if settler and has movement points
        if (unit.unitTypeId !== 'settler' || unit.movementLeft <= 0) {
          return false;
        }
        // Additional validation would be done in executeFoundCity
        return this.canFoundCityAtLocation(unit, unit.x, unit.y);

      case ActionType.BUILD_ROAD:
        // Check if worker
        return unit.unitTypeId === 'worker';

      default:
        return true;
    }
  }

  /**
   * Get action probability for unit
   */
  getActionProbability(
    unit: Unit,
    actionType: ActionType,
    targetX?: number,
    targetY?: number
  ): ActionProbability {
    if (!this.canUnitPerformAction(unit, actionType, targetX, targetY)) {
      return { min: 0, max: 0 };
    }

    // Most basic actions have 100% success rate
    switch (actionType) {
      case ActionType.FORTIFY:
      case ActionType.SENTRY:
      case ActionType.WAIT:
      case ActionType.GOTO:
      case ActionType.FOUND_CITY:
      case ActionType.BUILD_ROAD:
        return { min: 200, max: 200 }; // 100% in freeciv probability format

      case ActionType.ATTACK:
        // Combat probability would be calculated based on unit strengths
        return { min: 100, max: 150 }; // 50-75% example

      default:
        return { min: 200, max: 200 };
    }
  }

  /**
   * Execute action for unit
   */
  async executeAction(
    unit: Unit,
    actionType: ActionType,
    targetX?: number,
    targetY?: number
  ): Promise<ActionResult> {
    const actionDef = this.getActionDefinition(actionType);
    if (!actionDef) {
      return {
        success: false,
        message: `Unknown action: ${actionType}`,
      };
    }

    if (!this.canUnitPerformAction(unit, actionType, targetX, targetY)) {
      return {
        success: false,
        message: `Unit cannot perform ${actionDef.name}`,
      };
    }

    logger.info(`Executing action ${actionType} for unit ${unit.id}`, {
      unitId: unit.id,
      action: actionType,
      targetX,
      targetY,
    });

    // Execute action-specific logic
    switch (actionType) {
      case ActionType.FORTIFY:
        return await this.executeFortify(unit);

      case ActionType.SENTRY:
        return await this.executeSentry(unit);

      case ActionType.WAIT:
        return await this.executeWait(unit);

      case ActionType.GOTO:
        return await this.executeGoto(unit, targetX!, targetY!);

      case ActionType.FOUND_CITY:
        return await this.executeFoundCity(unit);

      case ActionType.BUILD_ROAD:
        return await this.executeBuildRoad(unit);

      default:
        return {
          success: false,
          message: `Action ${actionType} not yet implemented`,
        };
    }
  }

  /**
   * Check if a city can be founded at the given location
   */
  private canFoundCityAtLocation(_unit: Unit, _x: number, _y: number): boolean {
    // Basic validation - more detailed checks would require access to MapManager and game state
    // These are the rules that can be checked without external dependencies

    // TODO: Add the following validation rules when we have access to MapManager:
    // 1. Check terrain type (some terrains like ocean cannot have cities)
    // 2. Check minimum distance from other cities (usually 2 tiles in Freeciv)
    // 3. Check if tile is within map bounds
    // 4. Check if tile is owned by another player
    // 5. Check if there are hostile units on the tile

    return true; // Simplified for now
  }

  /**
   * Check if requirement is satisfied
   */
  private checkRequirement(
    unit: Unit,
    requirement: any,
    _targetX?: number,
    _targetY?: number
  ): boolean {
    switch (requirement.type) {
      case 'unit_type': {
        const validTypes = Array.isArray(requirement.value)
          ? requirement.value
          : [requirement.value];
        return requirement.present
          ? validTypes.includes(unit.unitTypeId)
          : !validTypes.includes(unit.unitTypeId);
      }

      case 'unit_flag':
        // This would check unit capabilities from ruleset data
        // For now, simplified check based on unit type
        if (requirement.value === 'canFoundCity') {
          return unit.unitTypeId === 'settler';
        }
        if (requirement.value === 'canBuildImprovements') {
          return unit.unitTypeId === 'worker';
        }
        return true;

      default:
        return true;
    }
  }

  // Action execution methods
  private async executeFortify(unit: Unit): Promise<ActionResult> {
    // This would be handled by UnitManager
    return {
      success: true,
      message: `${unit.unitTypeId} fortified`,
    };
  }

  private async executeSentry(unit: Unit): Promise<ActionResult> {
    return {
      success: true,
      message: `${unit.unitTypeId} on sentry duty`,
    };
  }

  private async executeWait(unit: Unit): Promise<ActionResult> {
    return {
      success: true,
      message: `${unit.unitTypeId} waiting`,
    };
  }

  /**
   * Execute goto command for a unit - moves unit along pathfinding path
   * Implements freeciv-web style goto with server-side pathfinding
   *
   * @reference freeciv-web/freeciv-web/src/main/webapp/javascript/control.js:do_map_click() - Client goto execution
   * @reference freeciv-web/freeciv/patches/goto_fcweb.patch:handle_web_goto_path_req() - Server goto handling
   * @reference freeciv/server/unithand.c:handle_unit_move_query() - Unit movement validation
   * @compliance Uses pathfinding results and movement cost deduction as per freeciv standards
   */
  private async executeGoto(unit: Unit, targetX: number, targetY: number): Promise<ActionResult> {
    // Validate target coordinates
    if (targetX < 0 || targetY < 0 || targetX >= 200 || targetY >= 200) {
      return {
        success: false,
        message: 'Invalid target coordinates',
      };
    }

    // Check if unit has movement points
    if (unit.movementLeft <= 0) {
      return {
        success: false,
        message: 'Unit has no movement points left',
      };
    }

    // Check if target is the same as current position
    if (unit.x === targetX && unit.y === targetY) {
      return {
        success: false,
        message: 'Unit is already at target position',
      };
    }

    // Use GameManager's requestPath method to find the best path
    if (!this.gameManagerCallback?.requestPath) {
      return {
        success: false,
        message: 'Pathfinding not available',
      };
    }

    const pathResult = await this.gameManagerCallback.requestPath(
      unit.playerId,
      unit.id,
      targetX,
      targetY
    );

    if (
      !pathResult ||
      !pathResult.success ||
      !pathResult.path ||
      !pathResult.path.tiles ||
      pathResult.path.tiles.length < 2
    ) {
      logger.warn('Pathfinding failed for unit movement', {
        unitId: unit.id,
        from: { x: unit.x, y: unit.y },
        to: { x: targetX, y: targetY },
        error: pathResult?.error,
      });

      return {
        success: false,
        message: pathResult?.error || 'No valid path to target',
      };
    }

    // For GOTO action, we move the unit along the entire path as far as movement points allow
    let currentX = unit.x;
    let currentY = unit.y;
    let remainingMovement = unit.movementLeft;
    let tilesTraversed = 0;

    // Process each step of the path
    for (let i = 1; i < pathResult.path.tiles.length; i++) {
      const nextTile = pathResult.path.tiles[i];

      // Calculate movement cost to next tile
      const dx = Math.abs(nextTile.x - currentX);
      const dy = Math.abs(nextTile.y - currentY);
      const movementCost = dx === 1 && dy === 1 ? Math.floor(SINGLE_MOVE * 1.5) : SINGLE_MOVE;

      // Check if we have enough movement points
      if (remainingMovement < movementCost) {
        break; // Stop here, not enough movement points
      }

      // Move to next tile
      currentX = nextTile.x;
      currentY = nextTile.y;
      remainingMovement -= movementCost;
      tilesTraversed++;
    }

    // If we couldn't move at all
    if (tilesTraversed === 0) {
      return {
        success: false,
        message: 'Insufficient movement points to start moving',
      };
    }

    // Calculate total movement cost before updating unit
    const totalMovementCost = unit.movementLeft - remainingMovement;

    // Update unit position to the furthest point we could reach
    const oldX = unit.x;
    const oldY = unit.y;
    unit.x = currentX;
    unit.y = currentY;
    unit.movementLeft = remainingMovement;

    logger.info('Unit goto executed', {
      gameId: this.gameId,
      unitId: unit.id,
      from: { x: oldX, y: oldY },
      to: { x: currentX, y: currentY },
      targetDestination: { x: targetX, y: targetY },
      tilesTraversed,
      remainingMovement,
    });

    // Check if we reached the destination
    const reachedDestination = currentX === targetX && currentY === targetY;

    // If we didn't reach the destination, add a move order to continue next turn
    if (!reachedDestination) {
      const moveOrder: UnitOrder = {
        type: 'move',
        targetX: targetX,
        targetY: targetY,
      };

      // Initialize orders array if it doesn't exist, then add the order
      if (!unit.orders) {
        unit.orders = [];
      }
      // Clear any existing orders and add the new move order
      unit.orders = [moveOrder];
    } else {
      // Clear orders when destination is reached
      unit.orders = [];
    }

    return {
      success: true,
      message: reachedDestination
        ? `${unit.unitTypeId} moved to (${targetX}, ${targetY})`
        : `${unit.unitTypeId} moved ${tilesTraversed} tiles toward (${targetX}, ${targetY}). Will continue next turn.`,
      newPosition: { x: currentX, y: currentY },
      movementCost: totalMovementCost,
    };
  }

  private async executeFoundCity(unit: Unit): Promise<ActionResult> {
    if (!this.gameManagerCallback) {
      return {
        success: false,
        message: 'City founding not available - game manager callback not set',
      };
    }

    // Validate that it's a settler
    if (unit.unitTypeId !== 'settler') {
      return {
        success: false,
        message: 'Only settlers can found cities',
      };
    }

    // Basic validation - the GameManager will do more detailed checks
    if (unit.movementLeft <= 0) {
      return {
        success: false,
        message: 'Unit has no movement points left',
      };
    }

    // Additional basic checks
    if (!this.canFoundCityAtLocation(unit, unit.x, unit.y)) {
      return {
        success: false,
        message: 'Cannot found city at this location',
      };
    }

    try {
      // Generate a default city name (GameManager could override this)
      const cityName = `New City (${unit.x},${unit.y})`;

      // Call GameManager to actually found the city
      const cityId = await this.gameManagerCallback.foundCity(
        this.gameId,
        unit.playerId,
        cityName,
        unit.x,
        unit.y
      );

      logger.info(`City founded successfully`, {
        cityId,
        unitId: unit.id,
        playerId: unit.playerId,
        position: { x: unit.x, y: unit.y },
      });

      return {
        success: true,
        message: `${unit.unitTypeId} founded ${cityName}`,
        unitDestroyed: true,
        cityId,
      };
    } catch (error: any) {
      logger.error(`Failed to found city`, {
        error: error.message,
        unitId: unit.id,
        playerId: unit.playerId,
        position: { x: unit.x, y: unit.y },
      });

      return {
        success: false,
        message: error.message || 'Failed to found city',
      };
    }
  }

  private async executeBuildRoad(unit: Unit): Promise<ActionResult> {
    return {
      success: true,
      message: `${unit.unitTypeId} building road`,
    };
  }
}

/**
 * Effects Manager - Centralized system for calculating game effects
 * Based on freeciv common/effects.c and effects.h
 *
 * Handles government-specific effects including:
 * - Corruption/waste calculations
 * - Happiness and martial law
 * - Unit support costs
 * - Building requirements
 * - Civic policies (multipliers)
 *
 * Reference: /reference/freeciv/common/effects.c
 */

import { rulesetLoader } from '../shared/data/rulesets/RulesetLoader';
import { logger } from '../utils/logger';
import type { Effect, Requirement } from '../shared/data/rulesets/schemas';

// Core effect types from freeciv - directly ported from effects_enums.def
export enum EffectType {
  // Government-specific corruption effects
  OUTPUT_WASTE = 'Output_Waste',
  OUTPUT_WASTE_BY_DISTANCE = 'Output_Waste_By_Distance',
  OUTPUT_WASTE_BY_REL_DISTANCE = 'Output_Waste_By_Rel_Distance',
  OUTPUT_WASTE_PCT = 'Output_Waste_Pct',

  // Government center effects (Palace, Courthouse)
  GOV_CENTER = 'Gov_Center',

  // Happiness and martial law effects
  MAKE_HAPPY = 'Make_Happy',
  MAKE_CONTENT = 'Make_Content',
  MAKE_CONTENT_MIL = 'Make_Content_Mil',
  MAKE_CONTENT_MIL_PER = 'Make_Content_Mil_Per',
  FORCE_CONTENT = 'Force_Content',
  NO_UNHAPPY = 'No_Unhappy',
  MARTIAL_LAW_BY_UNIT = 'Martial_Law_By_Unit',
  MARTIAL_LAW_MAX = 'Martial_Law_Max',
  CITY_UNHAPPY_SIZE = 'City_Unhappy_Size',
  REVOLUTION_UNHAPPINESS = 'Revolution_Unhappiness',

  // Unit support cost effects
  UPKEEP_FREE = 'Upkeep_Free',
  UNIT_UPKEEP_FREE_PER_CITY = 'Unit_Upkeep_Free_Per_City',
  UPKEEP_PCT = 'Upkeep_Pct',
  UNHAPPY_FACTOR = 'Unhappy_Factor',
  SHIELD2GOLD_PCT = 'Shield2Gold_Pct',

  // Building and specialist effects
  SPECIALIST_OUTPUT = 'Specialist_Output',
  OUTPUT_BONUS = 'Output_Bonus',
  OUTPUT_BONUS_2 = 'Output_Bonus_2',

  // General effects
  ANY_GOVERNMENT = 'Any_Government',
  NO_ANARCHY = 'No_Anarchy',
  HAS_SENATE = 'Has_Senate',
}

// Output types for effect calculations
export enum OutputType {
  FOOD = 'food',
  SHIELD = 'shield',
  TRADE = 'trade',
  GOLD = 'gold',
  SCIENCE = 'science',
  LUXURY = 'luxury',
}

// Context for effect evaluation - matches freeciv req_context
export interface EffectContext {
  playerId?: string;
  cityId?: string;
  unitId?: string;
  tileX?: number;
  tileY?: number;
  buildingId?: string;
  government?: string;
  outputType?: OutputType;
  specialist?: string;
  unitType?: string;
}

// Requirement evaluation result
export interface RequirementResult {
  satisfied: boolean;
  reason?: string;
}

// Effect calculation result with breakdown
export interface EffectResult {
  value: number;
  effects: Array<{
    effectId: string;
    type: EffectType;
    value: number;
    source: string;
  }>;
}

/**
 * EffectsManager - Centralized effects calculation system
 * Direct port of freeciv effects system architecture
 */
export class EffectsManager {
  private effectsCache = new Map<string, Record<string, Effect>>();
  private rulesetName: string;

  constructor(rulesetName: string = 'classic') {
    this.rulesetName = rulesetName;
  }

  /**
   * Get all effects for current ruleset
   * Reference: freeciv effects_cache_init()
   */
  private getEffects(): Record<string, Effect> {
    if (!this.effectsCache.has(this.rulesetName)) {
      try {
        const effects = rulesetLoader.getEffects(this.rulesetName);
        this.effectsCache.set(this.rulesetName, effects);
        logger.info(
          `Loaded ${Object.keys(effects).length} effects from ruleset '${this.rulesetName}'`
        );
      } catch (error) {
        logger.error(`Failed to load effects for ruleset '${this.rulesetName}':`, error);
        this.effectsCache.set(this.rulesetName, {});
      }
    }
    return this.effectsCache.get(this.rulesetName)!;
  }

  /**
   * Calculate total effect value for given type and context
   * Reference: freeciv get_city_bonus(), get_player_bonus(), etc.
   */
  public calculateEffect(
    effectType: EffectType,
    context: EffectContext,
    multiplierValue?: number
  ): EffectResult {
    const effects = this.getEffects();
    const result: EffectResult = {
      value: 0,
      effects: [],
    };

    // Find all effects matching the type
    for (const [effectId, effect] of Object.entries(effects)) {
      if (effect.type !== effectType) {
        continue;
      }

      // Check if requirements are satisfied
      const reqResult = this.evaluateRequirements(effect.reqs || [], context);
      if (!reqResult.satisfied) {
        continue;
      }

      // Apply multiplier if present (for civic policies)
      let effectValue = effect.value;
      if (multiplierValue !== undefined) {
        effectValue = this.applyMultiplier(effectValue, multiplierValue);
      }

      result.value += effectValue;
      result.effects.push({
        effectId,
        type: effectType,
        value: effectValue,
        source: this.getEffectSource(effect),
      });
    }

    return result;
  }

  /**
   * Get corruption/waste value for a city
   * Reference: freeciv city_waste() in common/city.c
   */
  public calculateWaste(
    cityContext: EffectContext,
    outputType: OutputType,
    totalOutput: number,
    distanceToGovCenter?: number
  ): number {
    const context = { ...cityContext, outputType };

    // Base waste percentage
    const baseWaste = this.calculateEffect(EffectType.OUTPUT_WASTE, context);
    let wasteLevel = (baseWaste.value * totalOutput) / 100;

    // Distance-based waste (if government center exists)
    if (distanceToGovCenter !== undefined && distanceToGovCenter > 0) {
      const distanceWaste = this.calculateEffect(EffectType.OUTPUT_WASTE_BY_DISTANCE, context);
      wasteLevel += (distanceWaste.value * distanceToGovCenter * totalOutput) / 10000;

      // Relative distance waste (scales with map size)
      // const relDistanceWaste = this.calculateEffect(EffectType.OUTPUT_WASTE_BY_REL_DISTANCE, context);
      // TODO: Implement relative distance calculation when map size data available
    }

    // Apply waste reduction effects
    const wasteReduction = this.calculateEffect(EffectType.OUTPUT_WASTE_PCT, context);
    if (wasteReduction.value > 0) {
      wasteLevel = (wasteLevel * wasteReduction.value) / 100;
    }

    return Math.min(Math.max(Math.floor(wasteLevel), 0), totalOutput);
  }

  /**
   * Calculate martial law happiness bonus
   * Reference: freeciv get_city_bonus() for martial law effects
   */
  public calculateMartialLaw(
    cityContext: EffectContext,
    militaryUnitsInCity: number
  ): { happyBonus: number; maxUnits: number } {
    // Martial law effectiveness per unit
    const martialLawPerUnit = this.calculateEffect(EffectType.MARTIAL_LAW_BY_UNIT, cityContext);

    // Maximum units that can provide martial law
    const martialLawMax = this.calculateEffect(EffectType.MARTIAL_LAW_MAX, cityContext);

    const effectiveUnits = Math.min(militaryUnitsInCity, martialLawMax.value);
    const happyBonus = effectiveUnits * martialLawPerUnit.value;

    return {
      happyBonus,
      maxUnits: martialLawMax.value,
    };
  }

  /**
   * Calculate unit support costs
   * Reference: freeciv city_support() calculations
   */
  public calculateUnitSupport(
    cityContext: EffectContext,
    outputType: OutputType,
    unitsSupported: number
  ): number {
    const context = { ...cityContext, outputType };

    // Free units per city based on government
    const freeUnits = this.calculateEffect(EffectType.UNIT_UPKEEP_FREE_PER_CITY, context);

    // Units requiring support
    const supportedUnits = Math.max(0, unitsSupported - freeUnits.value);

    // Base support cost (usually 1 per unit)
    let supportCost = supportedUnits;

    // Apply upkeep percentage modifier
    const upkeepPct = this.calculateEffect(EffectType.UPKEEP_PCT, context);
    if (upkeepPct.value !== 100) {
      supportCost = (supportCost * upkeepPct.value) / 100;
    }

    return Math.max(0, Math.floor(supportCost));
  }

  /**
   * Check if building can be built based on government requirements
   * Reference: freeciv can_player_build_improvement_direct()
   */
  public canBuildWithGovernment(
    buildingId: string,
    governmentId: string,
    context: EffectContext,
    _playerTechs?: Set<string>
  ): RequirementResult {
    // Government-specific building requirements from freeciv
    const governmentBuildingReqs: Record<string, Requirement[]> = {
      police_station: [
        {
          type: 'Gov',
          name: 'communism',
          range: 'Player',
          present: false, // Cannot build under communism
        },
      ],
      courthouse: [
        {
          type: 'Tech',
          name: 'Code of Laws',
          range: 'Player',
        },
      ],
      palace: [
        {
          type: 'Gov',
          name: 'anarchy',
          range: 'Player',
          present: false, // Cannot build during anarchy
        },
      ],
    };

    const requirements = governmentBuildingReqs[buildingId];
    if (!requirements) {
      return { satisfied: true }; // No special requirements
    }

    // Evaluate each requirement
    for (const req of requirements) {
      const result = this.evaluateSingleRequirement(req, {
        ...context,
        government: governmentId,
      });
      if (!result.satisfied) {
        return result;
      }
    }

    return { satisfied: true };
  }

  /**
   * Get building effects based on current government
   * Some buildings have different effects under different governments
   * Reference: freeciv building effects with government requirements
   */
  public getBuildingGovernmentEffects(
    buildingId: string,
    governmentId: string,
    _context: EffectContext
  ): Record<string, number> {
    const effects: Record<string, number> = {};

    // Government-specific building effects from freeciv
    switch (buildingId) {
      case 'police_station':
        if (governmentId === 'democracy') {
          effects.happiness = 2; // Extra happiness under democracy
        } else if (governmentId === 'republic') {
          effects.happiness = 1; // Standard happiness under republic
        }
        break;

      case 'courthouse':
        // Courthouse reduces corruption (acts as secondary government center)
        if (governmentId !== 'democracy') {
          effects.corruptionReduction = 50; // 50% corruption reduction
        }
        break;

      case 'palace':
        // Palace is always the primary government center
        effects.governmentCenter = 1;
        effects.corruptionReduction = 100; // Complete corruption immunity
        break;

      case 'temple':
        if (governmentId === 'monarchy' || governmentId === 'despotism') {
          effects.happiness = 1; // Base temple happiness
        }
        break;
    }

    return effects;
  }

  /**
   * Evaluate requirements for an effect
   * Reference: freeciv are_reqs_active() in common/requirements.c
   */
  private evaluateRequirements(
    requirements: Requirement[],
    context: EffectContext
  ): RequirementResult {
    for (const req of requirements) {
      const result = this.evaluateSingleRequirement(req, context);
      if (!result.satisfied) {
        return result;
      }
    }
    return { satisfied: true };
  }

  /**
   * Evaluate single requirement
   * Reference: freeciv is_req_active() in common/requirements.c
   */
  private evaluateSingleRequirement(
    requirement: Requirement,
    context: EffectContext
  ): RequirementResult {
    const isPresent = requirement.present !== false; // Default to true if not specified

    switch (requirement.type) {
      case 'Gov':
      case 'Government': {
        const hasGovernment = context.government === requirement.name;
        if (hasGovernment !== isPresent) {
          return {
            satisfied: false,
            reason: `Government requirement not met: ${requirement.name}`,
          };
        }
        break;
      }

      case 'OutputType': {
        const hasOutputType = context.outputType === requirement.name;
        if (hasOutputType !== isPresent) {
          return {
            satisfied: false,
            reason: `OutputType requirement not met: ${requirement.name}`,
          };
        }
        break;
      }

      case 'UnitType': {
        const hasUnitType = context.unitType === requirement.name;
        if (hasUnitType !== isPresent) {
          return {
            satisfied: false,
            reason: `UnitType requirement not met: ${requirement.name}`,
          };
        }
        break;
      }

      case 'Building':
        // TODO: Check if city has building when building system is integrated
        break;

      case 'Tech':
        // TODO: Check if player has technology when integrated with research system
        break;

      case 'Player': {
        const hasPlayer = context.playerId === requirement.name;
        if (hasPlayer !== isPresent) {
          return {
            satisfied: false,
            reason: `Player requirement not met: ${requirement.name}`,
          };
        }
        break;
      }

      // Add more requirement types as needed
      default:
        logger.warn(`Unknown requirement type: ${requirement.type}`);
        break;
    }

    return { satisfied: true };
  }

  /**
   * Apply multiplier to effect value (for civic policies)
   * Reference: freeciv player_multiplier_effect_value() in common/multipliers.c
   */
  private applyMultiplier(effectValue: number, multiplierValue: number): number {
    // Multiplier formula from freeciv: (value + offset) * (factor / 100)
    // For now, simple multiplication - will enhance when PolicyManager is implemented
    return Math.floor(effectValue * (multiplierValue / 100));
  }

  /**
   * Get source description for an effect (for debugging)
   */
  private getEffectSource(effect: Effect): string {
    // Extract source from requirements or use effect ID
    const govReq = effect.reqs?.find(req => req.type === 'Gov' || req.type === 'Government');
    if (govReq) {
      return `Government: ${govReq.name}`;
    }

    const buildingReq = effect.reqs?.find(req => req.type === 'Building');
    if (buildingReq) {
      return `Building: ${buildingReq.name}`;
    }

    return effect.comment || 'Unknown';
  }

  /**
   * Clear effects cache (for testing or ruleset changes)
   */
  public clearCache(): void {
    this.effectsCache.clear();
  }

  /**
   * Get all government centers (Palace, Courthouse) for corruption calculations
   * Reference: freeciv nearest_gov_center() in common/city.c
   */
  public getGovernmentCenters(_playerContext: EffectContext): string[] {
    // TODO: This will be implemented when integrated with CityManager
    // Should return list of cities with Gov_Center effect
    return [];
  }
}

