} from './map/MapTypes';

/**
 * Refactored MapManager that coordinates specialized map generation services
 * This is the main coordinator class that delegates to appropriate services
 * Maintains 100% API compatibility with the original MapManager
 * @reference freeciv/server/generator/mapgen.c:1268-1427 map_fractal_generate()
 */
export class MapManager {
  private width: number;
  private height: number;
  private seed: string;
  private generator: string;
  private defaultGeneratorType: MapGeneratorType;
  private defaultStartPosMode: MapStartpos;
  private random: () => number;

  // Specialized services
  private heightBasedMapService: HeightBasedMapService;
  private islandMapService: IslandMapService;
  private fairIslandsService: FairIslandsService;
  private mapAccessService: MapAccessService;

  constructor(
    width: number,
    height: number,
    seed?: string,
    generator: string = 'random',
    defaultGeneratorType?: MapGeneratorType,
    defaultStartPosMode?: MapStartpos,
    cleanupTemperatureMapAfterUse: boolean = false,
    temperatureParam: number = 50
  ) {
    this.width = width;
    this.height = height;
    this.seed = seed || this.generateSeed();
    this.generator = generator;
    this.defaultGeneratorType = defaultGeneratorType || 'FRACTAL';
    this.defaultStartPosMode = defaultStartPosMode || MapStartpos.ALL;
    this.random = this.createSeededRandom(this.seed);

    // Initialize specialized services
    this.heightBasedMapService = new HeightBasedMapService(
      width,
      height,
      this.seed,
      this.generator,
      this.random,
      this.defaultStartPosMode,
      cleanupTemperatureMapAfterUse,
      temperatureParam
    );

    this.islandMapService = new IslandMapService(
      width,
      height,
      this.seed,
      this.generator,
      this.random,
      this.defaultStartPosMode,
      cleanupTemperatureMapAfterUse,
      temperatureParam
    );

    this.fairIslandsService = new FairIslandsService(
      width,
      height,
      this.seed,
      this.generator,
      this.random,
      this.defaultStartPosMode,
      cleanupTemperatureMapAfterUse,
      temperatureParam
    );

    this.mapAccessService = new MapAccessService(width, height);
  }

  /**
   * Main map generation orchestration with generator routing
   * @reference freeciv/server/generator/mapgen.c:1268-1427 map_fractal_generate()
   * Routes to specific generators based on type, with fallback logic matching freeciv
   */
  public async generateMap(
    players: Map<string, PlayerState>,
    generatorType?: MapGeneratorType
  ): Promise<void> {
    // Use provided generator type or fall back to instance default (matches freeciv behavior)
    const generator = generatorType || this.defaultGeneratorType;

    logger.info('Generating map', {
      width: this.width,
      height: this.height,
      seed: this.seed,
      generator,
      reference: 'freeciv/server/generator/mapgen.c:1268-1427',
    });

    let mapData: MapData;

    try {
      // Implement freeciv's map_fractal_generate() routing logic
      // @reference freeciv/server/generator/mapgen.c:1315-1358
      // Handle FAIR generator with explicit fallback logic (matches freeciv behavior)
      if (generator === 'FAIR') {
        try {
          // Attempt fair islands generation, fallback to ISLAND if failed
          mapData = await this.fairIslandsService.generateMap(players);
        } catch (error) {
          if (error instanceof Error && error.message === 'FALLBACK_TO_ISLAND') {
            logger.info('Fair islands generation failed, falling back to ISLAND generator');
            // Explicit fallback to ISLAND (matches freeciv mapgen.c:1315-1318)
            // Use 'ALL' startpos mode for fair island fallback (maps to mapGenerator4)
            mapData = await this.islandMapService.generateMap(players, MapStartpos.ALL);
          } else {
            throw error;
          }
        }
      } else {
        // Handle other generators with standard routing
        switch (generator) {
          case 'ISLAND':
            // Use instance default startpos mode for island generation
            mapData = await this.islandMapService.generateMap(players, this.defaultStartPosMode);
            break;

          case 'RANDOM':
            mapData = await this.heightBasedMapService.generateMap(players, 'RANDOM');
            break;

          case 'FRACTURE':
            mapData = await this.heightBasedMapService.generateMap(players, 'FRACTURE');
            break;

          case 'SCENARIO':
            throw new Error(
              'SCENARIO generator not implemented - scenarios should be loaded from file'
            );

          case 'FRACTAL':
          default:
            mapData = await this.heightBasedMapService.generateMap(players, 'FRACTAL');
            break;
        }
      }

      // Set the map data in the access service for API compatibility
      this.mapAccessService.setMapData(mapData);

      logger.info('Map generation completed successfully', {
        generator,
        width: this.width,
        height: this.height,
        startingPositions: mapData.startingPositions.length,
      });
    } catch (error) {
      logger.error('Map generation failed', {
        generator,
        error: error instanceof Error ? error.message : error,
      });
      throw error;
    }
  }

  /**
   * Fractal height-based map generation (backward compatibility method)
   * @deprecated Use generateMap() with 'FRACTAL' type instead
   */
  public async generateMapFractal(players: Map<string, PlayerState>): Promise<void> {
    logger.warn('generateMapFractal() is deprecated, use generateMap() with FRACTAL type');
    return this.generateMap(players, 'FRACTAL');
  }

  /**
   * Island-based map generation (backward compatibility method)
   * @deprecated Use generateMap() with 'ISLAND' type instead
   */
  public async generateMapWithIslands(
    players: Map<string, PlayerState>,
    startPosMode: StartPosMode = MapStartpos.ALL
  ): Promise<void> {
    logger.warn('generateMapWithIslands() is deprecated, use generateMap() with ISLAND type');

    // Temporarily update the default start pos mode for this generation
    const originalStartPosMode = this.defaultStartPosMode;
    this.defaultStartPosMode = startPosMode;

    try {
      await this.generateMap(players, 'ISLAND');
    } finally {
      // Restore original start pos mode
      this.defaultStartPosMode = originalStartPosMode;
    }
  }

  /**
   * Pure random map generation (backward compatibility method)
   * @deprecated Use generateMap() with 'RANDOM' type instead
   */
  public async generateMapRandom(players: Map<string, PlayerState>): Promise<void> {
    logger.warn('generateMapRandom() is deprecated, use generateMap() with RANDOM type');
    return this.generateMap(players, 'RANDOM');
  }

  /**
   * Fracture map generation (backward compatibility method)
   * @deprecated Use generateMap() with 'FRACTURE' type instead
   */
  public async generateMapFracture(players: Map<string, PlayerState>): Promise<void> {
    logger.warn('generateMapFracture() is deprecated, use generateMap() with FRACTURE type');
    return this.generateMap(players, 'FRACTURE');
  }

  /**
   * Attempt fair islands generation (backward compatibility method)
   * @deprecated Use generateMap() with 'FAIR' type instead
   */
  public async attemptFairIslandsGeneration(players: Map<string, PlayerState>): Promise<boolean> {
    logger.warn('attemptFairIslandsGeneration() is deprecated, use generateMap() with FAIR type');

    try {
      await this.generateMap(players, 'FAIR');
      return true;
    } catch (error) {
      logger.warn('Fair islands generation failed', {
        error: error instanceof Error ? error.message : error,
      });
      return false;
    }
  }

  // === PUBLIC API METHODS (delegated to services) ===

  /**
   * Get current map data
   */
  public getMapData(): MapData | null {
    return this.mapAccessService.getMapData();
  }

  /**
   * Get the seed used for map generation
   */
  public getSeed(): string {
    return this.seed;
  }

  /**
   * Get a specific tile by coordinates
   */
  public getTile(x: number, y: number): MapTile | null {
    return this.mapAccessService.getTile(x, y);
  }

  /**
   * Get tiles visible from a position within radius
   */
  public getVisibleTiles(x: number, y: number, radius: number): MapTile[] {
    return this.mapAccessService.getVisibleTiles(x, y, radius);
  }

  /**
   * Update tile visibility for a player
   */
  public updateTileVisibility(playerId: string, x: number, y: number, radius: number): void {
    this.mapAccessService.updateTileVisibility(playerId, x, y, radius);
  }

  /**
   * Get neighboring tiles for a given position
   */
  public getNeighbors(x: number, y: number): MapTile[] {
    return this.mapAccessService.getNeighbors(x, y);
  }

  /**
   * Check if a position is valid within map bounds
   */
  public isValidPosition(x: number, y: number): boolean {
    return this.mapAccessService.isValidPosition(x, y);
  }

  /**
   * Update a specific property of a tile
   */
  public updateTileProperty(x: number, y: number, property: string, value: any): void {
    this.mapAccessService.updateTileProperty(x, y, property, value);
  }

  /**
   * Validate the current map data using the comprehensive validation system
   */
  public validateCurrentMap(players?: Map<string, PlayerState>): ValidationResult | null {
    return this.mapAccessService.validateCurrentMap(players);
  }

  /**
   * Get the map validator instance for advanced validation operations
   */
  public getMapValidator() {
    return this.mapAccessService.getMapValidator();
  }

  /**
   * Get movement cost for a tile
   */
  public getMovementCost(x: number, y: number, unitTypeId?: string): number {
    return this.mapAccessService.getMovementCost(x, y, unitTypeId);
  }

  /**
   * Calculate distance between two points
   */
  public getDistance(x1: number, y1: number, x2: number, y2: number): number {
    return this.mapAccessService.getDistance(x1, y1, x2, y2);
  }

  /**
   * Get tiles accessible within movement range
   */
  public getAccessibleTiles(
    x: number,
    y: number,
    movementPoints: number,
    unitTypeId?: string
  ): MapTile[] {
    return this.mapAccessService.getAccessibleTiles(x, y, movementPoints, unitTypeId);
  }

  /**
   * Validate map structure and properties
   */
  public validateMap(): { valid: boolean; issues: string[] } {
    return this.mapAccessService.validateMap();
  }

  // === UTILITY METHODS ===

  /**
   * Generate a random seed string
   */
  private generateSeed(): string {
    return (
      Math.random().toString(36).substring(2, 15) + Math.random().toString(36).substring(2, 15)
    );
  }

  /**
   * Create a seeded random number generator
   * @param seed String seed for reproducible random generation
   * @returns Function that returns random numbers [0, 1)
   */
  private createSeededRandom(seed: string): () => number {
    // Simple hash function to convert string to number
    let hash = 0;
    for (let i = 0; i < seed.length; i++) {
      const char = seed.charCodeAt(i);
      hash = (hash << 5) - hash + char;
      hash = hash & hash; // Convert to 32-bit integer
    }

    // Use the hash as seed for a simple PRNG
    let currentSeed = Math.abs(hash);

    return () => {
      // Linear congruential generator
      currentSeed = (currentSeed * 1103515245 + 12345) & 0x7fffffff;
      return currentSeed / 0x7fffffff;
    };
  }
}

import { logger } from '../utils/logger';
import { calculateMovementCost } from './constants/MovementConstants';
import type { Unit } from './UnitManager';

export interface PathTile {
  x: number;
  y: number;
  moveCost: number;
  direction?: number;
}

export interface PathfindingResult {
  path: PathTile[];
  totalCost: number;
  estimatedTurns: number;
  valid: boolean;
}

interface AStarNode {
  x: number;
  y: number;
  gCost: number; // Cost from start
  hCost: number; // Heuristic cost to goal
  fCost: number; // Total cost (g + h)
  parent: AStarNode | null;
  moveCost: number; // Cost to move to this tile
}

/**
 * A* Pathfinding Manager for unit movement
 * Based on freeciv's pathfinding system and classic A* algorithm
 *
 * @reference freeciv/common/aicore/path_finding.h - Core pathfinding definitions
 * @reference freeciv/common/aicore/path_finding.c - PF algorithm implementation
 * @reference freeciv-web/freeciv/patches/goto_fcweb.patch - Server-side goto handling
 * @compliance Implements A* algorithm with move cost calculations as per freeciv standards
 */
export class PathfindingManager {
  private mapWidth: number;
  private mapHeight: number;
  private mapManager: any; // MapManager instance for terrain access

  constructor(mapWidth: number, mapHeight: number, mapManager?: any) {
    this.mapWidth = mapWidth;
    this.mapHeight = mapHeight;
    this.mapManager = mapManager;
  }

  /**
   * Find path from unit to target using A* algorithm
   * Implements the core pathfinding logic similar to freeciv's PF system
   *
   * @reference freeciv/common/aicore/path_finding.c:pf_map_new() - Path finding initialization
   * @reference freeciv/common/aicore/path_finding.c:pf_map_iterate() - Path iteration algorithm
   * @compliance Uses movement cost calculation and heuristic matching freeciv standards
   */
  async findPath(unit: Unit, targetX: number, targetY: number): Promise<PathfindingResult> {
    const startTime = Date.now();

    logger.debug('PathfindingManager.findPath called', {
      unitId: unit.id,
      from: { x: unit.x, y: unit.y },
      to: { x: targetX, y: targetY },
      hasMapManager: !!this.mapManager,
      hasGetTile: !!this.mapManager?.getTile,
      mapSize: `${this.mapWidth}x${this.mapHeight}`,
    });

    try {
      // Validate coordinates
      if (!this.isValidCoordinate(targetX, targetY)) {
        logger.debug('Invalid coordinates in pathfinding', {
          targetX,
          targetY,
          mapSize: `${this.mapWidth}x${this.mapHeight}`,
        });
        return {
          path: [],
          totalCost: 0,
          estimatedTurns: 0,
          valid: false,
        };
      }

      // Check if already at target
      if (unit.x === targetX && unit.y === targetY) {
        return {
          path: [{ x: unit.x, y: unit.y, moveCost: 0 }],
          totalCost: 0,
          estimatedTurns: 0,
          valid: true,
        };
      }

      // Run A* pathfinding
      const path = this.aStar({ x: unit.x, y: unit.y }, { x: targetX, y: targetY }, unit);

      if (!path || path.length === 0) {
        return {
          path: [],
          totalCost: 0,
          estimatedTurns: 0,
          valid: false,
        };
      }

      // Convert to PathTile format and calculate costs
      const pathTiles = this.convertToPathTiles(path, unit);
      const totalCost = pathTiles.reduce((sum, tile) => sum + tile.moveCost, 0);
      const estimatedTurns = this.calculateTurns(totalCost, unit);

      const result = {
        path: pathTiles,
        totalCost,
        estimatedTurns,
        valid: true,
      };

      const duration = Date.now() - startTime;
      logger.info('Pathfinding completed', {
        unitId: unit.id,
        from: { x: unit.x, y: unit.y },
        to: { x: targetX, y: targetY },
        pathLength: pathTiles.length,
        totalCost,
        estimatedTurns,
        durationMs: duration,
      });

      return result;
    } catch (error) {
      logger.error('Pathfinding error', {
        unitId: unit.id,
        from: { x: unit.x, y: unit.y },
        to: { x: targetX, y: targetY },
        error: error instanceof Error ? error.message : String(error),
      });

      return {
        path: [],
        totalCost: 0,
        estimatedTurns: 0,
        valid: false,
      };
    }
  }

  /**
   * A* pathfinding algorithm implementation
   * @reference https://en.wikipedia.org/wiki/A*_search_algorithm
   */
  private aStar(
    start: { x: number; y: number },
    goal: { x: number; y: number },
    unit: Unit
  ): AStarNode[] | null {
    const openSet: AStarNode[] = [];
    const closedSet = new Set<string>();
    const nodes = new Map<string, AStarNode>();

    // Create start node
    const startNode: AStarNode = {
      x: start.x,
      y: start.y,
      gCost: 0,
      hCost: this.heuristic(start.x, start.y, goal.x, goal.y),
      fCost: 0,
      parent: null,
      moveCost: 0,
    };
    startNode.fCost = startNode.gCost + startNode.hCost;

    openSet.push(startNode);
    nodes.set(`${start.x},${start.y}`, startNode);

    let iterations = 0;
    const maxIterations = this.mapWidth * this.mapHeight; // Prevent infinite loops

    while (openSet.length > 0 && iterations < maxIterations) {
      iterations++;

      // Find node with lowest fCost
      const current = this.getLowestFCostNode(openSet);
      const currentIndex = openSet.indexOf(current);
      openSet.splice(currentIndex, 1);

      closedSet.add(`${current.x},${current.y}`);

      // Check if we reached the goal
      if (current.x === goal.x && current.y === goal.y) {
        return this.reconstructPath(current);
      }

      // Check all neighbors
      const neighbors = this.getNeighbors(current.x, current.y);

      for (const neighbor of neighbors) {
        const neighborKey = `${neighbor.x},${neighbor.y}`;

        // Skip if in closed set
        if (closedSet.has(neighborKey)) {
          continue;
        }

        // Check if neighbor is walkable
        const moveCost = this.getMovementCost(current.x, current.y, neighbor.x, neighbor.y, unit);

        if (moveCost < 0) {
          continue; // Unwalkable terrain
        }

        const tentativeGCost = current.gCost + moveCost;

        let neighborNode = nodes.get(neighborKey);
        if (!neighborNode) {
          // Create new node
          neighborNode = {
            x: neighbor.x,
            y: neighbor.y,
            gCost: tentativeGCost,
            hCost: this.heuristic(neighbor.x, neighbor.y, goal.x, goal.y),
            fCost: 0,
            parent: current,
            moveCost,
          };
          neighborNode.fCost = neighborNode.gCost + neighborNode.hCost;

          nodes.set(neighborKey, neighborNode);
          openSet.push(neighborNode);
        } else if (tentativeGCost < neighborNode.gCost) {
          // Found better path to this neighbor
          neighborNode.gCost = tentativeGCost;
          neighborNode.fCost = neighborNode.gCost + neighborNode.hCost;
          neighborNode.parent = current;
          neighborNode.moveCost = moveCost;

          // Add to open set if not already there
          if (!openSet.includes(neighborNode)) {
            openSet.push(neighborNode);
          }
        }
      }
    }

    // No path found
    logger.warn('A* pathfinding failed to find path', {
      unitId: unit.id,
      from: start,
      to: goal,
      iterations,
      maxIterations,
    });

    return null;
  }

  /**
   * Reconstruct path from goal node back to start
   */
  private reconstructPath(goalNode: AStarNode): AStarNode[] {
    const path: AStarNode[] = [];
    let current: AStarNode | null = goalNode;

    while (current) {
      path.unshift(current);
      current = current.parent;
    }

    return path;
  }

  /**
   * Find node with lowest fCost in open set
   */
  private getLowestFCostNode(openSet: AStarNode[]): AStarNode {
    let lowest = openSet[0];

    for (let i = 1; i < openSet.length; i++) {
      const node = openSet[i];
      if (node.fCost < lowest.fCost || (node.fCost === lowest.fCost && node.hCost < lowest.hCost)) {
        lowest = node;
      }
    }

    return lowest;
  }

  /**
   * Get valid neighbor coordinates
   */
  private getNeighbors(x: number, y: number): Array<{ x: number; y: number }> {
    const neighbors: Array<{ x: number; y: number }> = [];

    // 8-directional movement (includes diagonals)
    const directions = [
      { dx: -1, dy: -1 },
      { dx: 0, dy: -1 },
      { dx: 1, dy: -1 },
      { dx: -1, dy: 0 },
      { dx: 1, dy: 0 },
      { dx: -1, dy: 1 },
      { dx: 0, dy: 1 },
      { dx: 1, dy: 1 },
    ];

    for (const dir of directions) {
      const newX = x + dir.dx;
      const newY = y + dir.dy;

      if (this.isValidCoordinate(newX, newY)) {
        neighbors.push({ x: newX, y: newY });
      }
    }

    return neighbors;
  }

  /**
   * Calculate heuristic cost (Manhattan distance)
   */
  private heuristic(x1: number, y1: number, x2: number, y2: number): number {
    // Use Manhattan distance as heuristic
    return Math.abs(x2 - x1) + Math.abs(y2 - y1);
  }

  /**
   * Get movement cost between two adjacent tiles
   */
  private getMovementCost(
    fromX: number,
    fromY: number,
    toX: number,
    toY: number,
    unit: Unit
  ): number {
    // MapManager is required for terrain validation
    if (!this.mapManager || !this.mapManager.getTile) {
      logger.error('PathfindingManager: MapManager is required for movement validation', {
        unitId: unit.id,
        unitType: unit.unitTypeId,
        from: { x: fromX, y: fromY },
        to: { x: toX, y: toY },
        hasMapManager: !!this.mapManager,
        hasGetTile: !!this.mapManager?.getTile,
        mapManagerType: typeof this.mapManager,
      });
      return -1; // Impassable when no terrain data available
    }

    try {
      const tile = this.mapManager.getTile(toX, toY);
      if (!tile || !tile.terrain) {
        logger.warn('PathfindingManager: No terrain data for tile', {
          x: toX,
          y: toY,
          unitId: unit.id,
          unitType: unit.unitTypeId,
        });
        return -1; // Impassable when terrain data is missing
      }

      // Use enhanced movement cost calculation with terrain validation
      return calculateMovementCost(fromX, fromY, toX, toY, tile.terrain, unit.unitTypeId);
    } catch (error) {
      logger.error('PathfindingManager: Failed to get terrain data', {
        x: toX,
        y: toY,
        unitId: unit.id,
        unitType: unit.unitTypeId,
        error: error instanceof Error ? error.message : String(error),
      });
      return -1; // Impassable on error
    }
  }

  /**
   * Check if coordinate is valid on the map
   */
  private isValidCoordinate(x: number, y: number): boolean {
    return x >= 0 && x < this.mapWidth && y >= 0 && y < this.mapHeight;
  }

  /**
   * Convert A* nodes to PathTile format with directions
   */
  private convertToPathTiles(path: AStarNode[], _unit: Unit): PathTile[] {
    const pathTiles: PathTile[] = [];

    for (let i = 0; i < path.length; i++) {
      const node = path[i];
      const pathTile: PathTile = {
        x: node.x,
        y: node.y,
        moveCost: node.moveCost,
      };

      // Calculate direction to next tile for rendering
      if (i < path.length - 1) {
        const nextNode = path[i + 1];
        pathTile.direction = this.calculateDirection(node.x, node.y, nextNode.x, nextNode.y);
      }

      pathTiles.push(pathTile);
    }

    return pathTiles;
  }

  /**
   * Calculate direction from one tile to another (freeciv 8-direction system)
   */
  private calculateDirection(fromX: number, fromY: number, toX: number, toY: number): number {
    const dx = toX - fromX;
    const dy = toY - fromY;

    // Freeciv directions: 0=North, 1=NE, 2=East, 3=SE, 4=South, 5=SW, 6=West, 7=NW
    if (dx === 0 && dy === -1) return 0; // North
    if (dx === 1 && dy === -1) return 1; // NE
    if (dx === 1 && dy === 0) return 2; // East
    if (dx === 1 && dy === 1) return 3; // SE
    if (dx === 0 && dy === 1) return 4; // South
    if (dx === -1 && dy === 1) return 5; // SW
    if (dx === -1 && dy === 0) return 6; // West
    if (dx === -1 && dy === -1) return 7; // NW

    return 2; // Default to east
  }

  /**
   * Calculate number of turns needed for path based on unit movement
   */
  private calculateTurns(totalCost: number, unit: Unit): number {
    // Get unit's movement points per turn
    // For now, assume 3 movement points per turn for most units
    // This should be enhanced to use actual unit type data
    const movementPerTurn = unit.movementLeft || 3;

    return Math.ceil(totalCost / movementPerTurn);
  }
}

/**
 * Policy Manager - Civic Policies System (Multipliers)
 * Direct port of freeciv multipliers system from common/multipliers.c
 *
 * In freeciv, "multipliers" represent civic policies that players can adjust
 * to affect various game mechanics. Examples include:
 * - Tax rates (luxury/science/gold)
 * - Military policies affecting unit costs
 * - Economic policies affecting trade and production
 *
 * Reference: /reference/freeciv/common/multipliers.c, multipliers.h
 */

// import { db } from '../database';
// import { players as playersTable } from '../database/schema';
// import { eq, and } from 'drizzle-orm';
import { logger } from '../utils/logger';
import type { Requirement } from '../shared/data/rulesets/schemas';
// import { EffectsManager, EffectContext } from './EffectsManager';

// Policy definition - direct port of freeciv struct multiplier
export interface Policy {
  id: string;
  name: string;
  ruleditDisabled?: boolean; // Does not really exist - hole in multipliers array
  start: number; // Minimum value (display units)
  stop: number; // Maximum value (display units)
  step: number; // Step size for adjustments (display units)
  default: number; // Default value (display units)
  offset: number; // Formula: (ui_value + offset) * (factor/100) = effect_value
  factor: number; // Formula factor (usually 100 for 1:1 mapping)
  minimumTurns: number; // How often multiplier can be changed
  reqs?: Requirement[]; // Requirements for adjusting this policy
  helptext?: string; // Help text description
}

// Player policy value - port of freeciv struct multiplier_value
export interface PlayerPolicyValue {
  value: number; // Value currently in force
  targetValue: number; // Value player wants to change to
  changedTurn: number; // Turn when last changed (for minimum_turns check)
}

// Player policy state
export interface PlayerPolicies {
  playerId: string;
  policies: Map<string, PlayerPolicyValue>;
}

/**
 * PolicyManager - Civic Policies System
 * Direct port of freeciv multipliers architecture
 */
export class PolicyManager {
  private playerPolicies = new Map<string, PlayerPolicies>();
  private availablePolicies: Map<string, Policy> = new Map();

  constructor(_gameId: string, _effectsManager: any) {
    this.initializePolicies();
  }

  /**
   * Initialize available policies
   * In the full implementation, this would load from rulesets
   * For now, we'll define basic tax rate policies like freeciv
   */
  private initializePolicies(): void {
    // Basic tax rate policy (luxury/science/gold split)
    // This is conceptual - in freeciv this is handled differently
    // but demonstrates the multiplier system structure

    const taxRatePolicy: Policy = {
      id: 'tax_rates',
      name: 'Tax Allocation',
      start: 0,
      stop: 100,
      step: 10,
      default: 50,
      offset: 0,
      factor: 100,
      minimumTurns: 1, // Can change every turn
      helptext: 'Controls allocation of tax revenue between luxury, science, and gold',
    };

    // Economic focus policy
    const economicPolicy: Policy = {
      id: 'economic_focus',
      name: 'Economic Focus',
      start: 0,
      stop: 200,
      step: 25,
      default: 100,
      offset: -100,
      factor: 100,
      minimumTurns: 3, // Must wait 3 turns between changes
      helptext: 'Adjusts economic output vs military efficiency tradeoff',
    };

    this.availablePolicies.set(taxRatePolicy.id, taxRatePolicy);
    this.availablePolicies.set(economicPolicy.id, economicPolicy);

    logger.info(`Initialized ${this.availablePolicies.size} policies`);
  }

  /**
   * Initialize player policies
   * Reference: freeciv multipliers_init() and player initialization
   */
  public async initializePlayerPolicies(playerId: string): Promise<void> {
    const playerPolicies: PlayerPolicies = {
      playerId,
      policies: new Map(),
    };

    // Initialize each policy to default value
    for (const [policyId, policy] of this.availablePolicies) {
      playerPolicies.policies.set(policyId, {
        value: policy.default,
        targetValue: policy.default,
        changedTurn: 0,
      });
    }

    this.playerPolicies.set(playerId, playerPolicies);

    // TODO: Persist to database when we add policy persistence
    logger.info(`Initialized policies for player ${playerId}`);
  }

  /**
   * Get player's current policy value
   * Reference: freeciv player_multiplier_value()
   */
  public getPolicyValue(playerId: string, policyId: string): number {
    return this.getPlayerPolicyValue(playerId, policyId);
  }

  /**
   * Get player's current policy value (internal method)
   * Reference: freeciv player_multiplier_value()
   */
  public getPlayerPolicyValue(playerId: string, policyId: string): number {
    const playerPolicies = this.playerPolicies.get(playerId);
    if (!playerPolicies) {
      logger.warn(`No policies found for player ${playerId}`);
      return 0;
    }

    const policyValue = playerPolicies.policies.get(policyId);
    if (!policyValue) {
      const policy = this.availablePolicies.get(policyId);
      return policy?.default || 0;
    }

    return policyValue.value;
  }

  /**
   * Get player's target policy value (what they want to change to)
   * Reference: freeciv player_multiplier_target_value()
   */
  public getPlayerPolicyTargetValue(playerId: string, policyId: string): number {
    const playerPolicies = this.playerPolicies.get(playerId);
    if (!playerPolicies) {
      return this.getPlayerPolicyValue(playerId, policyId);
    }

    const policyValue = playerPolicies.policies.get(policyId);
    return policyValue?.targetValue || this.getPlayerPolicyValue(playerId, policyId);
  }

  /**
   * Get effective policy value for effects calculations
   * Reference: freeciv player_multiplier_effect_value()
   */
  public getEffectivePolicyValue(playerId: string, policyId: string): number {
    const policy = this.availablePolicies.get(policyId);
    if (!policy) {
      logger.warn(`Policy ${policyId} not found`);
      return 100; // Default neutral multiplier
    }

    const value = this.getPlayerPolicyValue(playerId, policyId);

    // Formula from freeciv: (value + offset) * factor (NO division by 100 here!)
    return (value + policy.offset) * policy.factor;
  }

  /**
   * Get effective policy value for effects calculations (internal)
   * Reference: freeciv player_multiplier_effect_value()
   */
  public getPlayerPolicyEffectValue(playerId: string, policyId: string): number {
    // This is the same as getEffectivePolicyValue - they should return the same value
    return this.getEffectivePolicyValue(playerId, policyId);
  }

  /**
   * Attempt to change a policy value
   * Reference: freeciv multiplier_can_be_changed() and related functions
   */
  public async changePolicyValue(
    playerId: string,
    policyId: string,
    newValue: number,
    currentTurn: number,
    playerResearchedTechs: Set<string>
  ): Promise<{ success: boolean; message?: string }> {
    const policy = this.availablePolicies.get(policyId);
    if (!policy) {
      return { success: false, message: 'Policy not found' };
    }

    const playerPolicies = this.playerPolicies.get(playerId);
    if (!playerPolicies) {
      return { success: false, message: 'Player policies not initialized' };
    }

    // Validate new value is within range
    if (newValue < policy.start || newValue > policy.stop) {
      return {
        success: false,
        message: `Value must be between ${policy.start} and ${policy.stop}`,
      };
    }

    // Validate step size
    if ((newValue - policy.start) % policy.step !== 0) {
      return {
        success: false,
        message: `Value must be in steps of ${policy.step}`,
      };
    }

    const currentPolicyValue = playerPolicies.policies.get(policyId);
    if (!currentPolicyValue) {
      return { success: false, message: 'Policy value not found' };
    }

    // Check minimum turns requirement
    const turnsSinceLastChange = currentTurn - currentPolicyValue.changedTurn;
    if (turnsSinceLastChange < policy.minimumTurns) {
      const turnsRemaining = policy.minimumTurns - turnsSinceLastChange;
      return {
        success: false,
        message: `Must wait ${turnsRemaining} more turns before changing this policy`,
      };
    }

    // Check requirements
    if (policy.reqs) {
      const canChange = await this.checkPolicyRequirements(
        playerId,
        policy.reqs,
        playerResearchedTechs
      );
      if (!canChange.allowed) {
        return { success: false, message: canChange.reason };
      }
    }

    // Apply the change
    currentPolicyValue.value = newValue;
    currentPolicyValue.targetValue = newValue;
    currentPolicyValue.changedTurn = currentTurn;

    // TODO: Persist to database

    logger.info(`Player ${playerId} changed policy ${policyId} to ${newValue}`);
    return {
      success: true,
      message: `Policy ${policy.name} changed to ${newValue}`,
    };
  }

  /**
   * Check if player can change a policy
   * Reference: freeciv multiplier_can_be_changed()
   */
  public async canChangePolicyValue(
    playerId: string,
    policyId: string,
    currentTurn: number,
    playerResearchedTechs: Set<string>
  ): Promise<{ allowed: boolean; reason?: string }> {
    const policy = this.availablePolicies.get(policyId);
    if (!policy) {
      return { allowed: false, reason: 'Policy not found' };
    }

    const playerPolicies = this.playerPolicies.get(playerId);
    if (!playerPolicies) {
      return { allowed: false, reason: 'Player policies not initialized' };
    }

    const currentPolicyValue = playerPolicies.policies.get(policyId);
    if (!currentPolicyValue) {
      return { allowed: false, reason: 'Policy value not found' };
    }

    // Check minimum turns requirement
    const turnsSinceLastChange = currentTurn - currentPolicyValue.changedTurn;
    if (turnsSinceLastChange < policy.minimumTurns) {
      const turnsRemaining = policy.minimumTurns - turnsSinceLastChange;
      return {
        allowed: false,
        reason: `Must wait ${turnsRemaining} more turns`,
      };
    }

    // Check requirements
    if (policy.reqs) {
      return await this.checkPolicyRequirements(playerId, policy.reqs, playerResearchedTechs);
    }

    return { allowed: true };
  }

  /**
   * Get all available policies as array (for integration test compatibility)
   * Reference: freeciv multipliers_iterate in common/multipliers.h:61-69
   */
  public getAvailablePolicies(): Policy[] {
    return Array.from(this.availablePolicies.values());
  }

  /**
   * Get all player policies with current values
   * Reference: freeciv player policy state
   */
  public getPlayerPolicies(playerId: string): PlayerPolicies | undefined {
    return this.playerPolicies.get(playerId);
  }

  /**
   * Get policies available to a specific player
   * Filters by requirements the player can meet
   */
  public async getAvailablePoliciesForPlayer(
    playerId: string,
    currentTurn: number,
    playerResearchedTechs: Set<string>
  ): Promise<
    Array<{
      policy: Policy;
      currentValue: number;
      targetValue: number;
      canChange: boolean;
      reason?: string;
    }>
  > {
    const result = [];

    for (const [policyId, policy] of this.availablePolicies) {
      const currentValue = this.getPlayerPolicyValue(playerId, policyId);
      const targetValue = this.getPlayerPolicyTargetValue(playerId, policyId);
      const changeCheck = await this.canChangePolicyValue(
        playerId,
        policyId,
        currentTurn,
        playerResearchedTechs
      );

      result.push({
        policy,
        currentValue,
        targetValue,
        canChange: changeCheck.allowed,
        reason: changeCheck.reason,
      });
    }

    return result;
  }

