/**
 * Movement system constants
 * Implements freeciv's movement fragment system and terrain costs
 *
 * @reference freeciv/common/movement.h - SINGLE_MOVE, MAX_MOVE_FRAGS definitions
 * @reference freeciv/data/classic/terrain.ruleset - Terrain movement costs
 * @reference freeciv/server/ruleset/ruleload.c - Terrain control loading
 * @compliance Movement fragments (3 per move point) match freeciv exactly
 */
export const SINGLE_MOVE = 3; // 1 movement point = 3 movement fragments
export const MAX_MOVE_FRAGS = 65535; // Maximum movement fragments - matches freeciv exactly

/**
 * Terrain movement costs in movement fragments
 * @reference freeciv/data/classic/terrain.ruleset
 */
export const TERRAIN_MOVEMENT_COSTS: Record<string, number> = {
  // Flat terrain: 1 movement point = 3 fragments
  ocean: SINGLE_MOVE,
  coast: SINGLE_MOVE,
  deep_ocean: SINGLE_MOVE,
  lake: SINGLE_MOVE,
  plains: SINGLE_MOVE,
  grassland: SINGLE_MOVE,
  desert: SINGLE_MOVE,
  tundra: SINGLE_MOVE,

  // Rough terrain: 2 movement points = 6 fragments
  hills: SINGLE_MOVE * 2,
  forest: SINGLE_MOVE * 2,
  jungle: SINGLE_MOVE * 2,
  swamp: SINGLE_MOVE * 2,

  // Impassable terrain: 3 movement points = 9 fragments
  mountains: SINGLE_MOVE * 3,
};

/**
 * Unit movement capabilities
 * Defines movement types for different unit classes
 *
 * @reference freeciv/common/unittype.h - utype_move_type enum definition
 * @reference freeciv/common/movement.c - Movement type validation
 * @compliance Matches freeciv's unit movement classification system
 */
export enum MovementType {
  LAND = 'land',
  SEA = 'sea',
  BOTH = 'both', // Amphibious units
  AIR = 'air',
}

/**
 * Unit type movement capabilities
 */
export const UNIT_MOVEMENT_TYPES: Record<string, MovementType> = {
  // Land units
  warrior: MovementType.LAND,
  archer: MovementType.LAND,
  spearman: MovementType.LAND,
  settler: MovementType.LAND,

  // Sea units
  trireme: MovementType.SEA,

  // Future: Air units, amphibious units, etc.
};

/**
 * Get terrain movement cost for specific unit type
 * @reference freeciv/common/movement.c map_move_cost_unit()
 */
export function getTerrainMovementCost(terrain: string, unitTypeId?: string): number {
  const baseCost = TERRAIN_MOVEMENT_COSTS[terrain] || SINGLE_MOVE;

  // If no unit type specified, return base cost
  if (!unitTypeId) {
    return baseCost;
  }

  const movementType = UNIT_MOVEMENT_TYPES[unitTypeId] || MovementType.LAND;

  // Check movement type compatibility
  switch (movementType) {
    case MovementType.LAND:
      // Land units cannot move on water tiles (except coast which represents shallow water)
      if (terrain === 'ocean' || terrain === 'deep_ocean' || terrain === 'lake') {
        return -1; // Impassable
      }
      return baseCost;

    case MovementType.SEA:
      // Sea units can only move on water
      if (!['ocean', 'deep_ocean', 'coast', 'lake'].includes(terrain)) {
        return -1; // Impassable
      }
      return baseCost;

    case MovementType.BOTH:
      // Amphibious units can move anywhere
      return baseCost;

    case MovementType.AIR:
      // Air units ignore terrain (not implemented yet)
      return SINGLE_MOVE;

    default:
      return baseCost;
  }
}

/**
 * Check if unit can enter terrain type
 * @reference freeciv/common/movement.c can_unit_exist_at_tile()
 */
export function canUnitEnterTerrain(terrain: string, unitTypeId: string): boolean {
  return getTerrainMovementCost(terrain, unitTypeId) >= 0;
}

/**
 * Calculate movement cost between two tiles (including diagonal penalty)
 * @reference freeciv/common/movement.c map_move_cost_unit()
 */
export function calculateMovementCost(
  fromX: number,
  fromY: number,
  toX: number,
  toY: number,
  toTerrain: string,
  unitTypeId: string
): number {
  const baseCost = getTerrainMovementCost(toTerrain, unitTypeId);

  if (baseCost < 0) {
    return -1; // Impassable
  }

  // Check if it's a diagonal move
  const dx = Math.abs(toX - fromX);
  const dy = Math.abs(toY - fromY);
  const isDiagonal = dx === 1 && dy === 1;

  // Diagonal moves cost sqrt(2)  1.41 times more (simplified to 1.5x for integer math)
  return isDiagonal ? Math.floor(baseCost * 1.5) : baseCost;
}

/**
 * GameBroadcastManager - Handles all Socket.IO broadcasting and real-time communication
 * Extracted from GameManager.ts following the established refactoring patterns
 * @reference docs/refactor/REFACTORING_ARCHITECTURE_PATTERNS.md Manager-Service-Repository Pattern
 */

import { BaseGameService } from './GameService';
import { logger } from '../../utils/logger';
import type { Server as SocketServer } from 'socket.io';
import { PacketType, PACKET_NAMES } from '../../types/packet';
import type { GameInstance } from '../GameManager';

export interface BroadcastService {
  broadcastToGame(gameId: string, event: string, data: any): void;
  broadcastPacketToGame(gameId: string, packetType: PacketType, data: any): void;
  broadcastMapData(gameId: string, mapData: any): void;
}

export class GameBroadcastManager extends BaseGameService implements BroadcastService {
  private io: SocketServer;
  private games = new Map<string, GameInstance>();

  constructor(io: SocketServer) {
    super(logger);
    this.io = io;
  }

  getServiceName(): string {
    return 'GameBroadcastManager';
  }

  /**
   * Set games reference for validation
   */
  setGamesReference(games: Map<string, GameInstance>): void {
    this.games = games;
  }

  /**
   * Broadcast event to all players in a specific game room
   * @reference Original GameManager.ts:1875-1881 broadcastToGame()
   */
  broadcastToGame(gameId: string, event: string, data: any): void {
    const gameInstance = this.games.get(gameId);
    if (!gameInstance) {
      // Don't return early - still try to broadcast for compatibility
      this.logger.warn(
        'Broadcasting to game without local instance (might be normal during transitions)',
        {
          gameId,
          event,
          gamesCount: this.games.size,
          availableGameIds: Array.from(this.games.keys()),
        }
      );
    }

    // Always broadcast to all sockets in the specific game room (like original code)
    const room = this.io.to(`game:${gameId}`);
    if (!room || typeof room.emit !== 'function') {
      this.logger.error('Socket room is invalid', { gameId, room });
      return;
    }
    room.emit(event, data);

    this.logger.debug('Broadcasted event to game room', {
      gameId,
      event,
      playerCount: gameInstance?.players.size || 'unknown',
      dataSize: JSON.stringify(data).length,
    });
  }

  /**
   * Broadcast structured packet to game room
   * @reference Original GameManager.ts:1883-1903 broadcastPacketToGame()
   */
  broadcastPacketToGame(gameId: string, packetType: PacketType, data: any): void {
    const gameInstance = this.games.get(gameId);
    if (!gameInstance) {
      this.logger.warn('Attempted to broadcast packet to non-existent game', {
        gameId,
        packetType,
      });
      return;
    }

    // Create packet structure and broadcast to game room
    const packet = {
      type: packetType,
      data,
      timestamp: Date.now(),
    };

    this.io.to(`game:${gameId}`).emit('packet', packet);

    this.logger.debug('Broadcasted structured packet to game room', {
      gameId,
      packetType: PACKET_NAMES[packetType] || packetType,
      playerCount: gameInstance.players.size,
      data: Array.isArray(data?.tiles)
        ? { tilesCount: data.tiles.length, ...data, tiles: '[truncated]' }
        : data,
    });
  }

  /**
   * Broadcast map data to all players in game
   * @reference Original GameManager.ts:605-681 broadcastMapData()
   */
  broadcastMapData(gameId: string, mapData: any): void {
    const gameInstance = this.games.get(gameId);
    if (!gameInstance) {
      this.logger.warn('Attempted to broadcast map data to non-existent game', { gameId });
      return;
    }

    // DEBUG: Check map data completeness before broadcasting
    const tilesComplete = mapData && mapData.tiles && mapData.tiles.length > 0;
    const firstTileComplete = tilesComplete && mapData.tiles[0] && mapData.tiles[0].length > 0;
    const sampleTile = firstTileComplete ? mapData.tiles[0][0] : null;

    this.logger.info('Broadcasting map data to players', {
      gameId,
      mapSize: `${mapData.width}x${mapData.height}`,
      playerCount: gameInstance.players.size,
      tilesComplete,
      firstTileComplete,
      sampleTileTerrain: sampleTile?.terrain || 'undefined',
      sampleTileElevation: sampleTile?.elevation || 'undefined',
    });

    // Broadcast to each player individually to provide player-specific data
    for (const [playerId] of gameInstance.players) {
      this.sendMapDataToPlayer(gameInstance, gameId, playerId, mapData);
    }

    // Also broadcast general game started event
    this.broadcastToGame(gameId, 'game_ready', {
      gameId,
      mapSize: `${mapData.width}x${mapData.height}`,
      playerCount: gameInstance.players.size,
      currentTurn: gameInstance.currentTurn,
    });
  }

  /**
   * Broadcast to specific player
   */
  broadcastToPlayer(playerId: string, event: string, data: any): void {
    this.io.to(`player:${playerId}`).emit(event, data);

    this.logger.debug('Broadcasted event to specific player', {
      playerId,
      event,
      dataSize: JSON.stringify(data).length,
    });
  }

  /**
   * Broadcast to all connected sockets
   */
  broadcastGlobally(event: string, data: any): void {
    this.io.emit(event, data);

    this.logger.debug('Broadcasted event globally', {
      event,
      dataSize: JSON.stringify(data).length,
    });
  }

  /**
   * Get connected player count for a game
   * @reference Original GameManager.ts:1868-1874 getConnectedPlayerCount()
   */
  getConnectedPlayerCount(gameId: string): number {
    const gameInstance = this.games.get(gameId);
    if (!gameInstance) return 0;

    return Array.from(gameInstance.players.values()).filter(player => player.isConnected).length;
  }

  /**
   * Send map data to a specific player
   */
  private sendMapDataToPlayer(
    gameInstance: any,
    gameId: string,
    playerId: string,
    mapData: any
  ): void {
    try {
      // TODO: Implement fog of war - for now send all tiles
      // Get player-specific visibility data (disabled until fog of war is implemented)
      const visibleTilesSet = gameInstance.visibilityManager.getVisibleTiles(playerId);

      // Process and format all tiles (no fog of war for now)
      const visibleTiles = this.processMapTilesForPlayer(mapData);

      // Get units visible to this player (delegate to UnitManager)
      const visibleUnits = gameInstance.unitManager.getVisibleUnits(playerId, visibleTilesSet);
      const formattedUnits = visibleUnits.map((unit: any) =>
        this.formatUnitForClient(unit, gameInstance.unitManager)
      );

      // Send MAP_INFO packet first (like original code)
      const mapInfoPacket = {
        xsize: mapData.width,
        ysize: mapData.height,
        wrap_id: 0, // Flat earth
        topology_id: 0,
      };
      this.broadcastPacketToGame(gameId, PacketType.MAP_INFO, mapInfoPacket);

      // Send tiles in batches like original code
      this.sendTileDataInBatches(gameId, visibleTiles);

      this.logger.debug('Sent player-specific map data', {
        gameId,
        playerId,
        tilesCount: visibleTiles.length,
        unitsCount: formattedUnits.length,
        batches: Math.ceil(visibleTiles.length / 100),
      });
    } catch (error) {
      this.logger.error('Error sending map data to player:', {
        error: error instanceof Error ? error.message : error,
        gameId,
        playerId,
      });
    }
  }

  /**
   * Process map tiles for player visibility
   */
  private processMapTilesForPlayer(mapData: any): any[] {
    const visibleTiles = [];
    for (let y = 0; y < mapData.height; y++) {
      for (let x = 0; x < mapData.width; x++) {
        const tileInfo = this.createTileInfo(mapData, x, y);
        if (tileInfo) {
          visibleTiles.push(tileInfo);
        }
      }
    }
    return visibleTiles;
  }

  /**
   * Create tile information object for a specific coordinate
   */
  private createTileInfo(mapData: any, x: number, y: number): any | null {
    const index = x + y * mapData.width;
    // Handle column-based tile array structure: mapData.tiles[x][y]
    const serverTile = mapData.tiles[x] && mapData.tiles[x][y];

    if (!serverTile) {
      return null;
    }

    // Format tile in exact freeciv-web format
    return {
      tile: index, // This is the key - tile index used by freeciv-web
      x: x,
      y: y,
      terrain: serverTile.terrain,
      resource: serverTile.resource,
      elevation: serverTile.elevation || 0,
      riverMask: serverTile.riverMask || 0,
      known: 1, // TILE_KNOWN
      seen: 1,
      player: null,
      worked: null,
      extras: 0, // BitVector for extras
    };
  }

  /**
   * Send tile data in batches
   */
  private sendTileDataInBatches(gameId: string, visibleTiles: any[]): void {
    const BATCH_SIZE = 100;
    for (let i = 0; i < visibleTiles.length; i += BATCH_SIZE) {
      const batch = visibleTiles.slice(i, i + BATCH_SIZE);

      // DEBUG: Check first tile in batch for completeness
      if (i === 0 && batch.length > 0) {
        this.logger.info('First TILE_INFO batch sample:', {
          firstTile: {
            tile: batch[0].tile,
            x: batch[0].x,
            y: batch[0].y,
            terrain: batch[0].terrain,
            elevation: batch[0].elevation,
            known: batch[0].known,
            seen: batch[0].seen,
          },
        });
      }

      this.broadcastPacketToGame(gameId, PacketType.TILE_INFO, {
        tiles: batch,
        startIndex: i,
        endIndex: Math.min(i + BATCH_SIZE, visibleTiles.length),
        total: visibleTiles.length,
      });
    }
  }

  /**
   * Format unit data for client transmission
   * @reference Original GameManager.ts:800-832 formatUnitForClient()
   */
  private formatUnitForClient(unit: any, unitManager: any): any {
    return {
      id: unit.id,
      playerId: unit.playerId,
      type: unit.type,
      x: unit.x,
      y: unit.y,
      movementLeft: unit.movementLeft,
      maxMovement: unitManager.getUnitMaxMovement(unit.type),
      health: unit.health || 100,
      veteran: unit.veteran || false,
      homeCity: unit.homeCity || null,
      activity: unit.activity || 'idle',
      fortified: unit.fortified || false,
      orders: unit.orders || null,
    };
  }
}

import { eq } from 'drizzle-orm';
import { DatabaseProvider } from '../../database';
import { games } from '../../database/schema';
import { GameInstance, PlayerState, TurnPhase } from '../GameManager';
import { BaseGameService } from './GameService';
import { logger } from '../../utils/logger';
import { CityManager } from '../CityManager';
import { MapManager } from '../MapManager';
import { PathfindingManager } from '../PathfindingManager';
import { ResearchManager } from '../ResearchManager';
import { TurnManager } from '../TurnManager';
import { UnitManager } from '../UnitManager';
import { VisibilityManager } from '../VisibilityManager';
import { Server as SocketServer } from 'socket.io';

/**
 * GameInstanceRecoveryService - Extracted game recovery operations from GameManager
 * @reference docs/refactor/REFACTORING_PLAN.md - Phase 1 GameManager refactoring
 *
 * Handles all game instance recovery and restoration including:
 * - Game instance recovery from database
 * - Map data restoration and deserialization
 * - Manager initialization and state restoration
 * - Database-to-memory synchronization
 */
export class GameInstanceRecoveryService extends BaseGameService {
  constructor(
    private databaseProvider: DatabaseProvider,
    private games: Map<string, GameInstance>,
    private playerToGame: Map<string, string>,
    private io: SocketServer,
    private foundCity: (
      gameId: string,
      playerId: string,
      name: string,
      x: number,
      y: number
    ) => Promise<string>,
    private requestPath: (
      playerId: string,
      unitId: string,
      targetX: number,
      targetY: number
    ) => Promise<any>,
    private createUnit: (
      gameId: string,
      playerId: string,
      unitType: string,
      x: number,
      y: number
    ) => Promise<string>,
    private broadcastToGame: (gameId: string, event: string, data: any) => void
  ) {
    super(logger);
  }

  getServiceName(): string {
    return 'GameInstanceRecoveryService';
  }

  /**
   * Recover a game instance from database storage
   * @reference Original GameManager.recoverGameInstance()
   */
  public async recoverGameInstance(gameId: string): Promise<GameInstance | null> {
    try {
      logger.info('Attempting to recover game instance from database', { gameId });

      // Get game from database with all related data
      const game = await this.databaseProvider.getDatabase().query.games.findFirst({
        where: eq(games.id, gameId),
        with: {
          players: true,
        },
      });

      if (!game || game.status !== 'active') {
        logger.warn('Game not found or not active, cannot recover', {
          gameId,
          found: !!game,
          status: game?.status,
        });
        return null;
      }

      // Check if map data exists in database
      if (!game.mapData || !game.mapSeed) {
        logger.warn('No map data found in database, cannot recover game instance', { gameId });
        return null;
      }

      logger.info('Recovering game instance with map data', {
        gameId,
        playerCount: game.players.length,
        mapSize: `${game.mapWidth}x${game.mapHeight}`,
      });

      // Reconstruct player state map
      const players = new Map<string, PlayerState>();
      for (const dbPlayer of game.players) {
        players.set(dbPlayer.id, {
          id: dbPlayer.id,
          userId: dbPlayer.userId,
          playerNumber: dbPlayer.playerNumber,
          civilization: dbPlayer.civilization,
          isReady: dbPlayer.isReady || false,
          hasEndedTurn: dbPlayer.hasEndedTurn || false,
          isConnected: dbPlayer.connectionStatus === 'connected',
          lastSeen: new Date(),
        });

        // Track player to game mapping
        this.playerToGame.set(dbPlayer.id, gameId);
      }

      // Extract terrain settings from stored game state
      const storedTerrainSettings = (game.gameState as any)?.terrainSettings;
      const temperatureParam = storedTerrainSettings?.temperature ?? 50;

      // Create MapManager and restore map data from database
      const mapManager = new MapManager(
        game.mapWidth,
        game.mapHeight,
        undefined,
        'recovered',
        undefined,
        undefined,
        false,
        temperatureParam
      );
      await this.restoreMapDataToManager(mapManager, game.mapData as any, game.mapSeed!);

      // Initialize managers (now that mapManager is available)
      const turnManager = new TurnManager(gameId, this.databaseProvider, this.io);
      const unitManager = new UnitManager(
        gameId,
        this.databaseProvider,
        game.mapWidth,
        game.mapHeight,
        mapManager,
        {
          foundCity: this.foundCity.bind(this),
          requestPath: this.requestPath.bind(this),
          broadcastUnitMoved: (gameId, unitId, x, y, movementLeft) => {
            this.broadcastToGame(gameId, 'unit_moved', { gameId, unitId, x, y, movementLeft });
          },
          getCityAt: (x: number, y: number) => {
            const city = cityManager.getCityAt(x, y);
            return city ? { playerId: city.playerId } : null;
          },
        }
      );

      // Initialize turn system with existing player IDs
      const playerIds = Array.from(players.keys());
      await turnManager.initializeTurn(playerIds);
      const cityManager = new CityManager(gameId, this.databaseProvider, undefined, {
        createUnit: (playerId: string, unitType: string, x: number, y: number) =>
          this.createUnit(gameId, playerId, unitType, x, y),
      });
      const researchManager = new ResearchManager(gameId, this.databaseProvider);
      const pathfindingManager = new PathfindingManager(game.mapWidth, game.mapHeight, mapManager);

      const visibilityManager = new VisibilityManager(gameId, unitManager, mapManager);

      // Create recovered game instance
      const gameInstance: GameInstance = {
        id: gameId,
        config: {
          name: game.name,
          hostId: game.hostId,
          maxPlayers: game.maxPlayers,
          mapWidth: game.mapWidth,
          mapHeight: game.mapHeight,
          ruleset: game.ruleset || 'classic',
          turnTimeLimit: game.turnTimeLimit || undefined,
          victoryConditions: (game.victoryConditions as string[]) || [
            'conquest',
            'science',
            'culture',
          ],
        },
        state: 'active',
        currentTurn: game.currentTurn,
        turnPhase: game.turnPhase as TurnPhase,
        players,
        turnManager,
        mapManager,
        unitManager,
        visibilityManager,
        cityManager,
        researchManager,
        pathfindingManager,
        lastActivity: new Date(),
      };

      // Store the recovered game instance
      this.games.set(gameId, gameInstance);

      // Load data from database into managers
      await cityManager.loadCities();
      await unitManager.loadUnits();

      // Initialize research and visibility for all players
      for (const player of players.values()) {
        await researchManager.initializePlayerResearch(player.id);
        visibilityManager.initializePlayerVisibility(player.id);
        // Grant initial visibility around starting position
        visibilityManager.updatePlayerVisibility(player.id);
      }

      logger.info('Game instance recovered successfully', { gameId });
      return gameInstance;
    } catch (error) {
      logger.error('Failed to recover game instance:', error);
      return null;
    }
  }

  /**
   * Load a game from database into memory for testing purposes
   * @reference Original GameManager.loadGame()
   */
  public async loadGame(gameId: string): Promise<GameInstance | null> {
    // Check if game is already loaded
    const existingInstance = this.games.get(gameId);
    if (existingInstance) {
      return existingInstance;
    }

    // Try to recover from database
    return await this.recoverGameInstance(gameId);
  }

  /**
   * Restore map data from database to MapManager
   * @reference Original GameManager.restoreMapDataToManager()
   */
  private async restoreMapDataToManager(
    mapManager: MapManager,
    mapData: any,
    mapSeed: string
  ): Promise<void> {
    try {
      // Reconstruct full MapData from serialized database storage
      const restoredMapData = {
        width: mapData.width,
        height: mapData.height,
        seed: mapSeed,
        generatedAt: new Date(mapData.generatedAt),
        startingPositions: mapData.startingPositions || [],
        tiles: this.deserializeMapTiles(mapData.tiles, mapData.width, mapData.height),
      };

      // Set the restored map data directly in MapManager
      // This bypasses generation and uses the stored data
      (mapManager as any).mapData = restoredMapData;

      logger.info('Map data restored to manager', {
        width: restoredMapData.width,
        height: restoredMapData.height,
        startingPositions: restoredMapData.startingPositions.length,
      });
    } catch (error) {
      logger.error('Failed to restore map data to manager:', error);
      throw error;
    }
  }

  /**
   * Deserialize compressed map tiles from database storage
   * @reference Original GameManager.deserializeMapTiles()
   */
  private deserializeMapTiles(compressedTiles: any, width: number, height: number): any[][] {
    // Create empty tile array filled with ocean tiles - match generation pattern [x][y]
    const tiles: any[][] = [];

    for (let x = 0; x < width; x++) {
      tiles[x] = [];
      for (let y = 0; y < height; y++) {
        // Default ocean tile
        tiles[x][y] = {
          x,
          y,
          terrain: 'ocean',
          elevation: 0,
          riverMask: 0,
          continentId: 0,
          isExplored: false,
          isVisible: false,
          hasRoad: false,
          hasRailroad: false,
          improvements: [],
          unitIds: [],
          properties: {},
          temperature: 4, // TEMPERATE
          wetness: 50,
        };
      }
    }

    // Restore non-ocean tiles from compressed storage
    if (compressedTiles) {
      for (const [key, tileData] of Object.entries(compressedTiles)) {
        const [x, y] = key.split(',').map(Number);
        if (
          x >= 0 &&
          x < width &&
          y >= 0 &&
          y < height &&
          tileData &&
          typeof tileData === 'object'
        ) {
          tiles[x][y] = {
            ...tiles[x][y], // Keep default values
            ...(tileData as any), // Override with stored data
          };
        }
      }
    }

    return tiles;
  }
}

/**
 * GameLifecycleManager - Handles game creation, initialization, starting, and cleanup
 * Extracted from GameManager.ts following the established refactoring patterns
 * @reference docs/refactor/REFACTORING_ARCHITECTURE_PATTERNS.md Manager-Service-Repository Pattern
 */

import { BaseGameService } from './GameService';
import { logger } from '../../utils/logger';
import { DatabaseProvider } from '../../database';
import { gameState } from '../../database/redis';
import { games } from '../../database/schema';
import { eq } from 'drizzle-orm';
import serverConfig from '../../config';
import { TurnManager } from '../TurnManager';
import { MapManager, MapGeneratorType } from '../MapManager';
import { UnitManager } from '../UnitManager';
import { VisibilityManager } from '../VisibilityManager';
import { CityManager } from '../CityManager';
import { ResearchManager } from '../ResearchManager';
import { PathfindingManager } from '../PathfindingManager';
import { MapStartpos } from '../map/MapTypes';
import type { Server as SocketServer } from 'socket.io';
import type { GameConfig, GameInstance, PlayerState, TerrainSettings } from '../GameManager';

export interface GameLifecycleService {
  createGame(gameConfig: GameConfig): Promise<string>;
  startGame(gameId: string, hostId: string): Promise<void>;
  deleteGame(gameId: string, userId?: string): Promise<void>;
  cleanupInactiveGames(): Promise<void>;
  initializeGameInstance(
    gameId: string,
    game: any,
    terrainSettings?: TerrainSettings
  ): Promise<GameInstance>;
}

export class GameLifecycleManager extends BaseGameService implements GameLifecycleService {
  private io: SocketServer;
  private databaseProvider: DatabaseProvider;
  private games: Map<string, GameInstance>;
  private onBroadcast?: (gameId: string, event: string, data: any) => void;
  private onPersistMapData?: (
    gameId: string,
    mapData: any,
    terrainSettings?: TerrainSettings
  ) => Promise<void>;
  private onCreateStartingUnits?: (
    gameId: string,
    mapData: any,
    unitManager: any,
    players: Map<string, PlayerState>
  ) => Promise<void>;
  private onFoundCity?: (
    gameId: string,
    playerId: string,
    name: string,
    x: number,
    y: number
  ) => Promise<string>;
  // private _onRequestPath - removed, delegating to GameManager instead
  private onBroadcastMapData?: (gameId: string, mapData: any) => void;

  constructor(
    io: SocketServer,
    databaseProvider: DatabaseProvider,
    games: Map<string, GameInstance>,
    onBroadcast?: (gameId: string, event: string, data: any) => void,
    onPersistMapData?: (
      gameId: string,
      mapData: any,
      terrainSettings?: TerrainSettings
    ) => Promise<void>,
    onCreateStartingUnits?: (
      gameId: string,
      mapData: any,
      unitManager: any,
      players: Map<string, PlayerState>
    ) => Promise<void>,
    onFoundCity?: (
      gameId: string,
      playerId: string,
      name: string,
      x: number,
      y: number
    ) => Promise<string>,
    // _onRequestPath removed - delegating to GameManager instead
    onBroadcastMapData?: (gameId: string, mapData: any) => void
  ) {
    super(logger);
    this.io = io;
    this.databaseProvider = databaseProvider;
    this.games = games;
    this.onBroadcast = onBroadcast;
    this.onPersistMapData = onPersistMapData;
    this.onCreateStartingUnits = onCreateStartingUnits;
    this.onFoundCity = onFoundCity;
    // this._onRequestPath removed - delegating to GameManager instead
    this.onBroadcastMapData = onBroadcastMapData;
  }

  getServiceName(): string {
    return 'GameLifecycleManager';
  }

  /**
   * Create a new game with specified configuration
   * @reference Original GameManager.ts:93-136 createGame()
   */
  async createGame(gameConfig: GameConfig): Promise<string> {
    this.logger.info('Creating new game', { name: gameConfig.name, hostId: gameConfig.hostId });

    // Prepare game data for database
    const gameData = {
      name: gameConfig.name,
      hostId: gameConfig.hostId,
      gameType: gameConfig.gameType || 'multiplayer',
      maxPlayers: gameConfig.maxPlayers || 8,
      mapWidth: gameConfig.mapWidth || 80,
      mapHeight: gameConfig.mapHeight || 50,
      ruleset: gameConfig.ruleset || 'classic',
      turnTimeLimit: gameConfig.turnTimeLimit,
      victoryConditions: gameConfig.victoryConditions || ['conquest', 'science', 'culture'],
      gameState: {
        terrainSettings: gameConfig.terrainSettings || {
          generator: 'random',
          landmass: 'normal',
          huts: 15,
          temperature: 50,
          wetness: 50,
          rivers: 50,
          resources: 'normal',
        },
      },
    };

    const [newGame] = await this.databaseProvider
      .getDatabase()
      .insert(games)
      .values(gameData)
      .returning();

    // Cache basic game data in Redis for performance
    await gameState.setGameState(newGame.id, {
      state: newGame.status,
      currentTurn: newGame.currentTurn,
      turnPhase: newGame.turnPhase,
      playerCount: 0,
    });

    this.logger.info('Game created successfully', { gameId: newGame.id });
    return newGame.id;
  }

  /**
   * Start a game after validation and initialization
   * @reference Original GameManager.ts:352-410 startGame()
   */
  async startGame(gameId: string, hostId: string): Promise<void> {
    // Get game from database
    const game = await this.databaseProvider.getDatabase().query.games.findFirst({
      where: eq(games.id, gameId),
      with: {
        players: true,
      },
    });

    if (!game) {
      throw new Error('Game not found');
    }

    if (game.hostId !== hostId) {
      throw new Error('Only the host can start the game');
    }

    // Different minimum requirements for single vs multiplayer
    const minPlayers = game.gameType === 'single' ? 1 : serverConfig.game.minPlayersToStart;
    if (game.players.length < minPlayers) {
      throw new Error(`Need at least ${minPlayers} players to start`);
    }

    if (game.status !== 'waiting') {
      throw new Error('Game is not in waiting state');
    }

    this.logger.info('Starting game', { gameId, playerCount: game.players.length });

    // Update database to active state
    await this.databaseProvider
      .getDatabase()
      .update(games)
      .set({
        status: 'active',
        startedAt: new Date(),
        currentTurn: 1,
      })
      .where(eq(games.id, gameId));

    // Update Redis cache
    await gameState.setGameState(gameId, {
      state: 'active',
      currentTurn: 1,
      turnPhase: 'movement',
      playerCount: game.players.length,
    });

    // Create a preliminary game instance with players to enable broadcasts during initialization
    const preliminaryPlayers = new Map<string, PlayerState>();
    for (const dbPlayer of game.players) {
      preliminaryPlayers.set(dbPlayer.id, {
        id: dbPlayer.id,
        userId: dbPlayer.userId,
        playerNumber: dbPlayer.playerNumber,
        civilization: dbPlayer.civilization,
        isReady: false,
        hasEndedTurn: false,
        isConnected: true,
        lastSeen: new Date(),
      });
    }

    // Store preliminary instance to enable broadcasts during initialization
    const preliminaryInstance: GameInstance = {
      id: gameId,
      config: {
        name: game.name,
        hostId: game.hostId,
        gameType: game.gameType as 'single' | 'multiplayer' | undefined,
        maxPlayers: game.maxPlayers ?? undefined,
        mapWidth: game.mapWidth ?? undefined,
        mapHeight: game.mapHeight ?? undefined,
        ruleset: game.ruleset ?? undefined,
        turnTimeLimit: game.turnTimeLimit ?? undefined,
        victoryConditions: game.victoryConditions as string[] | undefined,
        terrainSettings: (game.gameState as any)?.terrainSettings,
      },
      state: 'active',
      currentTurn: 1,
      turnPhase: 'movement',
      players: preliminaryPlayers,
      turnManager: null as any,
      mapManager: null as any,
      unitManager: null as any,
      visibilityManager: null as any,
      cityManager: null as any,
      researchManager: null as any,
      pathfindingManager: null as any,
      lastActivity: new Date(),
    };
    this.games.set(gameId, preliminaryInstance);

    // Initialize the full game instance with map generation
    const storedTerrainSettings = (game.gameState as any)?.terrainSettings;
    const gameInstance = await this.initializeGameInstance(gameId, game, storedTerrainSettings);

    // Replace with the fully initialized instance
    this.games.set(gameId, gameInstance);

    // Broadcast initial map data now that all managers are initialized
    this.onBroadcastMapData?.(gameId, gameInstance.mapManager.getMapData());

    // Notify all players that the game has started
    this.onBroadcast?.(gameId, 'game-started', {
      gameId,
      currentTurn: 1,
    });

    this.logger.info('Game started successfully', { gameId });
  }

  /**
   * Initialize game instance with all managers and map generation
   * @reference Original GameManager.ts:412-604 initializeGameInstance()
   */
  async initializeGameInstance(
    gameId: string,
    game: any,
    terrainSettings?: TerrainSettings
  ): Promise<GameInstance> {
    this.logger.info('Initializing game instance', { gameId });

    // Create player state map
    const players = new Map<string, PlayerState>();
    for (const dbPlayer of game.players) {
      players.set(dbPlayer.id, {
        id: dbPlayer.id,
        userId: dbPlayer.userId,
        playerNumber: dbPlayer.playerNumber,
        civilization: dbPlayer.civilization,
        isReady: false,
        hasEndedTurn: false,
        isConnected: true,
        lastSeen: new Date(),
      });
    }

    // Initialize managers with terrain settings
    const mapGenerator = terrainSettings?.generator || 'random';
    const temperatureParam = terrainSettings?.temperature ?? 50;
    const mapManager = new MapManager(
      game.mapWidth,
      game.mapHeight,
      undefined,
      mapGenerator,
      undefined,
      undefined,
      false,
      temperatureParam
    );

    const turnManager = new TurnManager(gameId, this.databaseProvider, this.io);

    // Initialize turn system with player IDs
    const playerIds = Array.from(players.keys());
    await turnManager.initializeTurn(playerIds);

    // Create cityManager first to avoid circular dependency
    const cityManager = new CityManager(gameId, this.databaseProvider, undefined, {
      createUnit: (_playerId: string, _unitType: string, _x: number, _y: number) =>
        // This callback will be handled by the main GameManager
        Promise.resolve(''),
    });

    // Create UnitManager with proper dependencies
    const unitManager = new UnitManager(
      gameId,
      this.databaseProvider,
      game.mapWidth,
      game.mapHeight,
      mapManager,
      {
        foundCity: this.onFoundCity
          ? (gameId: string, playerId: string, name: string, x: number, y: number) =>
              this.onFoundCity!(gameId, playerId, name, x, y)
          : async () => '',
        requestPath: async (playerId: string, unitId: string, targetX: number, targetY: number) => {
          // Delegate to the main GameManager's requestPath method
          // We need access to the GameManager instance that created this lifecycle manager

          // For now, we'll use a direct approach through the games map
          // This should be the same GameManager instance that created us
          const gameInstance = this.games.get(gameId);
          if (!gameInstance) {
            return { success: false, error: 'Game instance not found' };
          }

          // Use the GameManager's pathfinding directly via the game instance
          try {
            const unit = gameInstance.unitManager.getUnit(unitId);
            if (!unit) {
              return { success: false, error: 'Unit not found' };
            }

            if (unit.playerId !== playerId) {
              return { success: false, error: 'Unit does not belong to player' };
            }

            // Call PathfindingManager directly
            const pathResult = await gameInstance.pathfindingManager.findPath(
              unit,
              targetX,
              targetY
            );

            const tiles = Array.isArray(pathResult?.path) ? pathResult.path : [];
            const isValid = pathResult?.valid && tiles.length > 0;

            return {
              success: isValid,
              path: isValid
                ? {
                    unitId,
                    targetX,
                    targetY,
                    tiles: tiles,
                    totalCost: pathResult.totalCost || 0,
                    estimatedTurns: pathResult.estimatedTurns || 0,
                    valid: isValid,
                  }
                : undefined,
              error: isValid ? undefined : 'No valid path found',
            };
          } catch (error) {
            logger.error('Error in GameLifecycleManager requestPath delegation:', error);
            return { success: false, error: 'Pathfinding error' };
          }
        },
        broadcastUnitMoved: (gameId, unitId, x, y, movementLeft) => {
          this.onBroadcast?.(gameId, 'unit_moved', { gameId, unitId, x, y, movementLeft });
        },
        getCityAt: (x: number, y: number) => {
          const city = cityManager.getCityAt(x, y);
          return city ? { playerId: city.playerId } : null;
        },
      }
    );

    const visibilityManager = new VisibilityManager(gameId, unitManager, mapManager);

    const researchManager = new ResearchManager(gameId, this.databaseProvider);
    const pathfindingManager = new PathfindingManager(game.mapWidth, game.mapHeight, mapManager);

    // Generate the map with starting positions based on terrain settings
    await this.generateGameMap(gameId, mapManager, players, terrainSettings, unitManager);

    // Create game instance
    const gameInstance: GameInstance = {
      id: gameId,
      config: {
        name: game.name,
        hostId: game.hostId,
        gameType: game.gameType,
        maxPlayers: game.maxPlayers,
        mapWidth: game.mapWidth,
        mapHeight: game.mapHeight,
        ruleset: game.ruleset,
        turnTimeLimit: game.turnTimeLimit,
        victoryConditions: game.victoryConditions,
        terrainSettings: terrainSettings,
      },
      state: 'active',
      currentTurn: 1,
      turnPhase: 'movement',
      players,
      turnManager,
      mapManager,
      unitManager,
      visibilityManager,
      cityManager,
      researchManager,
      pathfindingManager,
      lastActivity: new Date(),
    };

    this.logger.info('Game instance initialized successfully', {
      gameId,
      playerCount: players.size,
    });
    return gameInstance;
  }

  /**
   * Delete a game and clean up all associated resources
   * @reference Original GameManager.ts:1905-1950 deleteGame()
   */
  async deleteGame(gameId: string, userId?: string): Promise<void> {
    // Check if game exists
    const game = await this.databaseProvider.getDatabase().query.games.findFirst({
      where: eq(games.id, gameId),
      with: {
        players: true,
      },
    });

    if (!game) {
      throw new Error('Game not found');
    }

    this.logger.info('Deleting game', { gameId, userId });

    // Remove from active games map if it exists
    const gameInstance = this.games.get(gameId);
    if (gameInstance) {
      // Cleanup managers
      gameInstance.visibilityManager.cleanup();
      gameInstance.cityManager.cleanup();

      // Remove from games map after all cleanup operations are complete
      this.games.delete(gameId);
    }

    // Update database to mark game as ended
    await this.databaseProvider
      .getDatabase()
      .update(games)
      .set({
        status: 'ended',
        endedAt: new Date(),
      })
      .where(eq(games.id, gameId));

    // Clear Redis cache
    await gameState.clearGameState(gameId);

    // Notify all players in the game room
    this.io.to(`game:${gameId}`).emit('game_deleted', { gameId });
  }

  /**
   * Clean up inactive games older than threshold
   * @reference Original GameManager.ts:1952-1994 cleanupInactiveGames()
   */
  async cleanupInactiveGames(): Promise<void> {
    const now = new Date();
    const inactiveThreshold = 30 * 60 * 1000; // 30 minutes

    const inactiveGames = Array.from(this.games.values()).filter(game => {
      const timeSinceActivity = now.getTime() - game.lastActivity.getTime();
      return timeSinceActivity > inactiveThreshold;
    });

    this.logger.info(`Cleaning up ${inactiveGames.length} inactive games`);

    for (const game of inactiveGames) {
      try {
        await this.deleteGame(game.id);
        this.logger.info('Cleaned up inactive game', { gameId: game.id });
      } catch (error) {
        this.logger.error('Failed to cleanup inactive game:', error);
      }
    }
  }

  /**
   * Get all active game instances
   */
  getActiveGameInstances(): GameInstance[] {
    return Array.from(this.games.values()).filter(game => game.state === 'active');
  }

  /**
   * Get specific game instance
   */
  getGameInstance(gameId: string): GameInstance | null {
    return this.games.get(gameId) || null;
  }

  /**
   * Get all game instances
   */
  getAllGameInstances(): GameInstance[] {
    return Array.from(this.games.values());
  }

  /**
   * Generate map for the game with all required setup
   * @reference Original GameManager.ts:474-604 map generation logic
   */
  private async generateGameMap(
    gameId: string,
    mapManager: MapManager,
    players: Map<string, PlayerState>,
    terrainSettings?: TerrainSettings,
    unitManager?: UnitManager
  ): Promise<void> {
    // Generate the map with starting positions based on terrain settings
    const generator = terrainSettings?.generator || 'random';
    const startpos = terrainSettings?.startpos ?? MapStartpos.DEFAULT;

    this.logger.debug('Map generation starting', { terrainSettings, generator, startpos });

    const generatorType = this.convertGeneratorType(generator);
    let generationAttempted = false;
    let lastError: Error | null = null;

    try {
      this.logger.info('Delegating to restructured MapManager', {
        generator,
        generatorType,
        reference: 'apps/server/src/game/MapManager.ts:97-138',
      });

      // Delegate to restructured MapManager system
      await mapManager.generateMap(players, generatorType);
      generationAttempted = true;
    } catch (error) {
      lastError = error instanceof Error ? error : new Error(String(error));
      this.logger.error('Map generation failed, attempting emergency recovery', {
        generator: generatorType,
        error: lastError.message,
      });
    }

    // Emergency fallback sequence (defensive addition, not in freeciv)
    if (!generationAttempted || !mapManager.getMapData()) {
      this.logger.warn('Initiating emergency fallback sequence (defensive extension)');

      try {
        this.logger.info('Emergency fallback: MAPGEN_FRACTAL');
        await mapManager.generateMap(players, 'FRACTAL');
        generationAttempted = true;
      } catch (error) {
        this.logger.error('Emergency fractal failed, trying final MAPGEN_RANDOM fallback', {
          error: error instanceof Error ? error.message : error,
        });

        try {
          this.logger.info('Final emergency fallback: MAPGEN_RANDOM');
          await mapManager.generateMap(players, 'RANDOM');
          generationAttempted = true;
        } catch (error) {
          const finalError = error instanceof Error ? error : new Error(String(error));
          this.logger.error('All generation methods exhausted', {
            originalError: lastError?.message,
            finalError: finalError.message,
          });
          throw new Error(
            `Complete map generation failure. Original: ${
              lastError?.message || 'unknown'
            }, Final: ${finalError.message}`
          );
        }
      }
    }

    const mapData = mapManager.getMapData();
    if (!mapData) {
      throw new Error('Map generation failed - no map data available');
    }

    this.logger.info('Map generated successfully', {
      gameId,
      mapSize: `${mapData.width}x${mapData.height}`,
      generator: generatorType,
      startingPositions: mapData.startingPositions?.length || 0,
    });

    // Persist map data to database
    await this.onPersistMapData?.(gameId, mapData, terrainSettings);

    // Create starting units for all players
    if (unitManager) {
      await this.onCreateStartingUnits?.(gameId, mapData, unitManager, players);
    }

    // Broadcast initial map data to all players
    this.onBroadcast?.(gameId, 'map_generated', {
      gameId,
      mapSize: `${mapData.width}x${mapData.height}`,
      startingPositions: mapData.startingPositions,
    });
  }

  /**
   * Convert generator string to MapGeneratorType
   * @reference Original GameManager.ts:1104-1123 convertGeneratorType()
   */
  private convertGeneratorType(generator: string): MapGeneratorType {
    switch (generator.toLowerCase()) {
      case 'random':
        return 'RANDOM';
      case 'fractal':
        return 'FRACTAL';
      case 'island':
        return 'ISLAND';
      case 'fair':
        return 'FAIR';
      case 'scenario':
        return 'SCENARIO';
      default:
        this.logger.warn(`Unknown generator type: ${generator}, defaulting to RANDOM`);
        return 'RANDOM';
    }
  }
}

/**
 * GameStateManager - Handles all database operations and game state persistence
 * Extracted from GameManager.ts following the established refactoring patterns
 * @reference docs/refactor/REFACTORING_ARCHITECTURE_PATTERNS.md Manager-Service-Repository Pattern
 */

import { BaseGameService } from './GameService';
import { DatabaseProvider } from '../../database';
import { gameState } from '../../database/redis';
import { games, players } from '../../database/schema';
import { eq } from 'drizzle-orm';
import type { TerrainSettings } from '../GameManager';
import type { MapManager } from '../MapManager';

export interface GameStateRepository {
  createGameInDatabase(gameData: any): Promise<any>;
  updateGameState(gameId: string, updates: any): Promise<void>;
  loadGameFromDatabase(gameId: string): Promise<any | null>;
  persistMapData(gameId: string, mapData: any, terrainSettings?: TerrainSettings): Promise<void>;
  restoreMapDataToManager(mapManager: MapManager, mapData: any, seed: string): Promise<void>;
  cacheGameState(gameId: string, state: any): Promise<void>;
}

export class GameStateManager extends BaseGameService implements GameStateRepository {
  private databaseProvider: DatabaseProvider;

  constructor(logger: any, databaseProvider: DatabaseProvider) {
    super(logger);
    this.databaseProvider = databaseProvider;
  }

  getServiceName(): string {
    return 'GameStateManager';
  }

  /**
   * Create a new game in the database
   * @reference Original GameManager.ts:93-136 createGame()
   */
  async createGameInDatabase(gameData: any): Promise<any> {
    this.logger.info('Creating new game in database', {
      name: gameData.name,
      hostId: gameData.hostId,
    });

    const [newGame] = await this.databaseProvider
      .getDatabase()
      .insert(games)
      .values(gameData)
      .returning();

    // Cache basic game data in Redis for performance
    await this.cacheGameState(newGame.id, {
      state: newGame.status,
      currentTurn: newGame.currentTurn,
      turnPhase: newGame.turnPhase,
      playerCount: 0,
    });

    this.logger.info('Game created successfully in database', { gameId: newGame.id });
    return newGame;
  }

  /**
   * Update game state in database
   */
  async updateGameState(gameId: string, updates: any): Promise<void> {
    try {
      await this.databaseProvider
        .getDatabase()
        .update(games)
        .set({
          ...updates,
          updatedAt: new Date(),
        })
        .where(eq(games.id, gameId));

      this.logger.debug('Game state updated in database', { gameId, updates });
    } catch (error) {
      this.logger.error('Failed to update game state in database:', error);
      throw error;
    }
  }

  /**
   * Load game data from database with all related data
   * @reference Original GameManager.ts:868-873 recoverGameInstance()
   */
  async loadGameFromDatabase(gameId: string): Promise<any | null> {
    try {
      this.logger.info('Loading game from database', { gameId });

      const game = await this.databaseProvider.getDatabase().query.games.findFirst({
        where: eq(games.id, gameId),
        with: {
          players: true,
        },
      });

      if (!game) {
        this.logger.warn('Game not found in database', { gameId });
        return null;
      }

      return game;
    } catch (error) {
      this.logger.error('Failed to load game from database:', error);
      return null;
    }
  }

  /**
   * Get game by player ID from database
   * @reference Original GameManager.ts:1155-1192 getGameByPlayerId()
   */
  async getGameByPlayerId(playerId: string): Promise<any | null> {
    try {
      const player = await this.databaseProvider.getDatabase().query.players.findFirst({
        where: eq(players.id, playerId),
        with: {
          game: {
            with: {
              host: {
                columns: {
                  username: true,
                },
              },
              players: true,
            },
          },
        },
      });

      if (!player?.game) return null;

      const game = player.game;
      return {
        id: game.id,
        name: game.name,
        hostName: game.host?.username || 'Unknown',
        status: game.status,
        currentPlayers: game.players?.length || 0,
        maxPlayers: game.maxPlayers,
        currentTurn: game.currentTurn,
        mapSize: `${game.mapWidth}x${game.mapHeight}`,
        createdAt: game.createdAt.toISOString(),
        canJoin: game.status === 'waiting' && (game.players?.length || 0) < game.maxPlayers,
      };
    } catch (error) {
      this.logger.error('Error fetching game by player ID:', error);
      return null;
    }
  }

  /**
   * Get all games from database with optional user filtering
   * @reference Original GameManager.ts:1202-1251 getAllGamesFromDatabase()
   */
  async getAllGamesFromDatabase(userId?: string | null): Promise<any[]> {
    try {
      const gamesQuery = await this.databaseProvider.getDatabase().query.games.findMany({
        with: {
          host: {
            columns: {
              username: true,
            },
          },
          players: {
            columns: {
              id: true,
              userId: true,
              civilization: true,
              connectionStatus: true,
            },
          },
        },
        orderBy: (games, { desc }) => [desc(games.createdAt)],
      });

      return gamesQuery.map(game => ({
        id: game.id,
        name: game.name,
        hostName: game.host?.username || 'Unknown',
        status: game.status,
        currentPlayers: game.players?.length || 0,
        maxPlayers: game.maxPlayers,
        currentTurn: game.currentTurn,
        mapSize: `${game.mapWidth}x${game.mapHeight}`,
        createdAt: game.createdAt.toISOString(),
        canJoin: game.status === 'waiting' && (game.players?.length || 0) < game.maxPlayers,
        isPlayer: userId ? game.players?.some(p => p.userId === userId) : false,
        players:
          game.players?.map(p => ({
            id: p.id,
            civilization: p.civilization,
            isConnected: p.connectionStatus === 'connected',
          })) || [],
      }));
    } catch (error) {
      this.logger.error('Error fetching games from database:', error);
      return [];
    }
  }

  /**
   * Persist map data to database
   * @reference Original GameManager.ts:682-723 persistMapDataToDatabase()
   */
  async persistMapData(
    gameId: string,
    mapData: any,
    terrainSettings?: TerrainSettings
  ): Promise<void> {
    try {
      this.logger.info('Persisting map data to database', { gameId });

      // Serialize map data for storage
      const serializedMapData = {
        width: mapData.width,
        height: mapData.height,
        seed: mapData.seed,
        generatedAt: mapData.generatedAt.toISOString(),
        startingPositions: mapData.startingPositions,
        tiles: this.serializeMapTiles(mapData.tiles),
      };

      // Update database with map data and seed
      await this.databaseProvider
        .getDatabase()
        .update(games)
        .set({
          mapSeed: mapData.seed,
          mapData: serializedMapData,
          gameState: {
            terrainSettings: terrainSettings || null,
            mapGenerated: true,
            generatedAt: mapData.generatedAt.toISOString(),
          },
          updatedAt: new Date(),
        })
        .where(eq(games.id, gameId));

      this.logger.info('Map data persisted successfully', {
        gameId,
        mapSize: `${mapData.width}x${mapData.height}`,
      });
    } catch (error) {
      this.logger.error('Failed to persist map data to database:', error);
      // Don't throw error to avoid breaking game initialization
    }
  }

  /**
   * Restore map data from database to MapManager instance
   * @reference Original GameManager.ts:1014-1047 restoreMapDataToManager()
   */
  async restoreMapDataToManager(
