  /**
   * Cache game state in Redis
   */
  async cacheGameState(gameId: string, state: any): Promise<void> {
    try {
      await gameState.setGameState(gameId, state);
    } catch (error) {
      this.logger.error('Failed to cache game state in Redis:', error);
      // Don't throw - Redis caching is not critical
    }
  }

  /**
   * Serialize map tiles for database storage
   * @reference Original GameManager.ts:833-862 serializeMapTiles()
   */
  private serializeMapTiles(tiles: any[][]): any {
    try {
      const serializedTiles = tiles.map(row =>
        row.map(tile => ({
          terrain: tile.terrain,
          resource: tile.resource || null,
          improvement: tile.improvement || null,
          altitude: tile.altitude || 0,
          temperature: tile.temperature || 0,
          moisture: tile.moisture || 0,
          riverMask: tile.riverMask || 0,
          special: tile.special || null,
          x: tile.x,
          y: tile.y,
        }))
      );

      return serializedTiles;
    } catch (error) {
      this.logger.error('Error serializing map tiles:', error);
      throw error;
    }
  }

  /**
   * Deserialize map tiles from database storage
   * @reference Original GameManager.ts:1048-1103 deserializeMapTiles()
   */
  private deserializeMapTiles(compressedTiles: any, width: number, height: number): any[][] {
    try {
      if (!compressedTiles || !Array.isArray(compressedTiles)) {
        this.logger.warn('Invalid tile data format, creating empty tiles');
        return this.createEmptyTileArray(width, height);
      }

      // Restore tile objects with all properties
      const tiles = compressedTiles.map((row: any[]) =>
        row.map((tileData: any) => ({
          terrain: tileData.terrain || 'ocean',
          resource: tileData.resource || null,
          improvement: tileData.improvement || null,
          altitude: tileData.altitude || 0,
          temperature: tileData.temperature || 0,
          moisture: tileData.moisture || 0,
          riverMask: tileData.riverMask || 0,
          special: tileData.special || null,
          x: tileData.x || 0,
          y: tileData.y || 0,
        }))
      );

      this.logger.debug('Deserialized map tiles successfully', {
        rows: tiles.length,
        columns: tiles[0]?.length,
      });

      return tiles;
    } catch (error) {
      this.logger.error('Error deserializing map tiles:', error);
      this.logger.info('Creating empty tile array as fallback');
      return this.createEmptyTileArray(width, height);
    }
  }

  /**
   * Create empty tile array as fallback
   */
  private createEmptyTileArray(width: number, height: number): any[][] {
    const tiles: any[][] = [];
    for (let x = 0; x < width; x++) {
      tiles[x] = [];
      for (let y = 0; y < height; y++) {
        tiles[x][y] = {
          terrain: 'ocean',
          resource: null,
          improvement: null,
          altitude: 0,
          temperature: 0,
          moisture: 0,
          riverMask: 0,
          special: null,
          x,
          y,
        };
      }
    }
    return tiles;
  }

  /**
   * Delete game from database
   * @reference Original GameManager.ts:1905-1951 deleteGame()
   */
  async deleteGameFromDatabase(gameId: string, userId?: string): Promise<void> {
    try {
      // Get game to verify ownership or admin permissions
      const game = await this.databaseProvider.getDatabase().query.games.findFirst({
        where: eq(games.id, gameId),
      });

      if (!game) {
        throw new Error('Game not found');
      }

      // Check if user has permission to delete (only host can delete)
      if (userId && game.hostId !== userId) {
        throw new Error('Only the host can delete a game');
      }

      // Delete game (cascade should handle players)
      await this.databaseProvider.getDatabase().delete(games).where(eq(games.id, gameId));

      // Clean up Redis cache
      try {
        await gameState.clearGameState(gameId);
      } catch (redisError) {
        this.logger.warn('Failed to clean up Redis cache for deleted game:', redisError);
      }

      this.logger.info('Game deleted successfully from database', { gameId, deletedBy: userId });
    } catch (error) {
      this.logger.error('Failed to delete game from database:', error);
      throw error;
    }
  }
}

/**
 * PlayerConnectionManager - Handles all player join/leave operations and connection management
 * Extracted from GameManager.ts following the established refactoring patterns
 * @reference docs/refactor/REFACTORING_ARCHITECTURE_PATTERNS.md Manager-Service-Repository Pattern
 */

import { BaseGameService } from './GameService';
import { logger } from '../../utils/logger';
import { DatabaseProvider } from '../../database';
import { gameState } from '../../database/redis';
import { games, players } from '../../database/schema';
import { eq } from 'drizzle-orm';
import { RulesetLoader } from '../../shared/data/rulesets/RulesetLoader';
import serverConfig from '../../config';
// PlayerState type is used in comments and method parameters but imported from GameManager

export interface PlayerConnectionService {
  joinGame(gameId: string, userId: string, civilization?: string): Promise<string>;
  updatePlayerConnection(playerId: string, isConnected: boolean): Promise<void>;
  ensureMinimumPlayers(gameId: string): Promise<void>;
}

export class PlayerConnectionManager extends BaseGameService implements PlayerConnectionService {
  private playerToGame = new Map<string, string>();
  private databaseProvider: DatabaseProvider;
  private onBroadcast?: (gameId: string, event: string, data: any) => void;
  private onAutoStartGame?: (gameId: string, hostId: string) => Promise<void>;

  constructor(
    databaseProvider: DatabaseProvider,
    onBroadcast?: (gameId: string, event: string, data: any) => void,
    onAutoStartGame?: (gameId: string, hostId: string) => Promise<void>
  ) {
    super(logger);
    this.databaseProvider = databaseProvider;
    this.onBroadcast = onBroadcast;
    this.onAutoStartGame = onAutoStartGame;
  }

  getServiceName(): string {
    return 'PlayerConnectionManager';
  }

  /**
   * Handle player joining a game with nation selection and validation
   * @reference Original GameManager.ts:138-285 joinGame()
   */
  async joinGame(gameId: string, userId: string, civilization?: string): Promise<string> {
    // Get game from database
    const game = await this.databaseProvider.getDatabase().query.games.findFirst({
      where: eq(games.id, gameId),
      with: {
        players: true,
      },
    });

    if (!game) {
      throw new Error('Game not found');
    }

    // Check if user is already in the game first
    const existingPlayer = game.players.find(p => p.userId === userId);
    if (existingPlayer) {
      // Track player to game mapping for existing player
      this.playerToGame.set(existingPlayer.id, gameId);
      return existingPlayer.id; // Already joined - allow rejoining at any game status
    }

    // Only allow new players in waiting games
    if (game.status !== 'waiting') {
      throw new Error('Game is not accepting new players');
    }

    if (game.players.length >= game.maxPlayers) {
      throw new Error('Game is full');
    }

    // Create player in database
    const playerNumber = game.players.length + 1;

    // Validate and select nation
    const selectedNation = await this.validateAndSelectNation(civilization, game.players);

    const playerData = {
      gameId,
      userId,
      playerNumber,
      nation: selectedNation,
      civilization: selectedNation || `Civilization${playerNumber}`,
      leaderName: `Leader${playerNumber}`,
      color: {
        r: Math.floor(Math.random() * 255),
        g: Math.floor(Math.random() * 255),
        b: Math.floor(Math.random() * 255),
      },
    };

    const [newPlayer] = await this.databaseProvider
      .getDatabase()
      .insert(players)
      .values(playerData)
      .returning();

    // Track player to game mapping
    this.playerToGame.set(newPlayer.id, gameId);

    // Update Redis cache
    await gameState.setGameState(gameId, {
      state: game.status,
      currentTurn: game.currentTurn,
      turnPhase: game.turnPhase,
      playerCount: game.players.length + 1,
    });

    this.logger.info('Player joined game', { gameId, playerId: newPlayer.id, userId });

    // Notify all players in the game
    this.onBroadcast?.(gameId, 'player-joined', {
      playerId: newPlayer.id,
      playerNumber,
      civilization: playerData.civilization,
      playerCount: game.players.length + 1,
    });

    // Handle auto-start logic
    await this.handleAutoStart(gameId);

    return newPlayer.id;
  }

  /**
   * Update player connection status
   * @reference Original GameManager.ts:1292-1331 updatePlayerConnection()
   */
  async updatePlayerConnection(playerId: string, isConnected: boolean): Promise<void> {
    const gameId = this.playerToGame.get(playerId);
    if (!gameId) return;

    // Update database connection status
    try {
      await this.databaseProvider
        .getDatabase()
        .update(players)
        .set({
          connectionStatus: isConnected ? 'connected' : 'disconnected',
          lastActionAt: new Date(),
        })
        .where(eq(players.id, playerId));
    } catch (error) {
      this.logger.error('Failed to update player connection status in database:', error);
    }

    if (isConnected) {
      this.logger.info('Player reconnected', { gameId, playerId });
    } else {
      this.logger.info('Player disconnected', { gameId, playerId });
    }

    // Broadcast connection status update
    this.onBroadcast?.(gameId, 'player-connection-changed', {
      playerId,
      isConnected,
      timestamp: Date.now(),
    });
  }

  /**
   * Ensure game has minimum players by adding AI players if needed
   * @reference Original GameManager.ts:290-351 ensureMinimumPlayers()
   */
  async ensureMinimumPlayers(gameId: string): Promise<void> {
    // Get current game state
    const game = await this.databaseProvider.getDatabase().query.games.findFirst({
      where: eq(games.id, gameId),
      with: { players: true },
    });

    if (!game) {
      this.logger.warn('Game not found for minimum player check', { gameId });
      return;
    }

    const currentPlayerCount = game.players.length;
    const minPlayers = serverConfig.game.minPlayersToStart;

    if (currentPlayerCount >= minPlayers) {
      this.logger.debug('Game already has sufficient players', {
        gameId,
        currentPlayerCount,
        minPlayers,
      });
      return;
    }

    const aiPlayersNeeded = minPlayers - currentPlayerCount;
    this.logger.info('Adding AI players to meet minimum requirements', {
      gameId,
      currentPlayerCount,
      minPlayers,
      aiPlayersNeeded,
    });

    // Get available nations for AI players
    const availableNations = await this.getAvailableNations(game.players);

    for (let i = 0; i < aiPlayersNeeded && i < availableNations.length; i++) {
      const playerNumber = game.players.length + i + 1;
      const aiNation = availableNations[i];

      const aiPlayerData = {
        gameId,
        userId: null, // AI players have null userId
        playerNumber,
        nation: aiNation,
        civilization: aiNation,
        leaderName: `AI Leader ${playerNumber}`,
        color: {
          r: Math.floor(Math.random() * 255),
          g: Math.floor(Math.random() * 255),
          b: Math.floor(Math.random() * 255),
        },
        connectionStatus: 'connected',
        isReady: true,
      };

      try {
        const [aiPlayer] = await this.databaseProvider
          .getDatabase()
          .insert(players)
          .values(aiPlayerData)
          .returning();
        this.logger.info('Added AI player to game', {
          gameId,
          aiPlayerId: aiPlayer.id,
          nation: aiNation,
        });

        // Broadcast AI player addition
        this.onBroadcast?.(gameId, 'player-joined', {
          playerId: aiPlayer.id,
          playerNumber,
          civilization: aiNation,
          isAI: true,
          playerCount: currentPlayerCount + i + 1,
        });
      } catch (error) {
        this.logger.error('Failed to add AI player:', error);
      }
    }
  }

  /**
   * Get player-to-game mapping
   */
  getPlayerToGame(): Map<string, string> {
    return this.playerToGame;
  }

  /**
   * Set player-to-game mapping (for recovery scenarios)
   */
  setPlayerToGame(playerId: string, gameId: string): void {
    this.playerToGame.set(playerId, gameId);
  }

  /**
   * Remove player from tracking
   */
  removePlayer(playerId: string): void {
    this.playerToGame.delete(playerId);
  }

  /**
   * Validate and select nation for player
   * @reference Original GameManager.ts:169-201 nation validation logic
   */
  private async validateAndSelectNation(
    civilization: string | undefined,
    existingPlayers: any[]
  ): Promise<string> {
    // Validate nation is not already taken (reference: freeciv/server/plrhand.c:2129)
    if (civilization && civilization !== 'random') {
      const existingPlayerWithNation = existingPlayers.find(p => p.civilization === civilization);
      if (existingPlayerWithNation) {
        throw new Error('That nation is already in use.');
      }
      return civilization;
    }

    // Handle random nation selection
    let selectedNation = civilization || 'american';
    if (civilization === 'random') {
      try {
        const loader = RulesetLoader.getInstance();
        const nationsRuleset = loader.loadNationsRuleset('classic');

        if (nationsRuleset) {
          // Get playable nations (exclude barbarian and already taken nations)
          const takenNations = new Set(existingPlayers.map(p => p.civilization));
          const playableNations = Object.values(nationsRuleset.nations)
            .filter(nation => nation.id !== 'barbarian' && !takenNations.has(nation.id))
            .map(nation => nation.id);

          // Randomly select from available nations
          if (playableNations.length > 0) {
            const randomIndex = Math.floor(Math.random() * playableNations.length);
            selectedNation = playableNations[randomIndex];
          }
        }
      } catch (error) {
        this.logger.warn('Failed to load nations for random selection, using default', error);
        selectedNation = 'american';
      }
    }

    return selectedNation;
  }

  /**
   * Get available nations for AI players
   */
  private async getAvailableNations(existingPlayers: any[]): Promise<string[]> {
    try {
      const loader = RulesetLoader.getInstance();
      const nationsRuleset = loader.loadNationsRuleset('classic');

      if (!nationsRuleset) {
        // Fallback nations if ruleset loading fails
        return ['american', 'roman', 'german', 'japanese', 'russian', 'british'];
      }

      // Get playable nations (exclude barbarian and already taken nations)
      const takenNations = new Set(existingPlayers.map(p => p.civilization));
      const availableNations = Object.values(nationsRuleset.nations)
        .filter(nation => nation.id !== 'barbarian' && !takenNations.has(nation.id))
        .map(nation => nation.id);

      return availableNations;
    } catch (error) {
      this.logger.warn('Failed to load available nations, using fallback list', error);
      // Fallback nations
      const fallbackNations = ['american', 'roman', 'german', 'japanese', 'russian', 'british'];
      const takenNations = new Set(existingPlayers.map(p => p.civilization));
      return fallbackNations.filter(nation => !takenNations.has(nation));
    }
  }

  /**
   * Handle auto-start logic after player joins
   * @reference Original GameManager.ts:237-282 auto-start logic
   */
  private async handleAutoStart(gameId: string): Promise<void> {
    // Get updated game state
    const updatedGame = await this.databaseProvider.getDatabase().query.games.findFirst({
      where: eq(games.id, gameId),
      with: { players: true },
    });

    this.logger.debug('Checking auto-start conditions', {
      gameId,
      gameExists: !!updatedGame,
      gameStatus: updatedGame?.status,
      playerCount: updatedGame?.players.length,
    });

    // Auto-start logic: immediately start single-player games, or start multiplayer when enough players join
    if (updatedGame && updatedGame.status === 'waiting') {
      const shouldAutoStart =
        updatedGame.gameType === 'single' || // Always start single-player games
        updatedGame.players.length >= serverConfig.game.minPlayersToStart; // Start multiplayer when enough players

      if (shouldAutoStart) {
        this.logger.info('Auto-starting game', {
          gameId,
          gameType: updatedGame.gameType,
          playerCount: updatedGame.players.length,
        });
        try {
          // Small delay to ensure socket room joins are complete
          await new Promise(resolve => setTimeout(resolve, 200));

          // Add AI player if needed to meet minimum requirements
          await this.ensureMinimumPlayers(gameId);

          // Trigger auto-start through callback
          await this.onAutoStartGame?.(gameId, updatedGame.hostId);
        } catch (error) {
          this.logger.error('Failed to auto-start game:', error);
        }
      } else {
        this.logger.debug('Auto-start conditions not met', {
          gameId,
          gameType: updatedGame.gameType,
          hasGame: !!updatedGame,
          status: updatedGame?.status,
          playerCount: updatedGame?.players.length,
        });
      }
    }
  }
}

import { logger } from '../../utils/logger';
import { PlayerState } from '../GameManager';
import { MapData, MapTile, MapStartpos } from './MapTypes';
import { BaseMapGenerationService } from './BaseMapGenerationService';
import { IslandMapService } from './IslandMapService';
import { Position } from './MapValidator';

/**
 * Resource balance validation result
 */
interface ResourceBalanceResult {
  balanced: boolean;
  score: number;
  issues: string[];
}

/**
 * Fair islands generation service with enhanced validation and retry logic
 * Implements freeciv's fair islands algorithm with comprehensive feasibility checks
 * @reference freeciv/server/generator/mapgen.c:3395-3754 fair islands generation
 * @reference freeciv/server/generator/mapgen.c:1316 fair islands validation
 */
export class FairIslandsService extends BaseMapGenerationService {
  private islandMapService: IslandMapService;
  private currentMapData: MapData | null = null;

  constructor(
    width: number,
    height: number,
    seed: string,
    generator: string,
    random: () => number,
    defaultStartPosMode: MapStartpos,
    cleanupTemperatureMapAfterUse: boolean = false,
    temperatureParam: number = 50
  ) {
    super(
      width,
      height,
      seed,
      generator,
      random,
      defaultStartPosMode,
      cleanupTemperatureMapAfterUse,
      temperatureParam
    );

    // Create island map service for actual generation
    this.islandMapService = new IslandMapService(
      width,
      height,
      seed,
      generator,
      random,
      defaultStartPosMode,
      cleanupTemperatureMapAfterUse,
      temperatureParam
    );
  }

  /**
   * Attempt fair islands generation with enhanced validation and retry logic
   * @reference freeciv/server/generator/mapgen.c:3395-3754 fair islands algorithm
   */
  public async generateMap(players: Map<string, PlayerState>): Promise<MapData> {
    const maxAttempts = 3; // Allow multiple attempts with parameter adjustment
    const startTime = Date.now();
    let attempt = 0;

    // @reference freeciv/server/generator/mapgen.c:1316
    // !map_generate_fair_islands() - pre-validation equivalent
    // Use 'ALL' startpos mode for fair islands validation (maps to mapGenerator4)
    if (!this.validateFairIslands(players, MapStartpos.ALL)) {
      logger.info(
        'Enhanced fair islands pre-validation failed (equivalent to early return FALSE)',
        {
          reference: 'freeciv/server/generator/mapgen.c:1316',
        }
      );
      throw new Error('FALLBACK_TO_ISLAND');
    }

    // Enhanced retry logic with adaptive parameters (inspired by freeciv iteration logic)
    while (attempt < maxAttempts) {
      attempt++;

      try {
        logger.info(`Fair islands generation attempt ${attempt}/${maxAttempts}`, {
          players: players.size,
          reference: 'Enhanced retry logic with adaptive parameters',
        });

        // Apply adaptive parameters based on attempt number
        // @reference freeciv/server/generator/mapgen.c:3689-3702 parameter reduction logic
        const parameterAdjustment = this.calculateParameterAdjustment(attempt, maxAttempts);
        const adjustedTerrainPercentages = this.adjustTerrainPercentages(parameterAdjustment);

        // Store original percentages for restoration
        const originalPercentages = { ...this.terrainPercentages };
        this.terrainPercentages = adjustedTerrainPercentages;

        // @reference freeciv/server/generator/mapgen.c:3523-3753
        // Fair islands algorithm attempts with iteration limits
        // Use 'ALL' startpos mode for fair islands (equivalent to mapgenerator4)
        const generationTimeout = 30000 + (attempt - 1) * 10000; // Increase timeout for later attempts
        const generationPromise = this.islandMapService.generateMap(players, MapStartpos.ALL);
        const timeoutPromise = new Promise<MapData>((_, reject) => {
          setTimeout(() => reject(new Error('Fair islands generation timeout')), generationTimeout);
        });

        const mapData = await Promise.race([generationPromise, timeoutPromise]);
        this.currentMapData = mapData;

        // Restore original percentages
        this.terrainPercentages = originalPercentages;

        // Enhanced post-generation validation equivalent to freeciv's done check
        if (!this.validateGeneratedFairMap(players)) {
          logger.warn(
            `Fair islands attempt ${attempt} failed post-validation (equivalent to !done)`,
            {
              attempt,
              maxAttempts,
              parameterAdjustment,
              reference: 'freeciv/server/generator/mapgen.c:3699-3703',
            }
          );

          if (attempt < maxAttempts) {
            logger.info('Retrying fair islands generation with adjusted parameters');
            continue; // Try again with different parameters
          }
          throw new Error('FALLBACK_TO_ISLAND');
        }

        const generationTime = Date.now() - startTime;
        logger.info(
          `Enhanced fair islands generation succeeded on attempt ${attempt} (equivalent to return TRUE)`,
          {
            attempt,
            generationTime,
            parameterAdjustment,
            reference: 'freeciv/server/generator/mapgen.c:3754',
          }
        );

        return mapData;
      } catch (error) {
        // Restore original percentages on error
        const originalPercentages = { ...this.terrainPercentages };
        this.terrainPercentages = originalPercentages;

        if (error instanceof Error && error.message === 'FALLBACK_TO_ISLAND') {
          throw error; // Re-throw fallback errors
        }

        logger.warn(`Fair islands attempt ${attempt} failed with error`, {
          attempt,
          maxAttempts,
          error: error instanceof Error ? error.message : error,
          reference: 'freeciv/server/generator/mapgen.c:3699-3703',
        });

        if (attempt < maxAttempts) {
          logger.info('Retrying fair islands generation after error');
          continue; // Try again
        }
      }
    }

    const totalTime = Date.now() - startTime;
    logger.warn(
      `Fair islands generation failed after ${maxAttempts} attempts (equivalent to return FALSE)`,
      {
        attempts: maxAttempts,
        totalTime,
        reference: 'Enhanced retry logic based on freeciv iteration pattern',
      }
    );

    throw new Error('FALLBACK_TO_ISLAND');
  }

  /**
   * Validate fair islands feasibility before generation attempt
   * @reference freeciv/server/generator/mapgen.c:3395-3509 fair islands validation
   */
  private validateFairIslands(
    players: Map<string, PlayerState>,
    startPosMode: MapStartpos = MapStartpos.ALL
  ): boolean {
    const playerCount = players.size;

    // @reference freeciv/server/generator/mapgen.c:3395
    // int min_island_size = wld.map.server.tinyisles ? 1 : 2;
    const minIslandSize = 2; // We don't support tinyisles setting yet

    // @reference freeciv/server/generator/mapgen.c:3396-3397
    // int players_per_island = 1;
    let playersPerIsland = 1;

    // @reference freeciv/server/generator/mapgen.c:3398
    // int i, iter = CLIP(1, 100000 / map_num_tiles(), 10);
    const mapNumTiles = this.width * this.height;
    const maxIterations = Math.max(1, Math.min(Math.floor(100000 / mapNumTiles), 10));

    // @reference freeciv/server/generator/mapgen.c:3419-3444
    // Calculate players_per_island based on startpos mode (freeciv MAPSTARTPOS logic)
    switch (startPosMode) {
      case MapStartpos.TWO_ON_THREE: {
        // MAPSTARTPOS_2or3: Prefer 2-3 players per island
        const maybe2 = playerCount % 2 === 0;
        const maybe3 = playerCount % 3 === 0;
        if (maybe3) {
          playersPerIsland = 3;
        } else if (maybe2) {
          playersPerIsland = 2;
        }
        // else playersPerIsland remains 1
        break;
      }
      case MapStartpos.ALL:
        // MAPSTARTPOS_ALL: Flexible island distribution, prefer larger groups
        if (playerCount >= 6 && playerCount % 3 === 0) {
          playersPerIsland = 3;
        } else if (playerCount >= 4 && playerCount % 2 === 0) {
          playersPerIsland = 2;
        }
        // else playersPerIsland remains 1
        break;
      case MapStartpos.VARIABLE:
        // MAPSTARTPOS_VARIABLE: Variable island sizes, prefer single players with some larger islands
        playersPerIsland = 1; // Primarily single-player islands
        break;
      case MapStartpos.DEFAULT:
      case MapStartpos.SINGLE:
        // MAPSTARTPOS_DEFAULT/SINGLE: One player per island
        playersPerIsland = 1;
        break;
      default:
        playersPerIsland = 1;
    }

    // @reference freeciv/server/generator/mapgen.c:3492-3497
    // Calculate playermass using freeciv's exact formula
    const landPercent = 30; // Default landpercent setting
    const polarTiles = 0; // 'i' in freeciv - polar tiles, simplified to 0 for now
    const playermass = Math.floor((mapNumTiles * landPercent - polarTiles) / (playerCount * 100));

    // @reference freeciv/server/generator/mapgen.c:3498-3501
    // islandmass1 = (players_per_island * playermass * 7) / 10;
    // if (islandmass1 < min_island_size) { islandmass1 = min_island_size; }
    let islandmass1 = Math.floor((playersPerIsland * playermass * 7) / 10);
    if (islandmass1 < minIslandSize) {
      islandmass1 = minIslandSize;
    }

    // Enhanced feasibility checks with freeciv-compliant logic
    // @reference freeciv/server/generator/mapgen.c:3492-3509
    const islandmass2 = Math.floor((playermass * 2) / 10);
    const islandmass3 = Math.floor(playermass / 10);
    const finalIslandmass2 = islandmass2 < minIslandSize ? minIslandSize : islandmass2;
    const finalIslandmass3 = islandmass3 < minIslandSize ? minIslandSize : islandmass3;

    // Basic feasibility check - if we can't create minimum viable islands, fail
    if (playermass <= 0 || islandmass1 <= minIslandSize) {
      logger.warn('Fair islands validation failed: insufficient landmass', {
        playerCount,
        playermass,
        islandmass1,
        islandmass2: finalIslandmass2,
        islandmass3: finalIslandmass3,
        minIslandSize,
        mapNumTiles,
        landPercent,
        reference: 'freeciv/server/generator/mapgen.c:3492-3501',
      });
      return false;
    }

    // Enhanced validation: check if total required land mass is feasible
    const totalRequiredLand =
      islandmass1 * Math.ceil(playerCount / playersPerIsland) +
      finalIslandmass2 * 2 +
      finalIslandmass3 * 3;
    const totalAvailableLand = Math.floor((mapNumTiles * landPercent) / 100);

    if (totalRequiredLand > totalAvailableLand * 1.2) {
      // Allow 20% overhead for generation variance
      logger.warn('Fair islands validation failed: total required landmass exceeds available', {
        totalRequiredLand,
        totalAvailableLand,
        overhead: Math.round((totalRequiredLand / totalAvailableLand) * 100) + '%',
        playerCount,
        playersPerIsland,
        reference: 'Enhanced landmass feasibility check',
      });
      return false;
    }

    logger.debug('Fair islands pre-validation passed', {
      playerCount,
      playersPerIsland,
      playermass,
      islandmass1,
      islandmass2: finalIslandmass2,
      islandmass3: finalIslandmass3,
      totalRequiredLand,
      totalAvailableLand,
      mapNumTiles,
      maxIterations,
      reference: 'freeciv/server/generator/mapgen.c:3395-3509',
    });

    return true;
  }

  /**
   * Validate generated fair map quality
   * @reference freeciv/server/generator/mapgen.c post-generation validation
   */
  private validateGeneratedFairMap(players: Map<string, PlayerState>): boolean {
    if (!this.currentMapData) {
      logger.warn('No map data available for post-generation validation');
      return false;
    }

    const { tiles, startingPositions } = this.currentMapData;
    const playerCount = players.size;

    // Basic validation: ensure we have starting positions for all players
    if (!startingPositions || startingPositions.length < playerCount) {
      logger.warn('Insufficient starting positions for fair islands validation', {
        required: playerCount,
        generated: startingPositions?.length || 0,
        reference: 'Post-generation validation requirement',
      });
      return false;
    }

    // Enhanced validation: Island size distribution analysis
    const islandSizes = this.analyzeIslandSizes(tiles);
    const sortedIslandSizes = islandSizes.sort((a, b) => b - a);

    if (sortedIslandSizes.length === 0) {
      logger.warn('No islands found in generated map', {
        reference: 'Post-generation island analysis',
      });
      return false;
    }

    // Validate that we have sufficient major islands for players
    const majorIslands = sortedIslandSizes.filter(size => size >= 20); // Minimum viable island size
    const expectedMajorIslands = Math.ceil(playerCount / this.getPlayersPerIslandForValidation());

    if (majorIslands.length < expectedMajorIslands) {
      logger.warn('Insufficient major islands for fair distribution', {
        majorIslands: majorIslands.length,
        expectedMajorIslands,
        playerCount,
        reference: 'Post-generation major island count validation',
      });
      return false;
    }

    // Enhanced validation: Starting position distance validation
    const positionDistances = this.calculateStartingPositionDistances(startingPositions);
    if (positionDistances.length > 0) {
      const minDistance = Math.min(...positionDistances);
      const avgDistance = positionDistances.reduce((a, b) => a + b, 0) / positionDistances.length;

      // Minimum distance should be reasonable to prevent unfair clustering
      const minMapDimension = Math.min(this.width, this.height);
      const expectedMinDistance = minMapDimension / (playerCount * 0.8); // Allow some clustering

      if (minDistance < expectedMinDistance) {
        logger.warn('Starting positions too close together for fair play', {
          minDistance: Math.round(minDistance),
          expectedMinDistance: Math.round(expectedMinDistance),
          avgDistance: Math.round(avgDistance),
          reference: 'Post-generation starting position distance validation',
        });
        return false;
      }
    }

    // Enhanced validation: Resource balance verification
    const resourceBalance = this.validateResourceBalance(tiles, startingPositions);
    if (!resourceBalance.balanced) {
      logger.warn('Resource distribution imbalance detected', {
        issues: resourceBalance.issues,
        reference: 'Post-generation resource balance validation',
      });
      return false;
    }

    logger.debug('Enhanced fair islands post-generation validation passed', {
      playerCount,
      startingPositions: startingPositions.length,
      majorIslands: majorIslands.length,
      islandSizes: sortedIslandSizes.slice(0, 5), // Top 5 islands
      minDistance: positionDistances.length > 0 ? Math.round(Math.min(...positionDistances)) : 0,
      avgDistance:
        positionDistances.length > 0
          ? Math.round(positionDistances.reduce((a, b) => a + b, 0) / positionDistances.length)
          : 0,
      resourceBalance: resourceBalance.score,
      reference: 'Enhanced post-generation validation',
    });

    return true;
  }

  /**
   * Get players per island for validation calculations
   */
  private getPlayersPerIslandForValidation(): number {
    // Default to single player per island for validation
    // This can be enhanced based on startpos mode if needed
    return 1;
  }

  /**
   * Analyze island sizes in the generated map
   */
  private analyzeIslandSizes(tiles: MapTile[][]): number[] {
    const continentSizes = new Map<number, number>();

    // Count tiles per continent
    for (let x = 0; x < this.width; x++) {
      for (let y = 0; y < this.height; y++) {
        const tile = tiles[x][y];
        if (tile.terrain !== 'ocean' && tile.continentId > 0) {
          const currentSize = continentSizes.get(tile.continentId) || 0;
          continentSizes.set(tile.continentId, currentSize + 1);
        }
      }
    }

    return Array.from(continentSizes.values());
  }

  /**
   * Calculate distances between all starting positions
   */
  private calculateStartingPositionDistances(positions: Position[]): number[] {
    const distances: number[] = [];

    for (let i = 0; i < positions.length; i++) {
      for (let j = i + 1; j < positions.length; j++) {
        const pos1 = positions[i];
        const pos2 = positions[j];
        const distance = Math.sqrt(Math.pow(pos1.x - pos2.x, 2) + Math.pow(pos1.y - pos2.y, 2));
        distances.push(distance);
      }
    }

    return distances;
  }

  /**
   * Validate resource balance across starting positions
   */
  private validateResourceBalance(
    tiles: MapTile[][],
    startingPositions: Position[]
  ): ResourceBalanceResult {
    const result: ResourceBalanceResult = {
      balanced: true,
      score: 0,
      issues: [],
    };

    // Simple resource balance check - ensure each starting position has nearby resources
    const resourceCounts: number[] = [];

    for (const position of startingPositions) {
      const nearbyResources = this.countNearbyResources(tiles, position);
      resourceCounts.push(nearbyResources);
    }

    // Calculate balance metrics
    if (resourceCounts.length > 0) {
      const minResources = Math.min(...resourceCounts);
      const maxResources = Math.max(...resourceCounts);

      // Balance ratio - should be reasonably close
      const balanceRatio = minResources / Math.max(maxResources, 1);
      result.score = Math.round(balanceRatio * 100);

      // Consider balanced if min is at least 60% of max
      if (balanceRatio < 0.6) {
        result.balanced = false;
        result.issues.push(
          `Resource imbalance: min=${minResources}, max=${maxResources}, ratio=${Math.round(
            balanceRatio * 100
          )}%`
        );
      }

      // Check for positions with very few resources
      if (minResources < 2) {
        result.balanced = false;
        result.issues.push(
          `Some starting positions have insufficient resources: min=${minResources}`
        );
      }
    } else {
      result.balanced = false;
      result.issues.push('No starting positions to validate');
    }

    return result;
  }

  /**
   * Count nearby resources around a starting position
   */
  private countNearbyResources(tiles: MapTile[][], position: Position): number {
    let nearbyResources = 0;
    const searchRadius = 3;

    // Check tiles within search radius for resources
    for (let dx = -searchRadius; dx <= searchRadius; dx++) {
      for (let dy = -searchRadius; dy <= searchRadius; dy++) {
        const resourceFound = this.checkTileForResource(tiles, position, dx, dy);
        if (resourceFound) {
          nearbyResources++;
        }
      }
    }

    return nearbyResources;
  }

  /**
   * Check if a tile at offset from position has a resource
   */
  private checkTileForResource(
    tiles: MapTile[][],
    position: Position,
    dx: number,
    dy: number
  ): boolean {
    const x = position.x + dx;
    const y = position.y + dy;

    if (x < 0 || x >= this.width || y < 0 || y >= this.height) {
      return false;
    }

    const tile = tiles[x][y];
    return Boolean(tile.resource) && tile.resource !== ('none' as any);
  }

  /**
   * Override to return current map tiles for land percentage calculation
   */
  protected getMapTiles(): MapTile[][] | null {
    return this.currentMapData?.tiles || null;
  }
}

/**
 * Phase 4: Fractal Height Generation System
 * Advanced height map generation using diamond-square algorithm and fracture maps
 * @reference freeciv/server/generator/height_map.c and fracture_map.c
 */

// Height map constants from freeciv reference
const HMAP_MAX_LEVEL = 1000; // Maximum height value (freeciv: hmap_max_level)
// Shore level is now calculated dynamically based on land percentage
const DEFAULT_STEEPNESS = 30; // Terrain steepness parameter 0-100 (freeciv: wld.map.server.steepness)
const DEFAULT_FLATPOLES = 100; // Pole flattening parameter 0-100 (freeciv: wld.map.server.flatpoles)

/**
 * Climate constants ported from freeciv reference
 * @reference freeciv/server/generator/temperature_map.h and mapgen_topology.h
 */
const MAX_COLATITUDE = 1000; // Normalized maximum colatitude (freeciv: MAP_MAX_LATITUDE)
const ICE_BASE_LEVEL = 200; // Base level for polar ice formation (freeciv: ice_base_colatitude)

// Constants for height generation

/**
 * Advanced height map generator using fractal algorithms
 * Ported from freeciv's height_map.c and fracture_map.c
 */
export class FractalHeightGenerator {
  private width: number;
  private height: number;
  private heightMap: number[];
  private random: () => number;
  private generator: string;
  private shoreLevel: number;
  private mountainLevel: number;
  private readonly steepness: number; // Used for mountain level calculation
  private flatpoles: number;

  constructor(
    width: number,
    height: number,
    random: () => number,
    steepness: number = DEFAULT_STEEPNESS,
    flatpoles: number = DEFAULT_FLATPOLES,
    generator: string = 'random'
  ) {
    this.width = width;
    this.height = height;
    this.heightMap = new Array(width * height).fill(0);
    this.random = random;
    this.generator = generator;
    this.steepness = steepness;
    this.flatpoles = flatpoles;

    // Calculate shore level based on land percentage (like freeciv make_land())
    const landPercent = 30; // MAP_DEFAULT_LANDMASS from freeciv reference
    this.shoreLevel = Math.floor((HMAP_MAX_LEVEL * (100 - landPercent)) / 100);

    // Calculate mountain level based on steepness parameter
    // Higher steepness = more mountains (lower mountain threshold)
    this.mountainLevel = Math.floor(
      ((HMAP_MAX_LEVEL - this.shoreLevel) * (100 - this.steepness)) / 100 + this.shoreLevel
    );
  }

  /**
   * Get height value at coordinates with bounds checking
   */
  private getHeight(x: number, y: number): number {
    if (x < 0 || x >= this.width || y < 0 || y >= this.height) {
      return 0;
    }
    return this.heightMap[y * this.width + x];
  }

  /**
   * Set height value at coordinates with bounds checking
   */
  private setHeight(x: number, y: number, value: number): void {
    if (x >= 0 && x < this.width && y >= 0 && y < this.height) {
      this.heightMap[y * this.width + x] = Math.max(0, Math.min(HMAP_MAX_LEVEL, value));
    }
  }

  /**
   * Factor by which to lower height map near poles in normalize_hmap_poles()
   * @reference freeciv/server/generator/height_map.c:35-57
   */
  private getPoleFactor(x: number, y: number): number {
    const colatitude = this.getColatitude(x, y);
    let factor = 1.0;

    if (this.isNearMapEdge(x, y)) {
      // Map edge near pole: clamp to what linear ramp would give us at pole
      // (maybe greater than 0)
      factor = (100 - this.flatpoles) / 100.0;
    } else if (this.flatpoles > 0) {
      // Linear ramp down from 100% at 2.5*ICE_BASE_LEVEL to (100-flatpoles) %
      // at the poles
      factor = 1 - ((1 - colatitude / (2.5 * ICE_BASE_LEVEL)) * this.flatpoles) / 100;
    }

    // A band of low height to try to separate the pole (this function is
    // only assumed to be called <= 2.5*ICE_BASE_LEVEL)
    if (colatitude >= 2 * ICE_BASE_LEVEL) {
      factor = Math.min(factor, 0.1);
    }

    return factor;
  }

  /**
   * Calculate colatitude (distance from equator) for climate effects
   */
  private getColatitude(_x: number, y: number): number {
    const latitudeFactor = Math.abs(y - this.height / 2) / (this.height / 2);
    return latitudeFactor * MAX_COLATITUDE;
  }

  /**
   * Check if coordinates are near map edge
   */
  private isNearMapEdge(x: number, y: number): boolean {
    const edgeDistance = 3;
    return (
      x < edgeDistance ||
      y < edgeDistance ||
      x >= this.width - edgeDistance ||
      y >= this.height - edgeDistance
    );
  }

  /**
   * Diamond-Square algorithm implementation
   * @reference freeciv/server/generator/height_map.c:120-182
   */
  private diamondSquareRecursive(
    step: number,
    xl: number,
    yt: number,
    xr: number,
    yb: number
  ): void {
    // Base case: rectangle too small
    if (yb - yt <= 0 || xr - xl <= 0 || (yb - yt === 1 && xr - xl === 1)) {
      return;
    }

    // Handle map wrapping for edge coordinates
    const x1wrap = xr >= this.width ? 0 : xr;
    const y1wrap = yb >= this.height ? 0 : yb;

    // Get corner values
    const val = [
      [this.getHeight(xl, yt), this.getHeight(xl, y1wrap)],
      [this.getHeight(x1wrap, yt), this.getHeight(x1wrap, y1wrap)],
    ];

    // Calculate midpoint coordinates
    const midX = Math.floor((xl + xr) / 2);
    const midY = Math.floor((yt + yb) / 2);

    // Set midpoints of sides with random variation
    this.setMidpoint(midX, yt, (val[0][0] + val[1][0]) / 2, step);
    this.setMidpoint(midX, y1wrap, (val[0][1] + val[1][1]) / 2, step);
    this.setMidpoint(xl, midY, (val[0][0] + val[0][1]) / 2, step);
    this.setMidpoint(x1wrap, midY, (val[1][0] + val[1][1]) / 2, step);

    // Set center point with random variation
    const centerValue = (val[0][0] + val[0][1] + val[1][0] + val[1][1]) / 4;
    this.setMidpoint(midX, midY, centerValue, step);

    // Recursively process four quadrants with reduced step size
    const newStep = Math.floor((2 * step) / 3);
    this.diamondSquareRecursive(newStep, xl, yt, midX, midY);
    this.diamondSquareRecursive(newStep, xl, midY, midX, yb);
    this.diamondSquareRecursive(newStep, midX, yt, xr, midY);
    this.diamondSquareRecursive(newStep, midX, midY, xr, yb);
  }

  /**
   * Set midpoint value with pole flattening and random variation
   */
  private setMidpoint(x: number, y: number, baseValue: number, step: number): void {
    if (x < 0 || x >= this.width || y < 0 || y >= this.height) {
      return;
    }

    const colatitude = this.getColatitude(x, y);
    const randomVariation = this.random() * step - step / 2;
    let value = baseValue + randomVariation;

    // Apply pole flattening for realistic world geometry
    if (colatitude <= ICE_BASE_LEVEL / 2) {
      value = (value * (100 - this.flatpoles)) / 100;
    } else if (this.isNearMapEdge(x, y) || this.getHeight(x, y) !== 0) {
      // Don't overwrite existing values or map edges
      return;
    }

    this.setHeight(x, y, value);
  }

  /**
   * Generate initial random height map (similar to MAPGEN_RANDOM approach)
   * @reference freeciv/server/generator/height_map.c make_random_hmap()
   */
  public generateRandomHeightMap(playerCount: number = 4): void {
    // Calculate smooth parameter like freeciv: MAX(1, 1 + get_sqsize() - player_count() / 4)
    // get_sqsize()  sqrt(map_area) / 10 in freeciv
    const sqSize = Math.floor(Math.sqrt(this.width * this.height) / 10);
    const smooth = Math.max(1, 1 + sqSize - Math.floor(playerCount / 4));

    // CRITICAL: Initialize each tile with a DIFFERENT random value (like freeciv INITIALIZE_ARRAY)
    // The freeciv macro evaluates fc_rand(1000 * smooth) for EACH array element
    for (let i = 0; i < this.heightMap.length; i++) {
      this.heightMap[i] = Math.floor(this.random() * (1000 * smooth));
    }

    // Apply advanced smoothing passes to create natural terrain variation
    this.applyAdvancedSmoothing(smooth);

    // CRITICAL FIX: Set shore level BEFORE normalization using original height distribution
    this.setShoreLevel();

    // Adjust to proper height range (like freeciv adjust_int_map)
    this.normalizeHeightMap();
  }

  /**
   * Generate fractal height map using proper grid-based approach
   * @reference freeciv/server/generator/height_map.c make_pseudofractal1_hmap()
   */
  public generatePseudoFractalHeightMap(): void {
    // CRITICAL: Initialize to ZEROS first (like freeciv does)
    this.heightMap.fill(0);

    // Create grid of seed points for fractal generation
    const xdiv = 5;
    const ydiv = 5;

    // Set initial seed points in a grid pattern
    for (let x = 0; x < xdiv + 1; x++) {
      for (let y = 0; y < ydiv + 1; y++) {
        const px = Math.floor((x * this.width) / xdiv);
        const py = Math.floor((y * this.height) / ydiv);

        // Create varied elevations for seed points (use step-based range like freeciv)
        const step = this.width + this.height;
        let seedHeight = Math.floor(this.random() * (2 * step)) - step;

        // Avoid edges (reduce land near map edges)
        if (this.isNearMapEdge(px, py)) {
          const landPercent = 30;
          const avoidedge = ((100 - landPercent) * step) / 100 + Math.floor(step / 3);
          seedHeight -= avoidedge;
        }

        this.setHeight(px, py, seedHeight);
      }
    }

    // Apply fractal subdivision to each grid cell
    const step = this.width + this.height; // Use freeciv step calculation
    for (let x = 0; x < xdiv; x++) {
      for (let y = 0; y < ydiv; y++) {
        const x1 = Math.floor((x * this.width) / xdiv);
        const y1 = Math.floor((y * this.height) / ydiv);
        const x2 = Math.floor(((x + 1) * this.width) / xdiv);
        const y2 = Math.floor(((y + 1) * this.height) / ydiv);

        this.diamondSquareRecursive(step, x1, y1, x2, y2);
      }
    }

    // CRITICAL FIX: Add missing shore level setup (like generateRandomHeightMap)
    // Set shore level BEFORE normalization using original height distribution
    this.setShoreLevel();

    // Adjust to proper height range (like freeciv adjust_int_map)
    this.normalizeHeightMap();
  }

  /**
   * Generate height map using different algorithms based on generator type
   * Following freeciv reference implementation choices
   */
  public generateHeightMap(): void {
    // Choose generation algorithm based on generator type
    switch (this.generator) {
      case 'random':
        // MAPGEN_RANDOM approach: fully random heights with smoothing
        this.generateRandomHeightMap();
        break;
      case 'fractal':
        // MAPGEN_FRACTAL approach: pseudofractal with grid-based seeds
        this.generatePseudoFractalHeightMap();
        break;
      case 'island':
      case 'fair':
        // For now, use fractal as fallback - these would need island-specific logic
        this.generatePseudoFractalHeightMap();
        break;
      default:
        // Default to random (freeciv default)
        this.generateRandomHeightMap();
        break;
    }

    // Apply pole normalization (must come after height generation)
    this.normalizeHeightMapPoles();

    // Add final random variation for natural detail
    for (let i = 0; i < this.heightMap.length; i++) {
      const fuzz = Math.floor(this.random() * 8) - 4;
      this.heightMap[i] = Math.max(0, Math.min(HMAP_MAX_LEVEL, this.heightMap[i] + fuzz));
    }

    // Normalize to final height range
    this.normalizeHeightMap();
  }

  /**
   * Lower the land near the map edges and (optionally) the polar region to
   * avoid too much land there.
   * See also renormalize_hmap_poles()
   * @reference freeciv/server/generator/height_map.c:65-75
   */
  public normalizeHeightMapPoles(): void {
    for (let x = 0; x < this.width; x++) {
      for (let y = 0; y < this.height; y++) {
        const colatitude = this.getColatitude(x, y);

        if (colatitude <= 2.5 * ICE_BASE_LEVEL) {
          const currentHeight = this.getHeight(x, y);
          const poleFactor = this.getPoleFactor(x, y);
          this.setHeight(x, y, currentHeight * poleFactor);
        } else if (this.isNearMapEdge(x, y)) {
          // Near map edge but not near pole.
          this.setHeight(x, y, 0);
        }
      }
    }
  }

  /**
   * Invert (most of) the effects of normalize_hmap_poles() so that we have
   * accurate heights for texturing the poles.
   * @reference freeciv/server/generator/height_map.c:81-95
   */
  public renormalizeHeightMapPoles(): void {
    for (let x = 0; x < this.width; x++) {
      for (let y = 0; y < this.height; y++) {
        const currentHeight = this.getHeight(x, y);

        if (currentHeight === 0) {
          // Nothing left to restore.
          continue;
        }

        const colatitude = this.getColatitude(x, y);
        if (colatitude <= 2.5 * ICE_BASE_LEVEL) {
          const poleFactor = this.getPoleFactor(x, y);

          if (poleFactor > 0) {
            // Invert the previously applied function
            this.setHeight(x, y, currentHeight / poleFactor);
          }
        }
      }
    }
  }

  /**
   * Normalize height map to proper elevation range (0-255 for tile.elevation)
   */
  private normalizeHeightMap(): void {
    // Find current min/max heights
    let minHeight = HMAP_MAX_LEVEL;
    let maxHeight = 0;

    for (const height of this.heightMap) {
      minHeight = Math.min(minHeight, height);
      maxHeight = Math.max(maxHeight, height);
    }

    // Normalize to 0-255 range
    const range = maxHeight - minHeight;
    if (range > 0) {
      for (let i = 0; i < this.heightMap.length; i++) {
        this.heightMap[i] = Math.floor(((this.heightMap[i] - minHeight) / range) * 255);
      }
    }
  }

  /**
   * Apply Gaussian smoothing passes like freeciv smooth_int_map
   * @reference freeciv/server/generator/mapgen_utils.c smooth_int_map()
   * @deprecated Use smoothIntMap() for full freeciv parity
   */
  public applySmoothingPasses(passes: number = 2): void {
    // Gaussian weights from freeciv: center=0.37, adjacent=0.19, edge=0.13
    const weights = [0.13, 0.19, 0.37, 0.19, 0.13];

    for (let pass = 0; pass < passes; pass++) {
      // Horizontal pass
      const tempMap = [...this.heightMap];
      this.applyHorizontalSmoothing(tempMap, weights);

      // Vertical pass
      this.heightMap = [...tempMap];
      this.applyVerticalSmoothing(tempMap, weights);
    }
  }

  /**
   * Apply horizontal smoothing pass
   * @param tempMap Temporary map to store results
   * @param weights Gaussian weights array
   */
  private applyHorizontalSmoothing(tempMap: number[], weights: number[]): void {
    for (let y = 0; y < this.height; y++) {
      for (let x = 0; x < this.width; x++) {
        const smoothedValue = this.calculateSmoothedValue(x, y, weights, true);
        if (smoothedValue !== null) {
          tempMap[y * this.width + x] = smoothedValue;
        }
      }
    }
  }

  /**
   * Apply vertical smoothing pass
   * @param tempMap Temporary map to read from
   * @param weights Gaussian weights array
   */
  private applyVerticalSmoothing(tempMap: number[], weights: number[]): void {
    for (let x = 0; x < this.width; x++) {
      for (let y = 0; y < this.height; y++) {
        const smoothedValue = this.calculateSmoothedValueFromMap(x, y, weights, false, tempMap);
        if (smoothedValue !== null) {
          this.heightMap[y * this.width + x] = smoothedValue;
        }
      }
    }
  }

  /**
   * Calculate smoothed value for a position using current heightMap
   * @param x X coordinate
   * @param y Y coordinate
   * @param weights Gaussian weights
   * @param horizontal True for horizontal smoothing, false for vertical
   * @returns Smoothed value or null if no valid neighbors
   */
  private calculateSmoothedValue(
    x: number,
    y: number,
    weights: number[],
    horizontal: boolean
  ): number | null {
    let weightedSum = 0;
    let totalWeight = 0;

    for (let i = -2; i <= 2; i++) {
      const nx = horizontal ? x + i : x;
      const ny = horizontal ? y : y + i;

      if (this.isValidCoordinate(nx, ny)) {
        weightedSum += this.getHeight(nx, ny) * weights[i + 2];
        totalWeight += weights[i + 2];
      }
    }

    return totalWeight > 0 ? Math.floor(weightedSum / totalWeight) : null;
  }

  /**
   * Calculate smoothed value for a position using provided map
   * @param x X coordinate
   * @param y Y coordinate
   * @param weights Gaussian weights
   * @param horizontal True for horizontal smoothing, false for vertical
   * @param sourceMap Source map to read from
   * @returns Smoothed value or null if no valid neighbors
   */
  private calculateSmoothedValueFromMap(
    x: number,
    y: number,
    weights: number[],
    horizontal: boolean,
    sourceMap: number[]
  ): number | null {
    let weightedSum = 0;
    let totalWeight = 0;

    for (let i = -2; i <= 2; i++) {
      const nx = horizontal ? x + i : x;
      const ny = horizontal ? y : y + i;

      if (this.isValidCoordinate(nx, ny)) {
        weightedSum += sourceMap[ny * this.width + nx] * weights[i + 2];
        totalWeight += weights[i + 2];
      }
    }

    return totalWeight > 0 ? Math.floor(weightedSum / totalWeight) : null;
  }

  /**
   * Check if coordinates are within map bounds
   * @param x X coordinate
   * @param y Y coordinate
   * @returns true if coordinates are valid
   */
  private isValidCoordinate(x: number, y: number): boolean {
    return x >= 0 && x < this.width && y >= 0 && y < this.height;
  }

  /**
   * Apply smoothing kernel for smoothIntMap function
   * @param indexMapper Function to map i offset to neighbor index (-1 if out of bounds)
   * @param sourceMap Source data map
   * @param weight Kernel weights
   * @returns Object with numerator and denominator for smoothing calculation
   */
  private applySmoothingKernel(
    indexMapper: (i: number) => number,
    sourceMap: number[],
    weight: number[]
  ): { numerator: number; denominator: number } {
    let numerator = 0;
    let denominator = 0;

    for (let i = -2; i <= 2; i++) {
      const neighborIndex = indexMapper(i);

      if (neighborIndex >= 0) {
        const kernelWeight = weight[i + 2];
        denominator += kernelWeight;
        numerator += kernelWeight * sourceMap[neighborIndex];
      }
    }

    return { numerator, denominator };
  }

  /**
   * Advanced Gaussian smoothing with proper freeciv parity
   * Port of smooth_int_map() with exact algorithmic implementation
   * @reference freeciv/server/generator/mapgen_utils.c:191-232
   */
  public smoothIntMap(
    intMap: number[],
    width: number,
    height: number,
    zeroesAtEdges: boolean = false
  ): void {
    // Gaussian kernel weights from freeciv reference
    const weightStandard = [0.13, 0.19, 0.37, 0.19, 0.13];
    // const weightIsometric = [0.15, 0.21, 0.29, 0.21, 0.15]; // For future isometric support

    // Use standard weights (could be configurable for isometric maps in future)
    const weight = weightStandard;

    // Create temporary map for two-pass algorithm
    const altIntMap = new Array(width * height);

    let axe = true; // true = X axis, false = Y axis
    let targetMap = altIntMap;
    let sourceMap = intMap;

    do {
      for (let y = 0; y < height; y++) {
        for (let x = 0; x < width; x++) {
          const currentIndex = y * width + x;
          let N = 0; // Numerator (weighted sum)
          let D = 0; // Denominator (total weight)

          // Apply 5-point kernel in current axis direction
          const smoothingResult = this.applySmoothingKernel(
            i => {
              if (axe) {
                const nx = x + i;
                return nx >= 0 && nx < width ? y * width + nx : -1;
              } else {
                const ny = y + i;
                return ny >= 0 && ny < height ? ny * width + x : -1;
              }
            },
            sourceMap,
            weight
          );

          N = smoothingResult.numerator;
          D = smoothingResult.denominator;

          // Handle edge conditions
          if (zeroesAtEdges) {
            D = 1; // Normalize by 1 instead of actual weight sum
          }

          targetMap[currentIndex] = D > 0 ? N / D : 0;
        }
      }

      // Switch axis for next pass
      axe = !axe;

      // Swap source and target maps
      const temp = sourceMap;
      sourceMap = targetMap;
      targetMap = temp;
    } while (!axe); // Continue until axe becomes false again (after Y-axis pass)

    // Copy final results back to original map if needed
    if (sourceMap === altIntMap) {
      for (let i = 0; i < intMap.length; i++) {
        intMap[i] = Math.floor(altIntMap[i]);
      }
    }
  }

  /**
   * Histogram equalization for natural value distribution
   * Port of adjust_int_map_filtered() with exact algorithmic implementation
   * @reference freeciv/server/generator/mapgen_utils.c:123-174
   */
  public adjustIntMapFiltered(
    intMap: number[],
    minValue: number,
    maxValue: number,
    filter?: (x: number, y: number) => boolean
  ): void {
    const intMapDelta = maxValue - minValue;
    let minVal = 0;
    let maxVal = 0;
    let total = 0;
    let first = true;

    // Pass 1: Determine minimum and maximum values
    for (let y = 0; y < this.height; y++) {
      for (let x = 0; x < this.width; x++) {
        if (filter && !filter(x, y)) {
          continue; // Skip tiles that don't pass the filter
        }

        const index = y * this.width + x;
        let value = intMap[index];

        // Convert fractional values to integers (freeciv expects integers)
        if (!Number.isInteger(value)) {
          value = Math.floor(value);
          intMap[index] = value;
        }

        if (first) {
          minVal = value;
          maxVal = value;
          first = false;
        } else {
          maxVal = Math.max(maxVal, value);
          minVal = Math.min(minVal, value);
        }
        total++;
      }
    }

    if (total === 0) {
      return; // No tiles to process
    }

    // Special case: if all values are the same, handle directly
    if (minVal === maxVal) {
      for (let y = 0; y < this.height; y++) {
        for (let x = 0; x < this.width; x++) {
          if (filter && !filter(x, y)) {
            continue;
          }
          const index = y * this.width + x;
          intMap[index] = minValue; // Set to minValue for uniform distribution
        }
      }
      return;
    }

    const size = 1 + maxVal - minVal;

    // Prevent invalid array sizes (this shouldn't happen with proper integer inputs)
    if (size < 1) {
      return; // No range to process
    }
    if (size > 1000000) {
      // This indicates fractional inputs that create huge ranges
      // Convert to integers to match freeciv's integer-only processing
      for (let y = 0; y < this.height; y++) {
        for (let x = 0; x < this.width; x++) {
          if (filter && !filter(x, y)) {
            continue;
          }
          const index = y * this.width + x;
          intMap[index] = Math.floor(intMap[index]);
        }
      }
      // Recalculate with integer values
      return this.adjustIntMapFiltered(intMap, minValue, maxValue, filter);
    }

    const frequencies = new Array(size).fill(0);

    // Pass 2: Translate values and build frequency histogram
    for (let y = 0; y < this.height; y++) {
      for (let x = 0; x < this.width; x++) {
        if (filter && !filter(x, y)) {
          continue;
        }

        const index = y * this.width + x;
        intMap[index] -= minVal; // Translate so minimum value is 0
        frequencies[intMap[index]]++;
      }
    }

    // Pass 3: Create cumulative distribution function (linearize function)
    let count = 0;
    for (let i = 0; i < size; i++) {
      count += frequencies[i];
      frequencies[i] = minValue + Math.floor((count * intMapDelta) / total);
    }

    // Pass 4: Apply the linearization function
    for (let y = 0; y < this.height; y++) {
      for (let x = 0; x < this.width; x++) {
        if (filter && !filter(x, y)) {
          continue;
        }

        const index = y * this.width + x;
        intMap[index] = frequencies[intMap[index]];
      }
    }
  }

  /**
   * Apply advanced smoothing to height map using freeciv algorithms
   * Replaces basic smoothing with Gaussian filter and histogram equalization
   * @reference freeciv/server/generator/height_map.c make_random_hmap()
   */
  public applyAdvancedSmoothing(smoothPasses: number = 1): void {
    // Apply Gaussian smoothing passes
    for (let i = 0; i < smoothPasses; i++) {
      this.smoothIntMap(this.heightMap, this.width, this.height, true);
    }

    // Apply histogram equalization for natural distribution
    this.adjustIntMapFiltered(this.heightMap, 0, HMAP_MAX_LEVEL);
  }

  /**
   * Get the generated height map
   */
  public getHeightMap(): number[] {
    return [...this.heightMap];
  }

  /**
   * Set shore level to achieve target land percentage using original height distribution
   * This must be called BEFORE normalizeHeightMap() to work with the original scale
   * @reference freeciv/server/generator/mapgen.c adjust_hmap_landmass()
   */
  private setShoreLevel(): void {
    const targetLandPercent = 30; // MAP_DEFAULT_LANDMASS
    const sortedHeights = [...this.heightMap].sort((a, b) => b - a); // Sort descending

    // Find the height that gives us the closest to 30% land
    const targetLandTiles = Math.floor((this.heightMap.length * targetLandPercent) / 100);

    if (targetLandTiles > 0 && targetLandTiles < sortedHeights.length) {
      // Set shore level so that the top targetLandPercent of tiles become land
      // Use the actual height value from the original distribution
      const targetHeight = sortedHeights[targetLandTiles - 1];

      // Store in HMAP_MAX_LEVEL scale for consistency
      this.shoreLevel = Math.min(HMAP_MAX_LEVEL - 1, Math.max(0, targetHeight));
    } else {
      // Fallback to default calculation
      this.shoreLevel = Math.floor((HMAP_MAX_LEVEL * (100 - targetLandPercent)) / 100);
    }
  }

  /**
   * Get shore level threshold for water/land classification
   */
  public getShoreLevel(): number {
    return Math.floor((this.shoreLevel / HMAP_MAX_LEVEL) * 255);
  }

  /**
   * Get mountain level threshold for elevation-based terrain
   */
  public getMountainLevel(): number {
    return Math.floor((this.mountainLevel / HMAP_MAX_LEVEL) * 255);
  }
}

