}

/**
 * Check if terrain type is ocean/water
 * @reference freeciv/common/terrain.c is_ocean_terrain()
 * Exact copy of freeciv ocean terrain classification
 */
export function isOceanTerrain(terrain: string): boolean {
  return ['ocean', 'coast', 'deep_ocean'].includes(terrain);
}

/**
 * Check if terrain type is frozen
 * @reference freeciv/common/terrain.c is_frozen_terrain()
 * Exact copy of freeciv frozen terrain classification
 */
export function isFrozenTerrain(_terrain: string): boolean {
  return false; // No frozen terrain types after removing glacier
}

/**
 * Check if terrain is land (non-water)
 * @reference freeciv/common/terrain.c is_land_terrain()
 * Exact copy of freeciv land terrain classification
 */
export function isLandTile(terrain: string): boolean {
  return ![
    'ocean',
    'coast',
    'deep_ocean',
    'lake', // Lake is considered water for this check
  ].includes(terrain);
}

/**
 * Create base tile with default properties
 * @reference freeciv/server/generator/mapgen.c create_base_tile()
 * Creates tile with default ocean terrain and properties
 */
export function createBaseTile(x: number, y: number): MapTile {
  return {
    x,
    y,
    terrain: 'ocean' as TerrainType,
    elevation: 0,
    riverMask: 0,
    continentId: 0,
    isExplored: false,
    isVisible: false,
    hasRoad: false,
    hasRailroad: false,
    improvements: [],
    unitIds: [],
    properties: {},
    temperature: TemperatureType.TEMPERATE,
    wetness: 50,
  };
}

/**
 * Set game-specific properties on terrain tiles
 * @reference freeciv/common/terrain.c terrain_properties()
 * Assigns movement cost and defense bonuses based on terrain type
 */
export function setTerrainGameProperties(tile: MapTile): void {
  // Ensure tile.properties exists
  if (!tile.properties) {
    tile.properties = {};
  }

  // Set terrain properties that other systems might depend on
  // This supplements the ruleset properties with game-specific values needed by RiverGenerator, etc.
  switch (tile.terrain) {
    case 'hills':
    case 'mountains':
      tile.properties.mountainous = tile.terrain === 'mountains' ? 100 : 70;
      break;
    case 'desert':
      tile.properties.dry = 100;
      tile.properties.tropical = 50;
      break;
    case 'swamp':
      tile.properties.wet = 100;
      break;
    case 'forest':
    case 'jungle':
      tile.properties.foliage = 50;
      tile.properties.green = 50;
      break;
    case 'tundra':
      tile.properties.cold = 50;
      tile.properties.frozen = 30;
      break;
    default:
      // For other terrains, ensure basic properties exist but don't override ruleset values
      break;
  }
}

/**
 * Check if coordinates are within map boundaries
 */
function isCoordinateInBounds(x: number, y: number, width: number, height: number): boolean {
  return x >= 0 && x < width && y >= 0 && y < height;
}

/**
 * Calculate averaged value for a single tile using 8-directional neighbors
 */
function calculateSmoothedValue(
  heightMap: number[],
  x: number,
  y: number,
  width: number,
  height: number
): number {
  let sum = 0;
  let count = 0;

  for (let dx = -1; dx <= 1; dx++) {
    for (let dy = -1; dy <= 1; dy++) {
      const nx = x + dx;
      const ny = y + dy;

      if (isCoordinateInBounds(nx, ny, width, height)) {
        const nindex = ny * width + nx;
        sum += heightMap[nindex];
        count++;
      }
    }
  }

  return Math.floor(sum / count);
}

/**
 * Smooth height map values using 8-directional averaging
 * @reference freeciv/server/generator/mapgen_utils.c smooth_int_map()
 * @deprecated Use smoothIntMap() for full freeciv parity
 */
export function smoothHeightMap(heightMap: number[], width: number, height: number): void {
  const smoothed = [...heightMap];

  for (let x = 0; x < width; x++) {
    for (let y = 0; y < height; y++) {
      const index = y * width + x;
      smoothed[index] = calculateSmoothedValue(heightMap, x, y, width, height);
    }
  }

  // Copy smoothed values back
  for (let i = 0; i < heightMap.length; i++) {
    heightMap[i] = smoothed[i];
  }
}

/**
 * Get neighbor index for smoothing kernel in specified axis direction
 */
function getNeighborIndex(
  x: number,
  y: number,
  i: number,
  width: number,
  height: number,
  axe: boolean
): { index: number; inBounds: boolean } {
  if (axe) {
    // X-axis smoothing
    const nx = x + i;
    if (nx >= 0 && nx < width) {
      return { index: y * width + nx, inBounds: true };
    }
  } else {
    // Y-axis smoothing
    const ny = y + i;
    if (ny >= 0 && ny < height) {
      return { index: ny * width + x, inBounds: true };
    }
  }
  return { index: 0, inBounds: false };
}

/**
 * Apply Gaussian kernel smoothing for a single tile
 */
function applyGaussianKernel(
  sourceMap: number[],
  x: number,
  y: number,
  width: number,
  height: number,
  weight: number[],
  axe: boolean,
  zeroesAtEdges: boolean
): number {
  let N = 0; // Numerator (weighted sum)
  let D = 0; // Denominator (total weight)

  // Apply 5-point kernel in current axis direction
  for (let i = -2; i <= 2; i++) {
    const { index: neighborIndex, inBounds } = getNeighborIndex(x, y, i, width, height, axe);

    if (inBounds) {
      const kernelWeight = weight[i + 2];
      D += kernelWeight;
      N += kernelWeight * sourceMap[neighborIndex];
    }
  }

  // Handle edge conditions
  if (zeroesAtEdges) {
    D = 1; // Normalize by 1 instead of actual weight sum
  }

  return D > 0 ? N / D : 0;
}

/**
 * Perform single pass of Gaussian smoothing
 */
function performSmoothingPass(
  sourceMap: number[],
  targetMap: number[],
  width: number,
  height: number,
  weight: number[],
  axe: boolean,
  zeroesAtEdges: boolean
): void {
  for (let y = 0; y < height; y++) {
    for (let x = 0; x < width; x++) {
      const currentIndex = y * width + x;
      targetMap[currentIndex] = applyGaussianKernel(
        sourceMap,
        x,
        y,
        width,
        height,
        weight,
        axe,
        zeroesAtEdges
      );
    }
  }
}

/**
 * Advanced Gaussian smoothing with proper freeciv parity
 * Port of smooth_int_map() with exact algorithmic implementation
 * @reference freeciv/server/generator/mapgen_utils.c:191-232
 */
export function smoothIntMap(
  intMap: number[],
  width: number,
  height: number,
  zeroesAtEdges: boolean = false
): void {
  // Gaussian kernel weights from freeciv reference
  const weightStandard = [0.13, 0.19, 0.37, 0.19, 0.13];
  // const weightIsometric = [0.15, 0.21, 0.29, 0.21, 0.15]; // For future isometric support

  // Use standard weights (could be configurable for isometric maps in future)
  const weight = weightStandard;

  // Create temporary map for two-pass algorithm
  const altIntMap = new Array(width * height);

  let axe = true; // true = X axis, false = Y axis
  let targetMap = altIntMap;
  let sourceMap = intMap;

  do {
    performSmoothingPass(sourceMap, targetMap, width, height, weight, axe, zeroesAtEdges);

    // Switch axis for next pass
    axe = !axe;

    // Swap source and target maps
    const temp = sourceMap;
    sourceMap = targetMap;
    targetMap = temp;
  } while (!axe); // Continue until axe becomes false again (after Y-axis pass)

  // Copy final results back to original map if needed
  if (sourceMap === altIntMap) {
    for (let i = 0; i < intMap.length; i++) {
      intMap[i] = Math.floor(altIntMap[i]);
    }
  }
}

/**
 * Histogram equalization for natural value distribution
 * Port of adjust_int_map_filtered() with exact algorithmic implementation
 * @reference freeciv/server/generator/mapgen_utils.c:123-174
 */
export function adjustIntMapFiltered(
  intMap: number[],
  width: number,
  height: number,
  minValue: number,
  maxValue: number,
  filter?: (x: number, y: number) => boolean
): void {
  const intMapDelta = maxValue - minValue;
  let minVal = 0;
  let maxVal = 0;
  let total = 0;
  let first = true;

  // Pass 1: Determine minimum and maximum values
  for (let y = 0; y < height; y++) {
    for (let x = 0; x < width; x++) {
      if (filter && !filter(x, y)) {
        continue; // Skip tiles that don't pass the filter
      }

      const index = y * width + x;
      let value = intMap[index];

      // Convert fractional values to integers (freeciv expects integers)
      if (!Number.isInteger(value)) {
        value = Math.floor(value);
        intMap[index] = value;
      }

      if (first) {
        minVal = value;
        maxVal = value;
        first = false;
      } else {
        maxVal = Math.max(maxVal, value);
        minVal = Math.min(minVal, value);
      }
      total++;
    }
  }

  if (total === 0) {
    return; // No tiles to process
  }

  const size = 1 + maxVal - minVal;

  // Prevent invalid array sizes (this shouldn't happen with proper integer inputs)
  if (size < 1) {
    return; // No range to process
  }
  if (size > 1000000) {
    // This indicates fractional inputs that create huge ranges
    // Convert to integers to match freeciv's integer-only processing
    for (let y = 0; y < height; y++) {
      for (let x = 0; x < width; x++) {
        if (filter && !filter(x, y)) {
          continue;
        }
        const index = y * width + x;
        intMap[index] = Math.floor(intMap[index]);
      }
    }
    // Recalculate with integer values
    return adjustIntMapFiltered(intMap, width, height, minValue, maxValue, filter);
  }

  // Special case: if all values are the same (size == 1), handle directly
  if (size === 1) {
    // When all values are uniform, freeciv maps them to minValue
    for (let y = 0; y < height; y++) {
      for (let x = 0; x < width; x++) {
        if (filter && !filter(x, y)) {
          continue;
        }
        const index = y * width + x;
        intMap[index] = minValue; // Set to minValue for uniform distribution
      }
    }
    return;
  }

  const frequencies = new Array(size).fill(0);

  // Pass 2: Translate values and build frequency histogram
  for (let y = 0; y < height; y++) {
    for (let x = 0; x < width; x++) {
      if (filter && !filter(x, y)) {
        continue;
      }

      const index = y * width + x;
      intMap[index] -= minVal; // Translate so minimum value is 0
      frequencies[intMap[index]]++;
    }
  }

  // Pass 3: Create cumulative distribution function (linearize function)
  // This exactly matches freeciv's algorithm
  let count = 0;
  for (let i = 0; i < size; i++) {
    count += frequencies[i];
    // Exact freeciv formula: int_map_min + (count * int_map_delta) / total
    frequencies[i] = minValue + Math.floor((count * intMapDelta) / total);
  }

  // Pass 4: Apply the linearization function
  for (let y = 0; y < height; y++) {
    for (let x = 0; x < width; x++) {
      if (filter && !filter(x, y)) {
        continue;
      }

      const index = y * width + x;
      intMap[index] = frequencies[intMap[index]];
    }
  }
}

/**
 * Normalize height map values to specified min/max range
 * @reference freeciv/server/generator/mapgen_utils.c adjust_int_map()
 * Exact copy of freeciv normalization algorithm
 */
export function adjustHeightMap(heightMap: number[], minVal: number, maxVal: number): void {
  const currentMin = Math.min(...heightMap);
  const currentMax = Math.max(...heightMap);

  if (currentMin === currentMax) {
    // Avoid division by zero
    for (let i = 0; i < heightMap.length; i++) {
      heightMap[i] = minVal;
    }
    return;
  }

  const scale = (maxVal - minVal) / (currentMax - currentMin);

  for (let i = 0; i < heightMap.length; i++) {
    heightMap[i] = Math.floor((heightMap[i] - currentMin) * scale + minVal);
  }
}

/**
 * Check if a land tile is part of a tiny island
 * @reference freeciv/server/generator/mapgen.c is_tiny_island()
 * Uses flood fill to count connected land mass size
 */
export function isTinyIsland(
  tiles: MapTile[][],
  x: number,
  y: number,
  width: number,
  height: number,
  random: () => number,
  isRandomMode: boolean = false
): boolean {
  const tile = tiles[x][y];

  // Only check land tiles
  if (!isLandTile(tile.terrain)) {
    return false;
  }

  // Simple heuristic: if isolated from other land within radius 2, it's likely tiny
  const radius = 2;
  let landCount = 0;

  for (let dx = -radius; dx <= radius; dx++) {
    for (let dy = -radius; dy <= radius; dy++) {
      const nx = x + dx;
      const ny = y + dy;

      if (nx >= 0 && nx < width && ny >= 0 && ny < height) {
        const neighborTile = tiles[nx][ny];
        if (isLandTile(neighborTile.terrain)) {
          landCount++;
        }
      }
    }
  }

  // If very few land tiles nearby, it's a tiny island
  // CALIBRATION FIX: Be much less aggressive for Random mode to preserve scattered land
  const threshold = isRandomMode
    ? Math.max(1, Math.floor(random() * 2)) // Random mode: only remove truly isolated single tiles (1-2 tiles)
    : 3 + Math.floor(random() * 3); // Other modes: remove small clusters (3-5 tiles)

  return landCount <= threshold;
}

/**
 * Assign fracture circle using Bresenham algorithm
 * @reference freeciv/server/generator/fracture_map.c assign_fracture_circle()
 * Exact copy of freeciv fracture circle assignment
 */
export function assignFractureCircle(
  continentMap: number[][],
  centerX: number,
  centerY: number,
  radius: number,
  continentId: number,
  width: number,
  height: number,
  landmass: { minX: number; minY: number; maxX: number; maxY: number; elevation: number }
): void {
  if (radius === 0) return;

  let x = 0;
  let y = radius;
  let p = 3 - 2 * radius;

  while (y >= x) {
    // Fill 8 octants of the circle using Bresenham algorithm
    fillFractureArea(continentMap, centerX - x, centerY - y, continentId, landmass, width, height);
    fillFractureArea(continentMap, centerX - y, centerY - x, continentId, landmass, width, height);
    fillFractureArea(continentMap, centerX + y, centerY - x, continentId, landmass, width, height);
    fillFractureArea(continentMap, centerX + x, centerY - y, continentId, landmass, width, height);
    fillFractureArea(continentMap, centerX - x, centerY + y, continentId, landmass, width, height);
    fillFractureArea(continentMap, centerX - y, centerY + x, continentId, landmass, width, height);
    fillFractureArea(continentMap, centerX + y, centerY + x, continentId, landmass, width, height);
    fillFractureArea(continentMap, centerX + x, centerY + y, continentId, landmass, width, height);

    if (p < 0) {
      p += 4 * x++ + 6;
    } else {
      p += 4 * (x++ - y--) + 10;
    }
  }
}

/**
 * Fill area during fracture generation
 * @reference freeciv/server/generator/fracture_map.c fmfill()
 * Exact copy of freeciv fracture fill algorithm
 */
function fillFractureArea(
  continentMap: number[][],
  x: number,
  y: number,
  continentId: number,
  landmass: { minX: number; minY: number; maxX: number; maxY: number; elevation: number },
  width: number,
  height: number
): void {
  if (x >= 0 && x < width && y >= 0 && y < height) {
    if (continentMap[x][y] === 0) {
      continentMap[x][y] = continentId;
      landmass.minX = Math.min(landmass.minX, x);
      landmass.maxX = Math.max(landmass.maxX, x);
      landmass.minY = Math.min(landmass.minY, y);
      landmass.maxY = Math.max(landmass.maxY, y);
    }
  }
}

/**
 * Global island terrain selection state
 * @reference freeciv/server/generator/mapgen.c island_terrain
 */
const islandTerrain: IslandTerrain = {
  init: false,
  forest: [],
  desert: [],
  mountain: [],
  swamp: [],
};

/**
 * Initialize terrain selection lists for island generation
 * @reference freeciv/server/generator/mapgen.c island_terrain_init()
 * Exact port of freeciv's island terrain initialization
 */
export function islandTerrainInit(): void {
  if (islandTerrain.init) {
    return; // Already initialized
  }

  // Forest terrain selections
  islandTerrain.forest = [
    // Tropical forests in tropical/wet areas, avoid dry
    {
      weight: 1,
      target: MapgenTerrainPropertyEnum.FOLIAGE,
      prefer: MapgenTerrainPropertyEnum.TROPICAL,
      avoid: MapgenTerrainPropertyEnum.DRY,
      tempCondition: TemperatureCondition.TT_TROPICAL,
      wetCondition: WetnessCondition.WC_ALL,
    },
    // Temperate forests (highest weight - most common)
    {
      weight: 3,
      target: MapgenTerrainPropertyEnum.FOLIAGE,
      prefer: MapgenTerrainPropertyEnum.TEMPERATE,
      avoid: MapgenTerrainPropertyEnum.UNUSED,
      tempCondition: TemperatureCondition.TT_ALL,
      wetCondition: WetnessCondition.WC_ALL,
    },
    // Wet forests in tropical areas, avoid frozen, need non-dry
    {
      weight: 1,
      target: MapgenTerrainPropertyEnum.FOLIAGE,
      prefer: MapgenTerrainPropertyEnum.WET,
      avoid: MapgenTerrainPropertyEnum.FROZEN,
      tempCondition: TemperatureCondition.TT_TROPICAL,
      wetCondition: WetnessCondition.WC_NDRY,
    },
    // Cold climate forests, avoid frozen areas
    {
      weight: 1,
      target: MapgenTerrainPropertyEnum.FOLIAGE,
      prefer: MapgenTerrainPropertyEnum.COLD,
      avoid: MapgenTerrainPropertyEnum.UNUSED,
      tempCondition: TemperatureCondition.TT_NFROZEN,
      wetCondition: WetnessCondition.WC_ALL,
    },
  ];

  // Desert terrain selections
  islandTerrain.desert = [
    // Hot tropical deserts (highest weight)
    {
      weight: 3,
      target: MapgenTerrainPropertyEnum.DRY,
      prefer: MapgenTerrainPropertyEnum.TROPICAL,
      avoid: MapgenTerrainPropertyEnum.GREEN,
      tempCondition: TemperatureCondition.TT_HOT,
      wetCondition: WetnessCondition.WC_DRY,
    },
    // Temperate deserts
    {
      weight: 2,
      target: MapgenTerrainPropertyEnum.DRY,
      prefer: MapgenTerrainPropertyEnum.TEMPERATE,
      avoid: MapgenTerrainPropertyEnum.GREEN,
      tempCondition: TemperatureCondition.TT_NFROZEN,
      wetCondition: WetnessCondition.WC_DRY,
    },
    // Cold dry areas, avoid tropical, not hot
    {
      weight: 1,
      target: MapgenTerrainPropertyEnum.COLD,
      prefer: MapgenTerrainPropertyEnum.DRY,
      avoid: MapgenTerrainPropertyEnum.TROPICAL,
      tempCondition: TemperatureCondition.TT_NHOT,
      wetCondition: WetnessCondition.WC_DRY,
    },
    // Frozen deserts (tundra)
    {
      weight: 1,
      target: MapgenTerrainPropertyEnum.FROZEN,
      prefer: MapgenTerrainPropertyEnum.DRY,
      avoid: MapgenTerrainPropertyEnum.UNUSED,
      tempCondition: TemperatureCondition.TT_FROZEN,
      wetCondition: WetnessCondition.WC_DRY,
    },
  ];

  // Mountain terrain selections
  islandTerrain.mountain = [
    // Green mountains (hills) - higher weight
    {
      weight: 2,
      target: MapgenTerrainPropertyEnum.MOUNTAINOUS,
      prefer: MapgenTerrainPropertyEnum.GREEN,
      avoid: MapgenTerrainPropertyEnum.UNUSED,
      tempCondition: TemperatureCondition.TT_ALL,
      wetCondition: WetnessCondition.WC_ALL,
    },
    // Mountains without green preference
    {
      weight: 1,
      target: MapgenTerrainPropertyEnum.MOUNTAINOUS,
      prefer: MapgenTerrainPropertyEnum.UNUSED,
      avoid: MapgenTerrainPropertyEnum.GREEN,
      tempCondition: TemperatureCondition.TT_ALL,
      wetCondition: WetnessCondition.WC_ALL,
    },
  ];

  // Swamp terrain selections
  islandTerrain.swamp = [
    // Tropical swamps
    {
      weight: 1,
      target: MapgenTerrainPropertyEnum.WET,
      prefer: MapgenTerrainPropertyEnum.TROPICAL,
      avoid: MapgenTerrainPropertyEnum.FOLIAGE,
      tempCondition: TemperatureCondition.TT_TROPICAL,
      wetCondition: WetnessCondition.WC_NDRY,
    },
    // Temperate swamps in hot areas (highest weight)
    {
      weight: 2,
      target: MapgenTerrainPropertyEnum.WET,
      prefer: MapgenTerrainPropertyEnum.TEMPERATE,
      avoid: MapgenTerrainPropertyEnum.FOLIAGE,
      tempCondition: TemperatureCondition.TT_HOT,
      wetCondition: WetnessCondition.WC_NDRY,
    },
    // Cold swamps, not hot
    {
      weight: 1,
      target: MapgenTerrainPropertyEnum.WET,
      prefer: MapgenTerrainPropertyEnum.COLD,
      avoid: MapgenTerrainPropertyEnum.FOLIAGE,
      tempCondition: TemperatureCondition.TT_NHOT,
      wetCondition: WetnessCondition.WC_NDRY,
    },
  ];

  islandTerrain.init = true;
}

/**
 * Free memory allocated for terrain selection lists
 * @reference freeciv/server/generator/mapgen.c island_terrain_free()
 * Exact port of freeciv's island terrain cleanup
 */
export function islandTerrainFree(): void {
  if (!islandTerrain.init) {
    return;
  }

  // Clear all terrain selection arrays
  islandTerrain.forest = [];
  islandTerrain.desert = [];
  islandTerrain.mountain = [];
  islandTerrain.swamp = [];

  islandTerrain.init = false;
}

/**
 * Get terrain selection list for a specific terrain type
 * @reference freeciv/server/generator/mapgen.c island_terrain access
 * Provides access to initialized terrain selection lists
 */
export function getIslandTerrainSelections(
  terrainType: 'forest' | 'desert' | 'mountain' | 'swamp'
): TerrainSelect[] {
  if (!islandTerrain.init) {
    throw new Error('Island terrain not initialized. Call islandTerrainInit() first.');
  }

  return islandTerrain[terrainType];
}

/**
 * Check if island terrain system is initialized
 */
export function isIslandTerrainInitialized(): boolean {
  return islandTerrain.init;
}

/**
 * Test temperature condition for a tile
 * @reference freeciv/server/generator/temperature_map.h tmap_is()
 * Exact port of freeciv temperature condition checking
 */
export function testTemperatureCondition(tile: MapTile, condition: TemperatureCondition): boolean {
  // tile.temperature is already TemperatureType enum, convert to TemperatureCondition values
  let tileTemp: TemperatureCondition;

  // Convert TemperatureType to TemperatureCondition values for bit masking
  switch (tile.temperature) {
    case TemperatureType.FROZEN:
      tileTemp = TemperatureCondition.TT_FROZEN;
      break;
    case TemperatureType.COLD:
      tileTemp = TemperatureCondition.TT_COLD;
      break;
    case TemperatureType.TEMPERATE:
      tileTemp = TemperatureCondition.TT_TEMPERATE;
      break;
    case TemperatureType.TROPICAL:
      tileTemp = TemperatureCondition.TT_TROPICAL;
      break;
    default:
      tileTemp = TemperatureCondition.TT_TEMPERATE;
      break;
  }

  return (tileTemp & condition) !== 0;
}

/**
 * Test wetness condition for a tile
 * @reference freeciv/server/generator/mapgen.c test_wetness()
 * Exact port of freeciv wetness condition checking
 */
export function testWetnessCondition(tile: MapTile, condition: WetnessCondition): boolean {
  switch (condition) {
    case WetnessCondition.WC_ALL:
      return true;
    case WetnessCondition.WC_DRY:
      // Dry if wetness < 50
      return tile.wetness < 50;
    case WetnessCondition.WC_NDRY:
      // Not dry if wetness >= 50
      return tile.wetness >= 50;
    default:
      return true;
  }
}

/**
 * Select terrain based on climate-specific selection lists
 * @reference freeciv/server/generator/mapgen.c fill_island()
 * Port of freeciv's weighted terrain selection algorithm
 */
export function selectTerrainFromList(
  terrainSelections: TerrainSelect[],
  tile: MapTile,
  random: () => number
): TerrainType | null {
  if (terrainSelections.length === 0) {
    return null;
  }

  // Calculate total weight of valid selections
  let totalWeight = 0;
  const validSelections: { selection: TerrainSelect; cumulativeWeight: number }[] = [];

  for (const selection of terrainSelections) {
    // Check temperature and wetness conditions
    if (!testTemperatureCondition(tile, selection.tempCondition)) {
      continue;
    }
    if (!testWetnessCondition(tile, selection.wetCondition)) {
      continue;
    }

    totalWeight += selection.weight;
    validSelections.push({
      selection,
      cumulativeWeight: totalWeight,
    });
  }

  if (validSelections.length === 0 || totalWeight === 0) {
    return null;
  }

  // Select based on weighted random
  const randomValue = Math.floor(random() * totalWeight);

  for (const { selection, cumulativeWeight } of validSelections) {
    if (randomValue < cumulativeWeight) {
      // Convert mapgen terrain property to actual terrain type
      return mapgenPropertyToTerrain(selection.target);
    }
  }

  // Fallback to first valid selection
  return mapgenPropertyToTerrain(validSelections[0].selection.target);
}

/**
 * Convert mapgen terrain property to actual terrain type
 * @reference freeciv/server/generator/mapgen_utils.c pick_terrain()
 * Maps terrain properties to specific terrain types
 */
function mapgenPropertyToTerrain(property: MapgenTerrainPropertyEnum): TerrainType {
  switch (property) {
    case MapgenTerrainPropertyEnum.FOLIAGE:
      return 'forest';
    case MapgenTerrainPropertyEnum.DRY:
      return 'desert';
    case MapgenTerrainPropertyEnum.MOUNTAINOUS:
      return 'mountains';
    case MapgenTerrainPropertyEnum.WET:
      return 'swamp';
    case MapgenTerrainPropertyEnum.FROZEN:
      return 'tundra';
    case MapgenTerrainPropertyEnum.COLD:
      return 'tundra';
    case MapgenTerrainPropertyEnum.GREEN:
      return 'grassland';
    case MapgenTerrainPropertyEnum.TROPICAL:
      return 'jungle';
    case MapgenTerrainPropertyEnum.TEMPERATE:
      return 'plains';
    default:
      return 'plains';
  }
}

/**
 * Fill island terrain using climate-based selection
 * @reference freeciv/server/generator/mapgen.c fill_island()
 * Port of freeciv's island terrain filling algorithm
 */
export function fillIslandTerrain(
  tiles: MapTile[][],
  terrainType: 'forest' | 'desert' | 'mountain' | 'swamp',
  targetCount: number,
  continentId: number,
  random: () => number
): void {
  if (!islandTerrain.init) {
    throw new Error('Island terrain not initialized. Call islandTerrainInit() first.');
  }

  const width = tiles.length;
  const height = tiles[0].length;
  const terrainSelections = getIslandTerrainSelections(terrainType);
  let placedCount = 0;
  let attempts = 0;
  const maxAttempts = targetCount * 10; // Prevent infinite loops

  while (placedCount < targetCount && attempts < maxAttempts) {
    attempts++;

    // Random tile selection
    const x = Math.floor(random() * width);
    const y = Math.floor(random() * height);
    const tile = tiles[x][y];

    // Only place on appropriate land tiles of the correct continent
    if (tile.continentId !== continentId || !isLandTile(tile.terrain)) {
      continue;
    }

    // Skip if already has specific terrain (don't overwrite)
    if (tile.terrain !== 'plains' && tile.terrain !== 'grassland') {
      continue;
    }

    // Use terrain selection logic
    const selectedTerrain = selectTerrainFromList(terrainSelections, tile, random);
    if (selectedTerrain) {
      tile.terrain = selectedTerrain;
      placedCount++;
    }
  }
}

/**
 * Placement tracking system for terrain generation
 * @reference freeciv/server/generator/mapgen_utils.c placement functions
 * Exact port of freeciv's placement map system for preventing terrain overwrites
 */
export class PlacementMap {
  private placedMap: boolean[][];
  private width: number;
  private height: number;
  private isInitialized: boolean = false;

  constructor(width: number, height: number) {
    this.width = width;
    this.height = height;
    this.placedMap = [];
  }

  /**
   * Create placement map - equivalent to create_placed_map()
   * @reference freeciv/server/generator/mapgen_utils.c:48 create_placed_map()
   * Allocates and initializes the placement map to FALSE
   */
  public createPlacedMap(): void {
    if (this.isInitialized) {
      throw new Error('Placement map already initialized');
    }

    // Initialize 2D array with all false values
    this.placedMap = Array(this.width)
      .fill(null)
      .map(() => Array(this.height).fill(false));

    this.isInitialized = true;
  }

  /**
   * Destroy placement map - equivalent to destroy_placed_map()
   * @reference freeciv/server/generator/mapgen_utils.c:58 destroy_placed_map()
   * Frees the placement map memory
   */
  public destroyPlacedMap(): void {
    if (!this.isInitialized) {
      throw new Error('Placement map not initialized');
    }

    this.placedMap = [];
    this.isInitialized = false;
  }

  /**
   * Check if tile is not yet placed - equivalent to not_placed()
   * @reference freeciv/server/generator/mapgen_utils.c:71 not_placed()
   * Returns true if land has not yet been placed on pmap at (x, y)
   */
  public isPlaced(x: number, y: number): boolean {
    if (!this.isInitialized) {
      throw new Error('Placement map not initialized');
    }

    if (x < 0 || x >= this.width || y < 0 || y >= this.height) {
      return true; // Out of bounds considered as placed
    }

    return this.placedMap[x][y];
  }

  /**
   * Check if tile is not yet placed
   * @reference freeciv/server/generator/mapgen_utils.c:71 not_placed()
   */
  public notPlaced(x: number, y: number): boolean {
    return !this.isPlaced(x, y);
  }

  /**
   * Mark tile terrain as placed - equivalent to map_set_placed()
   * @reference freeciv/server/generator/mapgen_utils.c:79 map_set_placed()
   * Mark tile terrain as placed
   */
  public setPlaced(x: number, y: number): void {
    if (!this.isInitialized) {
      throw new Error('Placement map not initialized');
    }

    if (x >= 0 && x < this.width && y >= 0 && y < this.height) {
      this.placedMap[x][y] = true;
    }
  }

  /**
   * Mark tile terrain as not placed - equivalent to map_unset_placed()
   * @reference freeciv/server/generator/mapgen_utils.c:87 map_unset_placed()
   * Mark tile terrain as not placed
   */
  public unsetPlaced(x: number, y: number): void {
    if (!this.isInitialized) {
      throw new Error('Placement map not initialized');
    }

    if (x >= 0 && x < this.width && y >= 0 && y < this.height) {
      this.placedMap[x][y] = false;
    }
  }

  /**
   * Check if placement map is initialized
   * @reference freeciv/server/generator/mapgen_utils.c:40 placed_map_is_initialized()
   */
  public isPlacedMapInitialized(): boolean {
    return this.isInitialized;
  }

  /**
   * Set all oceanic tiles as placed - equivalent to set_all_ocean_tiles_placed()
   * @reference freeciv/server/generator/mapgen_utils.c:95 set_all_ocean_tiles_placed()
   * Set all oceanics tiles in placed_map
   */
  public setAllOceanTilesPlaced(tiles: MapTile[][]): void {
    if (!this.isInitialized) {
      throw new Error('Placement map not initialized');
    }

    for (let x = 0; x < this.width; x++) {
      for (let y = 0; y < this.height; y++) {
        if (isOceanTerrain(tiles[x][y].terrain)) {
          this.setPlaced(x, y);
        }
      }
    }
  }

  /**
   * Set all nearby tiles as placed - equivalent to set_placed_near_pos()
   * @reference freeciv/server/generator/mapgen_utils.c:107 set_placed_near_pos()
   * Set all nearby tiles as placed on pmap within distance
   */
  public setPlacedNearPos(x: number, y: number, distance: number): void {
    if (!this.isInitialized) {
      throw new Error('Placement map not initialized');
    }

    // Iterate over square area around position (freeciv uses square_iterate)
    for (let dx = -distance; dx <= distance; dx++) {
      for (let dy = -distance; dy <= distance; dy++) {
        const nx = x + dx;
        const ny = y + dy;

        if (nx >= 0 && nx < this.width && ny >= 0 && ny < this.height) {
          this.setPlaced(nx, ny);
        }
      }
    }
  }
}

/**
 * Biome and wetness processing algorithms from freeciv
 * @reference freeciv/server/generator/mapgen.c biome and wetness processing
 * Handles biome-based terrain grouping, natural transitions, and wetness calculations
 */
import { MapTile, TemperatureType, TerrainType } from '../MapTypes';
import { isOceanTerrain, isLandTile, setTerrainGameProperties } from '../TerrainUtils';

/**
 * Handles biome identification, wetness calculation, and biome-based terrain transitions
 * Extracted from TerrainGenerator for better separation of concerns
 * @reference freeciv/server/generator/mapgen.c biome and wetness logic
 */
export class BiomeProcessor {
  private width: number;
  private height: number;
  private random: () => number;

  constructor(width: number, height: number, random: () => number) {
    this.width = width;
    this.height = height;
    this.random = random;
  }

  /**
   * Generate wetness map for terrain variation
   */
  public generateWetnessMap(tiles: MapTile[][]): void {
    // Use default wetness base for better terrain variety
    const baseWetness = 50;

    for (let x = 0; x < this.width; x++) {
      for (let y = 0; y < this.height; y++) {
        // Start with user's wetness setting
        let wetness = baseWetness;

        // Add wetness from nearby water sources
        wetness += this.calculateWetnessFromNearbyWater(tiles, x, y) * 0.3;

        // Store final wetness value
        tiles[x][y].wetness = Math.max(0, Math.min(100, Math.round(wetness)));
      }
    }
  }

  /**
   * Apply biome-based terrain transitions and grouping
   * @reference Task 10: Biome-based terrain grouping and natural transitions
   */
  public applyBiomeTransitions(tiles: MapTile[][]): void {
    const generatorAdjustments = { clusteringStrength: 0.8, transitionSmoothness: 0.6 };
    const newTerrain = tiles.map(col => col.map(tile => ({ ...tile })));

    // Phase 1: Biome-based terrain grouping
    this.applyBiomeBasedGrouping(tiles, newTerrain, generatorAdjustments);

    // Phase 2: Natural terrain transitions
    this.applyNaturalTerrainTransitions(tiles, newTerrain, generatorAdjustments);

    // Phase 3: Regional climate consistency
    this.enforceRegionalClimateConsistency(tiles, newTerrain, generatorAdjustments);

    // Apply terrain changes
    this.applyTerrainChanges(tiles, newTerrain);

    // Apply smoothing for better visual transitions
    this.applyBiomeTransitionSmoothing(tiles);
  }

  /**
   * Apply biome-based terrain grouping
   * @reference Task 10: Biome-based terrain grouping
   */
  private applyBiomeBasedGrouping(
    tiles: MapTile[][],
    newTerrain: MapTile[][],
    generatorAdjustments: any
  ): void {
    const clusteringStrength = generatorAdjustments?.clusteringStrength || 0.8;

    for (let x = 0; x < this.width; x++) {
      for (let y = 0; y < this.height; y++) {
        const tile = tiles[x][y];
        if (isOceanTerrain(tile.terrain)) continue;

        // Identify biome type for this tile
        const biomeType = this.identifyBiomeType(tile);

        // Find similar biome neighbors
        const similarBiomeNeighbors = this.findSimilarBiomeNeighbors(tiles, x, y, biomeType);

        if (similarBiomeNeighbors.length >= 3 && this.random() < 0.15 * clusteringStrength) {
          // Apply biome-based terrain grouping
          const dominantTerrain = this.findDominantTerrainInBiome(similarBiomeNeighbors, biomeType);
          if (dominantTerrain && this.isBiomeCompatible(tile.terrain, dominantTerrain, biomeType)) {
            newTerrain[x][y].terrain = dominantTerrain as TerrainType;
          }
        }
      }
    }
  }

  /**
   * Apply natural terrain transitions based on environmental gradients
   * @reference Task 10: Natural terrain transitions
   */
  private applyNaturalTerrainTransitions(
    tiles: MapTile[][],
    newTerrain: MapTile[][],
    generatorAdjustments: any
  ): void {
    const transitionSmoothness = generatorAdjustments?.transitionSmoothness || 0.6;

    for (let x = 0; x < this.width; x++) {
      for (let y = 0; y < this.height; y++) {
        const tile = tiles[x][y];
        if (isOceanTerrain(tile.terrain)) continue;

        // Calculate environmental gradients
        const elevationGradient = this.calculateElevationGradient(tiles, x, y);
        const temperatureGradient = this.calculateTemperatureGradient(tiles, x, y);
        const wetnessGradient = this.calculateWetnessGradient(tiles, x, y);

        // Apply elevation-based transitions
        if (elevationGradient > 100 && this.random() < 0.2 * transitionSmoothness) {
          const transitionTerrain = this.getElevationTransitionTerrain(tile, elevationGradient);
          if (transitionTerrain) {
            newTerrain[x][y].terrain = transitionTerrain as TerrainType;
          }
        }

        // Apply climate-based transitions
        if (
          (temperatureGradient > 200 || wetnessGradient > 15) &&
          this.random() < 0.25 * transitionSmoothness
        ) {
          const transitionTerrain = this.getClimateTransitionTerrain(
            tile,
            temperatureGradient,
            wetnessGradient
          );
          if (transitionTerrain) {
            newTerrain[x][y].terrain = transitionTerrain as TerrainType;
          }
        }
      }
    }
  }

  /**
   * Enforce regional climate consistency
   * @reference Task 10: Regional climate consistency
   */
  private enforceRegionalClimateConsistency(
    tiles: MapTile[][],
    newTerrain: MapTile[][],
    _generatorAdjustments: any
  ): void {
    const regionSize = 3;
    for (let x = regionSize; x < this.width - regionSize; x += regionSize) {
      for (let y = regionSize; y < this.height - regionSize; y += regionSize) {
        if (this.random() < 0.3) {
          this.enforceRegionalConsistency(tiles, newTerrain, x, y, regionSize);
        }
      }
    }
  }

  /**
   * Identify biome type based on temperature and wetness
   */
  private identifyBiomeType(tile: MapTile): string {
    const temp = tile.temperature as TemperatureType;
    const wetness = tile.wetness || 50;

    if (temp & TemperatureType.TROPICAL) {
      return wetness > 60 ? 'tropical_wet' : 'tropical_dry';
    } else if (temp & TemperatureType.TEMPERATE) {
      return wetness > 50 ? 'temperate_wet' : 'temperate_dry';
    } else if (temp & TemperatureType.COLD) {
      return wetness > 40 ? 'cold_wet' : 'cold_dry';
    } else if (temp & TemperatureType.FROZEN) {
      return 'arctic';
    }

    return 'temperate_dry'; // default
  }

  /**
   * Find neighbors with similar biome type
   */
  private findSimilarBiomeNeighbors(
    tiles: MapTile[][],
    x: number,
    y: number,
    biomeType: string
  ): MapTile[] {
    const neighbors = this.getNeighbors(tiles, x, y);
    return neighbors.filter((neighbor: MapTile) => {
      return isLandTile(neighbor.terrain) && this.identifyBiomeType(neighbor) === biomeType;
    });
  }

  /**
   * Find dominant terrain type in a biome
   */
  private findDominantTerrainInBiome(neighbors: MapTile[], biomeType: string): string | null {
    const terrainCounts: Record<string, number> = {};

    neighbors.forEach(neighbor => {
      if (this.isValidTerrainForBiome(neighbor.terrain, biomeType)) {
        terrainCounts[neighbor.terrain] = (terrainCounts[neighbor.terrain] || 0) + 1;
      }
    });

    let dominantTerrain: string | null = null;
    let maxCount = 0;

    Object.entries(terrainCounts).forEach(([terrain, count]) => {
      if (count > maxCount) {
        maxCount = count;
        dominantTerrain = terrain;
      }
    });

    return maxCount >= 2 ? dominantTerrain : null; // Require at least 2 neighbors
  }

  /**
   * Check if terrain type is valid for a biome
   */
  private isValidTerrainForBiome(terrain: string, biomeType: string): boolean {
    const biomeTerrains: Record<string, string[]> = {
      tropical_wet: ['jungle', 'forest', 'swamp', 'grassland'],
      tropical_dry: ['desert', 'plains', 'grassland'],
      temperate_wet: ['forest', 'grassland', 'plains', 'swamp'],
      temperate_dry: ['plains', 'grassland', 'desert'],
      cold_wet: ['forest', 'tundra', 'swamp'],
      cold_dry: ['tundra', 'plains'],
      arctic: ['tundra', 'glacier'],
    };

    return biomeTerrains[biomeType]?.includes(terrain) || false;
  }

  /**
   * Check if two terrain types are compatible within a biome
   */
  private isBiomeCompatible(terrain1: string, terrain2: string, biomeType: string): boolean {
    return (
      this.isValidTerrainForBiome(terrain1, biomeType) &&
      this.isValidTerrainForBiome(terrain2, biomeType)
    );
  }

  /**
   * Calculate elevation gradient
   */
  private calculateElevationGradient(tiles: MapTile[][], x: number, y: number): number {
    const centerElevation = tiles[x][y].elevation || 0;
    let totalDifference = 0;
    let count = 0;

    for (let dx = -1; dx <= 1; dx++) {
      for (let dy = -1; dy <= 1; dy++) {
        const nx = x + dx;
        const ny = y + dy;
        if (nx >= 0 && nx < this.width && ny >= 0 && ny < this.height && (dx !== 0 || dy !== 0)) {
          const neighborElevation = tiles[nx][ny].elevation || 0;
          totalDifference += Math.abs(centerElevation - neighborElevation);
          count++;
        }
      }
    }

    return count > 0 ? totalDifference / count : 0;
  }

  /**
   * Calculate temperature gradient
   */
  private calculateTemperatureGradient(tiles: MapTile[][], x: number, y: number): number {
    const centerTemp = tiles[x][y].temperature as number;
    let totalDifference = 0;
    let count = 0;

    for (let dx = -1; dx <= 1; dx++) {
      for (let dy = -1; dy <= 1; dy++) {
        const nx = x + dx;
        const ny = y + dy;
        if (nx >= 0 && nx < this.width && ny >= 0 && ny < this.height && (dx !== 0 || dy !== 0)) {
          const neighborTemp = tiles[nx][ny].temperature as number;
          totalDifference += Math.abs(centerTemp - neighborTemp);
          count++;
        }
      }
    }

    return count > 0 ? totalDifference / count : 0;
  }

  /**
   * Calculate wetness gradient
   */
  private calculateWetnessGradient(tiles: MapTile[][], x: number, y: number): number {
    const centerWetness = tiles[x][y].wetness || 0;
    let totalDifference = 0;
    let count = 0;

    for (let dx = -1; dx <= 1; dx++) {
      for (let dy = -1; dy <= 1; dy++) {
        const nx = x + dx;
        const ny = y + dy;
        if (nx >= 0 && nx < this.width && ny >= 0 && ny < this.height && (dx !== 0 || dy !== 0)) {
          const neighborWetness = tiles[nx][ny].wetness || 0;
          totalDifference += Math.abs(centerWetness - neighborWetness);
          count++;
        }
      }
    }

    return count > 0 ? totalDifference / count : 0;
  }

  /**
   * Get elevation-based transition terrain
   */
  private getElevationTransitionTerrain(tile: MapTile, gradient: number): string | null {
    const currentTerrain = tile.terrain;

    if (currentTerrain === 'mountains' && gradient > 150) {
      return 'hills';
    } else if (currentTerrain === 'hills' && gradient > 120) {
      // Transition hills to appropriate lower terrain
      return tile.temperature & TemperatureType.FROZEN ? 'tundra' : 'grassland';
    }

    return null;
  }

  /**
   * Get climate-based transition terrain
   */
  private getClimateTransitionTerrain(tile: MapTile, temp: number, wetness: number): string | null {
    const currentTerrain = tile.terrain;

    // Wetness-based transitions
    if (wetness > 10) {
      if (currentTerrain === 'desert' && tile.wetness > 40) {
        return 'grassland';
      } else if (currentTerrain === 'forest' && tile.wetness < 30) {
        return 'grassland';
      } else if (currentTerrain === 'jungle' && tile.wetness < 50) {
        return 'forest';
      }
    }

    // Temperature-based transitions
    if (temp > 500) {
      if (
        tile.temperature & TemperatureType.FROZEN &&
        ['grassland', 'plains'].includes(currentTerrain)
      ) {
        return 'tundra';
      } else if (temp > 700 && currentTerrain === 'forest') {
        return 'jungle';
      }
    }

    return null;
  }

  /**
   * Enforce regional consistency for terrain
   */
  private enforceRegionalConsistency(
    tiles: MapTile[][],
    newTerrain: MapTile[][],
    centerX: number,
    centerY: number,
    regionSize: number
  ): void {
    // Calculate regional averages
    let avgTemp = 0;
    let avgElevation = 0;
    let avgWetness = 0;
    let count = 0;

    for (let dx = -regionSize; dx <= regionSize; dx++) {
      for (let dy = -regionSize; dy <= regionSize; dy++) {
        const x = centerX + dx;
        const y = centerY + dy;
        if (x >= 0 && x < this.width && y >= 0 && y < this.height) {
          avgTemp += tiles[x][y].temperature as number;
          avgElevation += tiles[x][y].elevation || 0;
          avgWetness += tiles[x][y].wetness || 0;
          count++;
        }
      }
    }

    avgTemp /= count;
    avgElevation /= count;
    avgWetness /= count;
    const dominantBiome = this.identifyBiomeType({
      temperature: avgTemp as TemperatureType,
      elevation: avgElevation,
      wetness: avgWetness,
    } as MapTile);

    // Apply regional consistency
    for (let dx = -regionSize; dx <= regionSize; dx++) {
      for (let dy = -regionSize; dy <= regionSize; dy++) {
        this.applyRegionalConsistencyToTile(
          tiles,
          newTerrain,
          centerX + dx,
          centerY + dy,
          dominantBiome
        );
      }
    }
  }

  /**
   * Apply regional consistency to a single tile
   * @param tiles Original tile array
   * @param newTerrain New terrain array
   * @param x X coordinate
   * @param y Y coordinate
   * @param dominantBiome The dominant biome for the region
   */
  private applyRegionalConsistencyToTile(
    tiles: MapTile[][],
    newTerrain: MapTile[][],
    x: number,
    y: number,
    dominantBiome: string
  ): void {
    if (!this.isValidCoordinate(x, y)) return;

    const tile = tiles[x][y];
    if (this.isValidTerrainForBiome(tile.terrain, dominantBiome) || this.random() >= 0.3) {
      return;
    }

    const suitableTerrains = this.getValidTerrainsForBiome(dominantBiome);
    if (suitableTerrains.length > 0) {
      newTerrain[x][y].terrain = suitableTerrains[
        Math.floor(this.random() * suitableTerrains.length)
      ] as TerrainType;
    }
  }

  /**
   * Check if coordinates are within map bounds
   * @param x X coordinate
   * @param y Y coordinate
   * @returns true if coordinates are valid
   */
  private isValidCoordinate(x: number, y: number): boolean {
    return x >= 0 && x < this.width && y >= 0 && y < this.height;
  }

  /**
   * Apply smoothing logic to a single tile
   * @param tiles Original tile array
   * @param newTerrain New terrain array to modify
   * @param x X coordinate
   * @param y Y coordinate
   * @returns Number of changes applied (0 or 1)
   */
  private applySingleTileSmoothing(
    tiles: MapTile[][],
    newTerrain: MapTile[][],
    x: number,
    y: number
  ): number {
    const tile = tiles[x][y];
    const closeNeighbors = this.getNeighborTerrain(tiles, x, y, 1);
    const neighbors = this.getNeighborTerrain(tiles, x, y, 2);

    // Smooth isolated desert tiles
    if (tile.terrain === 'desert') {
      return this.smoothDesertTile(newTerrain, x, y, closeNeighbors, neighbors);
    }

    // Smooth forest transitions
    if (tile.terrain === 'forest') {
      return this.smoothForestTile(newTerrain, x, y, closeNeighbors);
    }

    // Create forest buffers
    if (['grassland', 'plains'].includes(tile.terrain)) {
      return this.createForestBuffer(newTerrain, x, y, neighbors);
    }

    return 0;
  }

  /**
   * Smooth isolated desert tiles
   */
  private smoothDesertTile(
    newTerrain: MapTile[][],
    x: number,
    y: number,
    closeNeighbors: string[],
    neighbors: string[]
  ): number {
    const desertNeighbors = closeNeighbors.filter(t => t === 'desert').length;
    const forestNeighbors = neighbors.filter(t => t === 'forest').length;

    if (desertNeighbors === 0 && forestNeighbors >= 4) {
      newTerrain[x][y].terrain = 'plains';
      setTerrainGameProperties(newTerrain[x][y]);
      return 1;
    }
    return 0;
  }

  /**
   * Smooth forest transitions
   */
  private smoothForestTile(
    newTerrain: MapTile[][],
    x: number,
    y: number,
    closeNeighbors: string[]
  ): number {
    const forestNeighbors = closeNeighbors.filter(t => t === 'forest').length;
    if (forestNeighbors <= 1) {
      newTerrain[x][y].terrain = 'plains';
      setTerrainGameProperties(newTerrain[x][y]);
      return 1;
    }
    return 0;
  }

  /**
   * Create forest buffers around forest areas
   */
  private createForestBuffer(
    newTerrain: MapTile[][],
    x: number,
    y: number,
    neighbors: string[]
  ): number {
    const forestNeighbors = neighbors.filter(t => t === 'forest').length;
    if (forestNeighbors >= 5 && this.random() < 0.4) {
      newTerrain[x][y].terrain = 'forest';
      setTerrainGameProperties(newTerrain[x][y]);
      return 1;
    }
    return 0;
  }

  /**
   * Get valid terrain types for a biome
   */
  private getValidTerrainsForBiome(biomeType: string): string[] {
    const biomeTerrains: Record<string, string[]> = {
      tropical_wet: ['jungle', 'forest', 'swamp', 'grassland'],
      tropical_dry: ['desert', 'plains', 'grassland'],
      temperate_wet: ['forest', 'grassland', 'plains', 'swamp'],
      temperate_dry: ['plains', 'grassland', 'desert'],
      cold_wet: ['forest', 'tundra', 'swamp'],
      cold_dry: ['tundra', 'plains'],
      arctic: ['tundra', 'glacier'],
    };

    return biomeTerrains[biomeType] || ['grassland'];
  }

  /**
   * Apply terrain changes from new terrain array to original
   */
  private applyTerrainChanges(tiles: MapTile[][], newTerrain: MapTile[][]): void {
    for (let x = 0; x < this.width; x++) {
      for (let y = 0; y < this.height; y++) {
        if (tiles[x][y].terrain !== newTerrain[x][y].terrain) {
          tiles[x][y].terrain = newTerrain[x][y].terrain;
          setTerrainGameProperties(tiles[x][y]);
        }
      }
    }

    // Apply final smoothing pass
    this.applyBiomeTransitionSmoothing(tiles);
  }

  /**
   * Apply biome transition smoothing for better visual transitions
   */
  private applyBiomeTransitionSmoothing(tiles: MapTile[][]): number {
    let changesApplied = 0;
    const maxPasses = 2;

    for (let pass = 0; pass < maxPasses; pass++) {
      const newTerrain = tiles.map(row => [...row]);

      for (let x = 1; x < this.width - 1; x++) {
        for (let y = 1; y < this.height - 1; y++) {
          const tile = tiles[x][y];
          if (isOceanTerrain(tile.terrain)) continue;

          const smoothingResult = this.applySingleTileSmoothing(tiles, newTerrain, x, y);
          changesApplied += smoothingResult;
        }
      }

      // Apply changes for this pass
      for (let x = 0; x < this.width; x++) {
        for (let y = 0; y < this.height; y++) {
          if (tiles[x][y].terrain !== newTerrain[x][y].terrain) {
            tiles[x][y].terrain = newTerrain[x][y].terrain;
          }
        }
      }
    }

    return changesApplied;
  }

  /**
   * Get neighbor terrain types within specified radius
   */
  private getNeighborTerrain(
    tiles: MapTile[][],
    centerX: number,
    centerY: number,
    radius: number
  ): string[] {
    const neighbors: string[] = [];

    for (let dx = -radius; dx <= radius; dx++) {
      for (let dy = -radius; dy <= radius; dy++) {
        if (dx === 0 && dy === 0) continue;

        const x = centerX + dx;
        const y = centerY + dy;

        if (x >= 0 && x < this.width && y >= 0 && y < this.height) {
          neighbors.push(tiles[x][y].terrain);
        }
      }
    }

    return neighbors;
  }

  /**
   * Calculate wetness contribution from nearby water sources
   */
  private calculateWetnessFromNearbyWater(tiles: MapTile[][], x: number, y: number): number {
    let wetnessBonus = 0;
    const searchRadius = 3;

    for (let dx = -searchRadius; dx <= searchRadius; dx++) {
      for (let dy = -searchRadius; dy <= searchRadius; dy++) {
        const nx = x + dx;
        const ny = y + dy;

        if (nx >= 0 && nx < this.width && ny >= 0 && ny < this.height) {
          const tile = tiles[nx][ny];
          const distance = Math.sqrt(dx * dx + dy * dy);

          if (isOceanTerrain(tile.terrain)) {
            // Ocean tiles contribute wetness based on distance
            const contribution = Math.max(0, 30 - distance * 8);
            wetnessBonus += contribution;
          } else if (tile.riverMask > 0) {
            // River tiles also contribute wetness
            const contribution = Math.max(0, 15 - distance * 4);
            wetnessBonus += contribution;
          }
        }
      }
    }

    return Math.min(wetnessBonus, 40); // Cap at 40 bonus wetness
  }

  /**
   * Get neighbor tiles
   */
  private getNeighbors(tiles: MapTile[][], x: number, y: number): MapTile[] {
    const neighbors: MapTile[] = [];

    for (let dx = -1; dx <= 1; dx++) {
      for (let dy = -1; dy <= 1; dy++) {
        if (dx === 0 && dy === 0) continue;

        const nx = x + dx;
        const ny = y + dy;

        if (nx >= 0 && nx < this.width && ny >= 0 && ny < this.height) {
          neighbors.push(tiles[nx][ny]);
        }
      }
    }

    return neighbors;
  }
}

/**
 * Ocean and water processing algorithms from freeciv
 * @reference freeciv/server/generator/mapgen.c ocean and water processing
 * Handles ocean depth smoothing, ocean type selection, and water body identification
 */
import { MapTile, TerrainType } from '../MapTypes';
import { isOceanTerrain, isFrozenTerrain, isLandTile } from '../TerrainUtils';

/**
 * Handles ocean depth processing, water body identification, and ocean terrain selection
 * Extracted from TerrainGenerator for better separation of concerns
 * @reference freeciv/server/generator/mapgen.c ocean processing logic
 */
export class OceanProcessor {
  private width: number;
  private height: number;
  private random: () => number;

  constructor(width: number, height: number, random: () => number) {
    this.width = width;
    this.height = height;
    this.random = random;
  }

  /**
   * Process a single tile for ocean type assignment based on depth
   */
  private processOceanTileByDepth(tile: MapTile): void {
    if (!isOceanTerrain(tile.terrain)) {
      return;
    }

    // Calculate depth based on elevation (lower elevation = deeper)
    const elevation = tile.elevation || 0;
    const depth = Math.max(0, 255 - elevation);

    // Determine if tile should be frozen
    const isFrozen = isFrozenTerrain(tile.terrain);
    const newOceanType = this.pickOcean(depth, isFrozen);

    if (newOceanType && newOceanType !== tile.terrain) {
      tile.terrain = newOceanType as TerrainType;
    }
  }

  /**
   * Process a single tile for ocean type smoothing
   */
  private processOceanTileSmoothing(tiles: MapTile[][], x: number, y: number): void {
    const tile = tiles[x][y];

    if (!isOceanTerrain(tile.terrain)) {
      return;
    }

    const mostCommonAdjacentOcean = this.getMostAdjacentOceanType(tiles, x, y);
    if (!mostCommonAdjacentOcean || mostCommonAdjacentOcean === tile.terrain) {
      return;
    }

    // Apply smoothing with some randomness to avoid uniform patches
    if (this.random() < 0.6) {
      tile.terrain = mostCommonAdjacentOcean as TerrainType;
    }
  }

  /**
   * Process a single tile for distance-based ocean type assignment
   */
  private processOceanTileDistanceBased(tiles: MapTile[][], x: number, y: number): void {
    const tile = tiles[x][y];

    if (!isOceanTerrain(tile.terrain)) {
      return;
    }

    const distanceToCoast = this.calculateDistanceToCoast(tiles, x, y);

    // Deep ocean should be further from coast
    if (distanceToCoast > 3 && tile.terrain === 'coast' && this.random() < 0.4) {
      tile.terrain = 'ocean' as TerrainType;
    } else if (distanceToCoast > 6 && tile.terrain === 'ocean' && this.random() < 0.3) {
      tile.terrain = 'deep_ocean' as TerrainType;
    }
  }

  /**
   * Smooth water depth for realistic ocean depth transitions
   * @reference freeciv/server/generator/mapgen.c ocean depth processing
   */
  public smoothWaterDepth(tiles: MapTile[][]): void {
    const maxPasses = 3;

    for (let pass = 0; pass < maxPasses; pass++) {
      // Pass 1: Set ocean types based on depth
      for (let x = 0; x < this.width; x++) {
        for (let y = 0; y < this.height; y++) {
          this.processOceanTileByDepth(tiles[x][y]);
        }
      }

      // Pass 2: Smooth transitions between adjacent ocean types
      for (let x = 0; x < this.width; x++) {
        for (let y = 0; y < this.height; y++) {
          this.processOceanTileSmoothing(tiles, x, y);
        }
      }

      // Pass 3: Distance-based smoothing from coast
      for (let x = 0; x < this.width; x++) {
        for (let y = 0; y < this.height; y++) {
          this.processOceanTileDistanceBased(tiles, x, y);
        }
      }
    }
  }

  /**
   * Check if a tile has ocean neighbors
   */
  public hasOceanNeighbor(tiles: MapTile[][], x: number, y: number): boolean {
    for (let dx = -1; dx <= 1; dx++) {
      for (let dy = -1; dy <= 1; dy++) {
        if (dx === 0 && dy === 0) continue;

        const nx = x + dx;
        const ny = y + dy;
        if (nx >= 0 && nx < this.width && ny >= 0 && ny < this.height) {
          if (isOceanTerrain(tiles[nx][ny].terrain)) {
            return true;
          }
        }
      }
    }
    return false;
  }

  /**
   * Check if there's land at the given coordinates
   */
  private isCoordinateInBoundsWithLand(tiles: MapTile[][], x: number, y: number): boolean {
    if (x < 0 || x >= this.width || y < 0 || y >= this.height) {
      return false;
    }
    return isLandTile(tiles[x][y].terrain);
  }

  /**
   * Check if land exists at perimeter of search square at given distance
   */
  private findLandAtDistance(
    tiles: MapTile[][],
    centerX: number,
    centerY: number,
    distance: number
  ): boolean {
    for (let dx = -distance; dx <= distance; dx++) {
      for (let dy = -distance; dy <= distance; dy++) {
        // Only check perimeter of search square
        if (Math.abs(dx) !== distance && Math.abs(dy) !== distance) {
          continue;
        }

        const nx = centerX + dx;
        const ny = centerY + dy;

        if (this.isCoordinateInBoundsWithLand(tiles, nx, ny)) {
          return true;
        }
      }
    }
    return false;
  }

  /**
   * Calculate distance to nearest coastal land
   */
  private calculateDistanceToCoast(tiles: MapTile[][], x: number, y: number): number {
    const maxSearchDistance = 10;

    for (let distance = 1; distance <= maxSearchDistance; distance++) {
      if (this.findLandAtDistance(tiles, x, y, distance)) {
        return distance;
      }
    }

    return maxSearchDistance;
  }

  /**
   * Pick appropriate ocean type based on depth and temperature
   * @reference freeciv/server/generator/mapgen.c ocean type selection
   */
  private pickOcean(depth: number, _isFrozen: boolean): string | null {
    // Ocean type selection based on depth
    // Shallow areas become coast, medium becomes ocean, deep becomes deep_ocean
    const oceanTerrains = [
      { type: 'coast', minDepth: 0, maxDepth: 80 },
      { type: 'ocean', minDepth: 60, maxDepth: 180 },
      { type: 'deep_ocean', minDepth: 150, maxDepth: 255 },
    ];

    let bestTerrain: string | null = null;
    let bestScore = -1;

    for (const terrain of oceanTerrains) {
      if (depth >= terrain.minDepth && depth <= terrain.maxDepth) {
        const score =
          Math.random() * 100 +
          (terrain.maxDepth - Math.abs(depth - (terrain.minDepth + terrain.maxDepth) / 2));
        if (score > bestScore) {
          bestScore = score;
          bestTerrain = terrain.type;
        }
      }
    }

    return bestTerrain;
  }

  /**
   * Get the most common adjacent ocean type for smoothing
   * @reference freeciv/server/generator/mapgen.c ocean smoothing
   */
  private getMostAdjacentOceanType(tiles: MapTile[][], x: number, y: number): string | null {
    const oceanTypeCounts: Record<string, number> = {};

    for (let dx = -1; dx <= 1; dx++) {
      for (let dy = -1; dy <= 1; dy++) {
        if (dx === 0 && dy === 0) continue;

        const nx = x + dx;
        const ny = y + dy;
        if (nx >= 0 && nx < this.width && ny >= 0 && ny < this.height) {
          const neighborTerrain = tiles[nx][ny].terrain;
          if (isOceanTerrain(neighborTerrain)) {
            oceanTypeCounts[neighborTerrain] = (oceanTypeCounts[neighborTerrain] || 0) + 1;
          }
        }
      }
    }

    // Find most common ocean type, preferring deeper waters
    const oceanTerrainTypes = ['coast', 'ocean', 'deep_ocean'];
    let mostCommon: string | null = null;
    let maxCount = 0;

    for (const terrainType of oceanTerrainTypes) {
      const count = oceanTypeCounts[terrainType] || 0;
      if (count > maxCount || (count === maxCount && terrainType === 'deep_ocean')) {
        maxCount = count;
        mostCommon = terrainType;
      }
    }

    return maxCount >= 2 ? mostCommon : null; // Require at least 2 neighbors
  }

  /**
   * Identify separate ocean bodies for lake generation
   * @reference freeciv/server/generator/mapgen.c ocean body identification
   */
  public identifyOceanBodies(tiles: MapTile[][]): Array<{ tiles: MapTile[]; id: number }> {
    const visited: boolean[][] = Array(this.width)
      .fill(null)
      .map(() => Array(this.height).fill(false));

    const oceanBodies: Array<{ tiles: MapTile[]; id: number }> = [];
    let oceanBodyId = 1;

    for (let x = 0; x < this.width; x++) {
      for (let y = 0; y < this.height; y++) {
        const tile = tiles[x][y];

        if (!isOceanTerrain(tile.terrain)) continue;
        if (visited[x][y]) continue;

        const oceanTiles: MapTile[] = [];
        this.floodFillOceanBody(tiles, x, y, visited, oceanTiles);

        if (oceanTiles.length > 0) {
          oceanBodies.push({
            tiles: oceanTiles,
            id: oceanBodyId++,
          });
        }
      }
    }

    return oceanBodies;
  }

  /**
   * Flood fill to identify connected ocean tiles
   * @reference freeciv/server/generator/mapgen.c flood fill for ocean bodies
   */
  private floodFillOceanBody(
    tiles: MapTile[][],
    startX: number,
    startY: number,
    visited: boolean[][],
    oceanTiles: MapTile[]
  ): void {
    const stack: Array<{ x: number; y: number }> = [{ x: startX, y: startY }];

    while (stack.length > 0) {
      const { x, y } = stack.pop()!;

      if (x < 0 || x >= this.width || y < 0 || y >= this.height) continue;
      if (visited[x][y]) continue;

      const tile = tiles[x][y];
      if (!isOceanTerrain(tile.terrain)) {
        continue;
      }

      visited[x][y] = true;
      oceanTiles.push(tile);

      // Add neighboring tiles to stack
      stack.push({ x: x - 1, y });
      stack.push({ x: x + 1, y });
      stack.push({ x, y: y - 1 });
      stack.push({ x, y: y + 1 });
    }
  }

  /**
   * Regenerate small ocean bodies as lakes
   * @reference freeciv/server/generator/mapgen.c lake generation
   */
  public regenerateLakes(tiles: MapTile[][]): void {
    const oceanBodies = this.identifyOceanBodies(tiles);

    // Convert small ocean bodies to lakes
    oceanBodies.forEach(oceanBody => {
      // Small ocean bodies (< 20 tiles) become lakes
      if (oceanBody.tiles.length < 20) {
        oceanBody.tiles.forEach(tile => {
          const currentTerrain = tile.terrain;

          // Only convert if it makes sense climatically
          if (!isFrozenTerrain(currentTerrain)) {
            tile.terrain = 'lake' as TerrainType;
          }
        });
      }
    });
  }
}

/**
 * Terrain placement and distribution algorithms from freeciv
 * @reference freeciv/server/generator/mapgen.c make_terrains(), place_terrain(), rand_map_pos_characteristic()
 * Exact copies of freeciv terrain placement algorithms
 */
import { MapTile, TemperatureType, TerrainType } from '../MapTypes';
import { isOceanTerrain, PlacementMap } from '../TerrainUtils';
import { MapgenTerrainPropertyEnum, pickTerrain, getTerrainProperties } from '../TerrainRuleset';

export interface TerrainParams {
  mountain_pct: number;
  forest_pct: number;
  jungle_pct: number;
  desert_pct: number;
  swamp_pct: number;
  river_pct: number;
}

/**
 * Handles terrain placement, distribution, and spatial allocation algorithms
 * Extracted from TerrainGenerator for better separation of concerns
 * @reference freeciv/server/generator/mapgen.c:491-600 terrain placement logic
 */
export class TerrainPlacementProcessor {
  private width: number;
  private height: number;
  private random: () => number;
  private placementMap: PlacementMap;
  private hmapLowLevel: number = 0;

  constructor(width: number, height: number, random: () => number, placementMap: PlacementMap) {
    this.width = width;
    this.height = height;
    this.random = random;
    this.placementMap = placementMap;
  }

  /**
   * Initialize hmap_low_level for mountain conditions
   * @reference freeciv/server/generator/mapgen.c ini_hmap_low_level()
   * Must be called before makeTerrains() with proper shore and max levels
   */
  public initializeHmapLowLevel(
    swampPct: number,
    hmapShoreLevel: number,
    hmapMaxLevel: number
  ): void {
    // @reference freeciv/server/generator/mapgen.c:120-123
    // hmap_low_level = (4 * swamp_pct * (hmap_max_level - hmap_shore_level)) / 100 + hmap_shore_level;
    this.hmapLowLevel = Math.floor(
      (4 * swampPct * (hmapMaxLevel - hmapShoreLevel)) / 100 + hmapShoreLevel
    );
  }

  /**
   * Main terrain placement algorithm - exact copy of freeciv make_terrains() function
   * @reference freeciv/server/generator/mapgen.c:491 make_terrains()
   */
  public makeTerrains(tiles: MapTile[][], terrainParams: TerrainParams): void {
    // Count total unplaced tiles using placement tracking
    // @reference freeciv/server/generator/mapgen.c:491 make_terrains()
    let total = 0;
    for (let x = 0; x < this.width; x++) {
      for (let y = 0; y < this.height; y++) {
        const tile = tiles[x][y];
        // In freeciv: not_placed(ptile) - tiles that aren't ocean and haven't been assigned terrain yet
        if (this.placementMap.notPlaced(x, y) && !isOceanTerrain(tile.terrain)) {
          total++;
        }
      }
    }

    // Calculate terrain counts exactly as freeciv does
    let forests_count = Math.floor(
      (total * terrainParams.forest_pct) / (100 - terrainParams.mountain_pct)
    );
    let jungles_count = Math.floor(
      (total * terrainParams.jungle_pct) / (100 - terrainParams.mountain_pct)
    );
    let deserts_count = Math.floor(
      (total * terrainParams.desert_pct) / (100 - terrainParams.mountain_pct)
    );
    let swamps_count = Math.floor(
      (total * terrainParams.swamp_pct) / (100 - terrainParams.mountain_pct)
    );
    let alt_deserts_count = 0;

    // Grassland, tundra, arctic and plains is counted in plains_count
    let plains_count = total - forests_count - deserts_count - swamps_count - jungles_count;

    // The placement loop - exact copy of freeciv logic
    do {
      // PLACE_ONE_TYPE(forests_count, plains_count, pick_terrain(MG_FOLIAGE, MG_TEMPERATE, MG_TROPICAL), WC_ALL, TT_NFROZEN, MC_NONE, 60);
      if (forests_count > 0) {
        const candidate = this.randMapPosCharacteristic(tiles, 'WC_ALL', 'TT_NFROZEN', 'MC_NONE');
        if (candidate) {
          // Use pick_terrain with proper properties as in freeciv
          // @reference freeciv/server/generator/mapgen.c:522 pick_terrain(MG_FOLIAGE, MG_TEMPERATE, MG_TROPICAL)
          const terrain = pickTerrain(
            MapgenTerrainPropertyEnum.FOLIAGE,
            MapgenTerrainPropertyEnum.TEMPERATE,
            MapgenTerrainPropertyEnum.TROPICAL,
            this.random
          );
          this.placeTerrain(
            candidate.tile,
            candidate.x,
            candidate.y,
            60,
            terrain,
            forests_count,
            plains_count,
            'WC_ALL',
            'TT_NFROZEN',
            'MC_NONE'
          );
          forests_count--;
        } else {
          plains_count += forests_count;
          forests_count = 0;
        }
      }

      // PLACE_ONE_TYPE(jungles_count, forests_count, pick_terrain(MG_FOLIAGE, MG_TROPICAL, MG_COLD), WC_ALL, TT_TROPICAL, MC_NONE, 50);
      if (jungles_count > 0) {
        const candidate = this.randMapPosCharacteristic(tiles, 'WC_ALL', 'TT_TROPICAL', 'MC_NONE');
        if (candidate) {
          // Use pick_terrain with proper properties as in freeciv
          // @reference freeciv/server/generator/mapgen.c:540 pick_terrain(MG_FOLIAGE, MG_TROPICAL, MG_COLD)
          const terrain = pickTerrain(
            MapgenTerrainPropertyEnum.FOLIAGE,
            MapgenTerrainPropertyEnum.TROPICAL,
            MapgenTerrainPropertyEnum.COLD,
            this.random
          );
          this.placeTerrain(
            candidate.tile,
            candidate.x,
            candidate.y,
            50,
            terrain,
            jungles_count,
            forests_count,
            'WC_ALL',
            'TT_TROPICAL',
            'MC_NONE'
          );
          jungles_count--;
        } else {
          forests_count += jungles_count;
          jungles_count = 0;
        }
      }

      // PLACE_ONE_TYPE(swamps_count, forests_count, pick_terrain(MG_WET, MG_UNUSED, MG_FOLIAGE), WC_NDRY, TT_HOT, MC_LOW, 50);
      if (swamps_count > 0) {
        const candidate = this.randMapPosCharacteristic(tiles, 'WC_NDRY', 'TT_HOT', 'MC_LOW');
        if (candidate) {
          // Use pick_terrain with proper properties as in freeciv
          // @reference freeciv/server/generator/mapgen.c:558 pick_terrain(MG_WET, MG_UNUSED, MG_FOLIAGE)
          const terrain = pickTerrain(
            MapgenTerrainPropertyEnum.WET,
            MapgenTerrainPropertyEnum.UNUSED,
            MapgenTerrainPropertyEnum.FOLIAGE,
            this.random
          );
          this.placeTerrain(
            candidate.tile,
            candidate.x,
            candidate.y,
            50,
            terrain,
            swamps_count,
            forests_count,
            'WC_NDRY',
            'TT_HOT',
            'MC_LOW'
          );
          swamps_count--;
        } else {
          forests_count += swamps_count;
          swamps_count = 0;
        }
      }

      // PLACE_ONE_TYPE(deserts_count, alt_deserts_count, pick_terrain(MG_DRY, MG_TROPICAL, MG_COLD), WC_DRY, TT_NFROZEN, MC_NLOW, 80);
      if (deserts_count > 0) {
        const candidate = this.randMapPosCharacteristic(tiles, 'WC_DRY', 'TT_NFROZEN', 'MC_NLOW');
        if (candidate) {
          // Use pick_terrain with proper properties as in freeciv
          // @reference freeciv/server/generator/mapgen.c:576 pick_terrain(MG_DRY, MG_TROPICAL, MG_COLD)
          const terrain = pickTerrain(
            MapgenTerrainPropertyEnum.DRY,
            MapgenTerrainPropertyEnum.TROPICAL,
            MapgenTerrainPropertyEnum.COLD,
            this.random
          );
          this.placeTerrain(
            candidate.tile,
            candidate.x,
            candidate.y,
            80,
            terrain,
            deserts_count,
            alt_deserts_count,
            'WC_DRY',
            'TT_NFROZEN',
            'MC_NLOW'
          );
          deserts_count--;
        } else {
          alt_deserts_count += deserts_count;
          deserts_count = 0;
        }
      }

      // PLACE_ONE_TYPE(alt_deserts_count, plains_count, pick_terrain(MG_DRY, MG_TROPICAL, MG_WET), WC_ALL, TT_NFROZEN, MC_NLOW, 40);
      if (alt_deserts_count > 0) {
        const candidate = this.randMapPosCharacteristic(tiles, 'WC_ALL', 'TT_NFROZEN', 'MC_NLOW');
        if (candidate) {
          // Use pick_terrain with proper properties as in freeciv
          // @reference freeciv/server/generator/mapgen.c:594 pick_terrain(MG_DRY, MG_TROPICAL, MG_WET)
          const terrain = pickTerrain(
            MapgenTerrainPropertyEnum.DRY,
            MapgenTerrainPropertyEnum.TROPICAL,
            MapgenTerrainPropertyEnum.WET,
            this.random
          );
          this.placeTerrain(
            candidate.tile,
            candidate.x,
            candidate.y,
            40,
            terrain,
            alt_deserts_count,
            plains_count,
            'WC_ALL',
            'TT_NFROZEN',
            'MC_NLOW'
          );
          alt_deserts_count--;
        } else {
          plains_count += alt_deserts_count;
          alt_deserts_count = 0;
        }
      }
    } while (
      forests_count > 0 ||
      jungles_count > 0 ||
      swamps_count > 0 ||
      deserts_count > 0 ||
      alt_deserts_count > 0
    );

    // Fill remaining spots with plains/grassland - exact copy of freeciv logic
    // @reference freeciv/server/generator/mapgen.c:607-612
    for (let x = 0; x < this.width; x++) {
      for (let y = 0; y < this.height; y++) {
        const tile = tiles[x][y];
        if (this.placementMap.notPlaced(x, y) && !isOceanTerrain(tile.terrain)) {
          const candidate = this.randMapPosCharacteristic(tiles, 'WC_ALL', 'TT_ALL', 'MC_NONE');
          if (candidate) {
            this.makePlain(candidate.tile, candidate.x, candidate.y);
          }
        }
      }
    }
  }

  /**
   * Find random position with given characteristics
   * @reference freeciv/server/generator/mapgen.c rand_map_pos_characteristic()
   * Uses placement tracking to find valid tiles for terrain placement
   */
  private randMapPosCharacteristic(
    tiles: MapTile[][],
    wetness_condition: string,
    temp_condition: string,
    mount_condition: string
  ): { tile: MapTile; x: number; y: number } | null {
    const candidates: { tile: MapTile; x: number; y: number }[] = [];

    for (let x = 0; x < this.width; x++) {
      for (let y = 0; y < this.height; y++) {
        const tile = tiles[x][y];

        // Only consider unplaced land tiles using placement tracking
        // @reference freeciv/server/generator/mapgen.c:262 not yet placed on pmap
        if (!this.placementMap.notPlaced(x, y) || isOceanTerrain(tile.terrain)) continue;

        // Check wetness condition
        if (!this.checkWetnessCondition(tile, wetness_condition)) continue;

        // Check temperature condition
        if (!this.checkTemperatureCondition(tile, temp_condition)) continue;

        // Check mountain condition
        if (!this.checkMountainCondition(tile, mount_condition)) continue;

        candidates.push({ tile, x, y });
      }
    }

    if (candidates.length === 0) return null;
    return candidates[Math.floor(this.random() * candidates.length)];
  }

  /**
   * Supporting function - place terrain with weight/spread logic
   * @reference freeciv/server/generator/mapgen.c place_terrain()
   * Marks placed tiles using placement tracking system
   */
  private placeTerrain(
    tile: MapTile,
    x: number,
    y: number,
    _weight: number,
    terrain: string,
    _count: number,
    _alternate: number,
    _wc: string,
    _tc: string,
    _mc: string
  ): void {
    // Set terrain type
    tile.terrain = terrain as TerrainType;

    // Mark tile as placed in placement map
    // @reference freeciv/server/generator/mapgen_utils.c:79 map_set_placed()
    this.placementMap.setPlaced(x, y);

    this.setTerrainProperties(tile);
  }

  /**
   * Supporting function - make plains/tundra based on temperature
   * @reference freeciv/server/generator/mapgen.c make_plain()
   * Uses placement tracking to mark placed tiles
   */
  private makePlain(tile: MapTile, x: number, y: number): void {
    // Choose terrain based on temperature using pick_terrain like freeciv
    // @reference freeciv/server/generator/mapgen.c:437-445
    let terrain: TerrainType;
    if (tile.temperature === TemperatureType.FROZEN) {
      // tile_set_terrain(ptile, pick_terrain(MG_FROZEN, MG_UNUSED, MG_MOUNTAINOUS));
      terrain = pickTerrain(
        MapgenTerrainPropertyEnum.FROZEN,
        MapgenTerrainPropertyEnum.UNUSED,
        MapgenTerrainPropertyEnum.MOUNTAINOUS,
        this.random
      );
    } else if (tile.temperature === TemperatureType.COLD) {
      // tile_set_terrain(ptile, pick_terrain(MG_COLD, MG_UNUSED, MG_MOUNTAINOUS));
      terrain = pickTerrain(
        MapgenTerrainPropertyEnum.COLD,
        MapgenTerrainPropertyEnum.UNUSED,
        MapgenTerrainPropertyEnum.MOUNTAINOUS,
        this.random
      );
    } else {
      // tile_set_terrain(ptile, pick_terrain(MG_TEMPERATE, MG_GREEN, MG_MOUNTAINOUS));
      terrain = pickTerrain(
        MapgenTerrainPropertyEnum.TEMPERATE,
        MapgenTerrainPropertyEnum.GREEN,
        MapgenTerrainPropertyEnum.MOUNTAINOUS,
        this.random
      );
    }

    tile.terrain = terrain;

    // Mark as placed using placement tracking
    // @reference freeciv/server/generator/mapgen_utils.c:79 map_set_placed()
    this.placementMap.setPlaced(x, y);

    this.setTerrainProperties(tile);
  }

  /**
   * Check wetness condition for terrain placement
   * @reference freeciv/server/generator/mapgen.c wetness conditions
   */
  private checkWetnessCondition(tile: MapTile, condition: string): boolean {
    const wetness = tile.wetness || 0;
    switch (condition) {
      case 'WC_ALL':
        return true;
      case 'WC_DRY':
        return wetness < 50;
      case 'WC_NDRY':
        return wetness >= 50;
      default:
        return true;
    }
  }

  /**
   * Check temperature condition for terrain placement
   * @reference freeciv/server/generator/mapgen.c temperature conditions
   */
  private checkTemperatureCondition(tile: MapTile, condition: string): boolean {
    switch (condition) {
      case 'TT_ALL':
        return true;
      case 'TT_NFROZEN':
        return tile.temperature !== TemperatureType.FROZEN;
      case 'TT_TROPICAL':
        return tile.temperature === TemperatureType.TROPICAL;
      case 'TT_HOT':
        return (
          tile.temperature === TemperatureType.TROPICAL ||
          tile.temperature === TemperatureType.TEMPERATE
        );
      default:
        return true;
    }
  }

  /**
   * Check mountain condition for terrain placement
   * @reference freeciv/server/generator/mapgen.c mountain conditions
   * Uses freeciv's hmap_low_level threshold for MC_LOW/MC_NLOW conditions
   */
  private checkMountainCondition(tile: MapTile, condition: string): boolean {
    const height = tile.elevation || 0;
    switch (condition) {
      case 'MC_NONE':
        return true;
      case 'MC_LOW':
        // @reference freeciv/server/generator/mapgen.c map_pos_is_low(ptile)
        return height < this.hmapLowLevel;
      case 'MC_NLOW':
        // @reference freeciv/server/generator/mapgen.c !map_pos_is_low(ptile)
        return height >= this.hmapLowLevel;
      default:
        return true;
    }
  }

  /**
   * Public method to set terrain properties for external use
   * @reference freeciv/server/generator/mapgen.c terrain property setting
   */
  public setTerrainPropertiesForTile(tile: MapTile): void {
    this.setTerrainProperties(tile);
  }

  /**
   * Set terrain properties after terrain assignment
   * @reference freeciv/server/generator/mapgen.c terrain property setting
   */
  private setTerrainProperties(tile: MapTile): void {
    // Set game properties based on terrain type
    // This mirrors the freeciv server's terrain property assignment

    // Get terrain properties from ruleset
    const properties = getTerrainProperties(tile.terrain);

    // Apply properties to tile (this ensures consistency with freeciv rules)
    tile.properties = properties;
  }
}

import { Server, Socket } from 'socket.io';
import { logger } from '../../utils/logger';
import { PacketHandler } from '../PacketHandler';
import { BaseSocketHandler } from './BaseSocketHandler';
import { PacketType, TileVisibilityReqSchema } from '../../types/packet';
import { GameManager } from '../../game/GameManager';
import { db } from '../../database';
import { games } from '../../database/schema';
import { eq } from 'drizzle-orm';

/**
 * Handles map and visibility-related packets
 */
export class MapVisibilityHandler extends BaseSocketHandler {
  protected handledPacketTypes = [
    PacketType.MAP_INFO,
    PacketType.TILE_INFO,
    PacketType.MAP_VIEW_REQ,
    PacketType.MAP_VIEW_REPLY,
    PacketType.TILE_VISIBILITY_REQ,
    PacketType.TILE_VISIBILITY_REPLY,
  ];

  protected handlerName = 'MapVisibilityHandler';

  constructor(private activeConnections: Map<string, any>, private gameManager: GameManager) {
    super();
  }

  register(handler: PacketHandler, _io: Server, socket: Socket): void {
    handler.register(PacketType.MAP_VIEW_REQ, async (socket, _data) => {
      await this.handleMapViewRequest(handler, socket, _data);
    });

    handler.register(
      PacketType.TILE_VISIBILITY_REQ,
      async (socket, _data) => {
        await this.handleTileVisibilityRequest(handler, socket, _data);
      },
      TileVisibilityReqSchema
    );

    // Register socket event handlers
    this.registerSocketEvents(socket, _io);

    logger.debug(`${this.handlerName} registered handlers for socket ${socket.id}`);
  }

  private registerSocketEvents(socket: Socket, _io: Server): void {
    socket.on('get_map_data', async (_data, callback) => {
      await this.handleGetMapData(socket, callback);
    });

    socket.on('get_visible_tiles', async (_data, callback) => {
      await this.handleGetVisibleTiles(socket, callback);
    });
  }

  private async handleMapViewRequest(
    handler: PacketHandler,
    socket: Socket,
    _data: any
  ): Promise<void> {
    const connection = this.getConnection(socket, this.activeConnections);
    if (!this.isAuthenticated(connection) || !this.isInGame(connection)) {
      return;
    }

    try {
      const game = await this.gameManager.getGame(connection.gameId!);
      if (!game) return;

      const player = Array.from(game.players.values()).find(
        (p: any) => p.userId === connection.userId
      ) as any;
      if (!player) return;

      // Update visibility first
      this.gameManager.updatePlayerVisibility(connection.gameId!, player.id);

      // Get player's map view
      const mapData = this.gameManager.getPlayerMapView(connection.gameId!, player.id);

      handler.send(socket, PacketType.MAP_VIEW_REPLY, {
        mapData,
      });

      logger.debug('Sent map view to player', {
        gameId: connection.gameId,
        playerId: player.id,
      });
    } catch (error) {
      logger.error('Error processing map view request:', error);
    }
  }

  private async handleTileVisibilityRequest(
    handler: PacketHandler,
    socket: Socket,
    data: any
  ): Promise<void> {
    const connection = this.getConnection(socket, this.activeConnections);
    if (!this.isAuthenticated(connection) || !this.isInGame(connection)) {
      handler.send(socket, PacketType.TILE_VISIBILITY_REPLY, {
        x: data.x,
        y: data.y,
        isVisible: false,
        isExplored: false,
      });
      return;
    }

    try {
      const game = await this.gameManager.getGame(connection.gameId!);
      if (!game) return;

      const player = Array.from(game.players.values()).find(
        (p: any) => p.userId === connection.userId
      ) as any;
      if (!player) return;

      const visibility = this.gameManager.getTileVisibility(
        connection.gameId!,
        player.id,
        data.x,
        data.y
      );

      handler.send(socket, PacketType.TILE_VISIBILITY_REPLY, {
        x: data.x,
        y: data.y,
        isVisible: visibility.isVisible,
        isExplored: visibility.isExplored,
        lastSeen: visibility.lastSeen,
      });

      logger.debug('Sent tile visibility info', {
        gameId: connection.gameId,
        playerId: player.id,
        tile: { x: data.x, y: data.y },
        visibility,
      });
    } catch (error) {
      logger.error('Error processing tile visibility request:', error);
      handler.send(socket, PacketType.TILE_VISIBILITY_REPLY, {
        x: data.x,
        y: data.y,
        isVisible: false,
        isExplored: false,
      });
    }
  }

  private async handleGetMapData(socket: Socket, callback: (response: any) => void): Promise<void> {
    const connection = this.getConnection(socket, this.activeConnections);
    if (!this.isInGame(connection)) {
      callback({ success: false, error: 'Not in a game' });
      return;
    }

    try {
      const mapData = this.gameManager.getMapData(connection.gameId!);
      callback({ success: true, mapData });
    } catch (error) {
      logger.error('Error getting map data:', error);
      callback({
        success: false,
        error: error instanceof Error ? error.message : 'Failed to get map data',
      });
    }
  }

  private async handleGetVisibleTiles(
    socket: Socket,
    callback: (response: any) => void
  ): Promise<void> {
    const connection = this.getConnection(socket, this.activeConnections);
    if (!this.isInGame(connection)) {
      callback({ success: false, error: 'Not in a game' });
      return;
    }

    try {
      const game = await db.query.games.findFirst({
        where: eq(games.id, connection.gameId!),
        with: {
          players: true,
        },
      });

      if (!game) {
        callback({ success: false, error: 'Game not found' });
        return;
      }

      const player = game.players.find(p => p.userId === connection.userId);
      if (!player) {
        callback({ success: false, error: 'Player not found in game' });
        return;
      }

      const visibleTiles = this.gameManager.getPlayerVisibleTiles(connection.gameId!, player.id);
      callback({ success: true, visibleTiles });
    } catch (error) {
      logger.error('Error getting visible tiles:', error);
      callback({
        success: false,
        error: error instanceof Error ? error.message : 'Failed to get visible tiles',
      });
    }
  }
}

import { Server, Socket } from 'socket.io';
import { logger } from '../../utils/logger';
import { PacketHandler } from '../PacketHandler';
import { BaseSocketHandler } from './BaseSocketHandler';
import {
  PacketType,
  ResearchSetSchema,
  ResearchGoalSetSchema,
  ResearchListSchema,
  ResearchProgressSchema,
} from '../../types/packet';
import { GameManager } from '../../game/GameManager';

/**
 * Handles research-related packets: setting research, research goals, progress tracking
 */
export class ResearchHandler extends BaseSocketHandler {
  protected handledPacketTypes = [
    PacketType.RESEARCH_SET,
    PacketType.RESEARCH_SET_REPLY,
    PacketType.RESEARCH_GOAL_SET,
    PacketType.RESEARCH_GOAL_SET_REPLY,
    PacketType.RESEARCH_LIST,
    PacketType.RESEARCH_LIST_REPLY,
    PacketType.RESEARCH_PROGRESS,
    PacketType.RESEARCH_PROGRESS_REPLY,
  ];

  protected handlerName = 'ResearchHandler';

  constructor(private activeConnections: Map<string, any>, private gameManager: GameManager) {
    super();
  }

  register(handler: PacketHandler, _io: Server, socket: Socket): void {
    handler.register(
      PacketType.RESEARCH_SET,
      async (socket, _data) => {
        await this.handleResearchSet(handler, socket, _data);
      },
      ResearchSetSchema
    );

    handler.register(
      PacketType.RESEARCH_GOAL_SET,
      async (socket, _data) => {
        await this.handleResearchGoalSet(handler, socket, _data);
      },
      ResearchGoalSetSchema
    );

    handler.register(
      PacketType.RESEARCH_LIST,
      async (socket, _data) => {
        await this.handleResearchList(handler, socket, _data);
      },
      ResearchListSchema
    );

    handler.register(
      PacketType.RESEARCH_PROGRESS,
      async (socket, _data) => {
        await this.handleResearchProgress(handler, socket, _data);
      },
      ResearchProgressSchema
    );

    logger.debug(`${this.handlerName} registered handlers for socket ${socket.id}`);
  }

  private async handleResearchSet(
    handler: PacketHandler,
    socket: Socket,
    data: any
  ): Promise<void> {
    const connection = this.getConnection(socket, this.activeConnections);
    if (!this.isAuthenticated(connection) || !this.isInGame(connection)) {
      handler.send(socket, PacketType.RESEARCH_SET_REPLY, {
        success: false,
        message: 'Not authenticated or not in a game',
      });
      return;
    }

    try {
      const game = await this.gameManager.getGame(connection.gameId!);
      if (!game || game.state !== 'active') {
        handler.send(socket, PacketType.RESEARCH_SET_REPLY, {
          success: false,
          message: 'Game is not active',
        });
        return;
      }

      const player = Array.from(game.players.values()).find(
        (p: any) => p.userId === connection.userId
      ) as any;
      if (!player) {
        handler.send(socket, PacketType.RESEARCH_SET_REPLY, {
          success: false,
          message: 'Player not found in game',
        });
        return;
      }

      await this.gameManager.setPlayerResearch(connection.gameId!, player.id, data.techId);

      const availableTechs = this.gameManager.getAvailableTechnologies(
        connection.gameId!,
        player.id
      );

      handler.send(socket, PacketType.RESEARCH_SET_REPLY, {
        success: true,
        availableTechs: availableTechs.map(tech => ({
          id: tech.id,
          name: tech.name,
          cost: tech.cost,
          requirements: tech.requirements,
          description: tech.description,
        })),
      });

      logger.debug('Research set', {
        gameId: connection.gameId,
        playerId: player.id,
        techId: data.techId,
      });
    } catch (error) {
      logger.error('Error setting research:', error);
      handler.send(socket, PacketType.RESEARCH_SET_REPLY, {
        success: false,
        message: error instanceof Error ? error.message : 'Failed to set research',
      });
    }
  }

  private async handleResearchGoalSet(
    handler: PacketHandler,
    socket: Socket,
    data: any
  ): Promise<void> {
    const connection = this.getConnection(socket, this.activeConnections);
    if (!this.isAuthenticated(connection) || !this.isInGame(connection)) {
      handler.send(socket, PacketType.RESEARCH_GOAL_SET_REPLY, {
        success: false,
        message: 'Not authenticated or not in a game',
      });
      return;
    }

    try {
      const game = await this.gameManager.getGame(connection.gameId!);
      if (!game || game.state !== 'active') {
        handler.send(socket, PacketType.RESEARCH_GOAL_SET_REPLY, {
          success: false,
          message: 'Game is not active',
        });
        return;
      }

      const player = Array.from(game.players.values()).find(
        (p: any) => p.userId === connection.userId
      ) as any;
      if (!player) {
        handler.send(socket, PacketType.RESEARCH_GOAL_SET_REPLY, {
          success: false,
          message: 'Player not found in game',
        });
        return;
      }

      await this.gameManager.setResearchGoal(connection.gameId!, player.id, data.techId);

      handler.send(socket, PacketType.RESEARCH_GOAL_SET_REPLY, {
        success: true,
      });

      logger.debug('Research goal set', {
        gameId: connection.gameId,
        playerId: player.id,
        techGoal: data.techId,
      });
    } catch (error) {
      logger.error('Error setting research goal:', error);
      handler.send(socket, PacketType.RESEARCH_GOAL_SET_REPLY, {
        success: false,
        message: error instanceof Error ? error.message : 'Failed to set research goal',
      });
    }
  }

  private async handleResearchList(
    handler: PacketHandler,
    socket: Socket,
    _data: any
  ): Promise<void> {
    const connection = this.getConnection(socket, this.activeConnections);
    if (!this.isAuthenticated(connection) || !this.isInGame(connection)) {
      return;
    }

    try {
      const game = await this.gameManager.getGame(connection.gameId!);
      if (!game) return;

      const player = Array.from(game.players.values()).find(
        (p: any) => p.userId === connection.userId
      ) as any;
      if (!player) return;

      const availableTechs = this.gameManager.getAvailableTechnologies(
        connection.gameId!,
        player.id
      );
      const playerResearch = this.gameManager.getPlayerResearch(connection.gameId!, player.id);

      handler.send(socket, PacketType.RESEARCH_LIST_REPLY, {
        availableTechs: availableTechs.map(tech => ({
          id: tech.id,
          name: tech.name,
          cost: tech.cost,
          requirements: tech.requirements,
          description: tech.description,
        })),
        researchedTechs: playerResearch ? Array.from(playerResearch.researchedTechs) : [],
      });

      logger.debug('Sent research list', {
        gameId: connection.gameId,
        playerId: player.id,
        availableCount: availableTechs.length,
        researchedCount: playerResearch?.researchedTechs.size || 0,
      });
    } catch (error) {
      logger.error('Error getting research list:', error);
    }
  }

  private async handleResearchProgress(
    handler: PacketHandler,
    socket: Socket,
    _data: any
  ): Promise<void> {
    const connection = this.getConnection(socket, this.activeConnections);
    if (!this.isAuthenticated(connection) || !this.isInGame(connection)) {
      return;
    }

    try {
      const game = await this.gameManager.getGame(connection.gameId!);
      if (!game) return;

      const player = Array.from(game.players.values()).find(
        (p: any) => p.userId === connection.userId
      ) as any;
      if (!player) return;

      const playerResearch = this.gameManager.getPlayerResearch(connection.gameId!, player.id);
      const progress = this.gameManager.getResearchProgress(connection.gameId!, player.id);

      handler.send(socket, PacketType.RESEARCH_PROGRESS_REPLY, {
        currentTech: playerResearch?.currentTech,
        techGoal: playerResearch?.techGoal,
        current: progress?.current || 0,
        required: progress?.required || 0,
        turnsRemaining: progress?.turnsRemaining || -1,
      });

      logger.debug('Sent research progress', {
        gameId: connection.gameId,
        playerId: player.id,
        currentTech: playerResearch?.currentTech,
        progress: progress,
      });
    } catch (error) {
      logger.error('Error getting research progress:', error);
    }
  }
}

import { Server, Socket } from 'socket.io';
import { logger } from '../../utils/logger';
import { PacketHandler } from '../PacketHandler';
import { BaseSocketHandler } from './BaseSocketHandler';
import { PacketType } from '../../types/packet';
import { GameManager } from '../../game/GameManager';

/**
 * Handles turn management packets: ending turns, turn processing
 */
export class TurnManagementHandler extends BaseSocketHandler {
  protected handledPacketTypes = [
    PacketType.END_TURN,
    PacketType.TURN_END_REPLY,
    PacketType.TURN_START,
    PacketType.NEW_TURN,
    PacketType.BEGIN_TURN,
  ];

  protected handlerName = 'TurnManagementHandler';

  constructor(private activeConnections: Map<string, any>, private gameManager: GameManager) {
    super();
  }

  register(handler: PacketHandler, io: Server, socket: Socket): void {
    handler.register(PacketType.END_TURN, async socket => {
      await this.handleEndTurn(handler, socket, io);
    });

    logger.debug(`${this.handlerName} registered handlers for socket ${socket.id}`);
  }

  private async handleEndTurn(handler: PacketHandler, socket: Socket, io: Server): Promise<void> {
    const connection = this.getConnection(socket, this.activeConnections);
    if (!this.isAuthenticated(connection) || !this.isInGame(connection)) {
      return;
    }

    try {
      let playerId: string | null = null;
      for (const game of await this.gameManager.getAllGames()) {
        const player = game.players.find((p: any) => p.userId === connection.userId) as any;
        if (player) {
          playerId = player.id;
          break;
        }
      }

      if (!playerId) return;

      const turnAdvanced = await this.gameManager.endTurn(playerId);

      if (turnAdvanced && connection.gameId) {
        // Get the updated game state from database after turn processing
        const updatedGame = await this.gameManager.getGame(connection.gameId);
        const gameInstance = this.gameManager.getGameInstance(connection.gameId);

        if (updatedGame && gameInstance) {
          const turnData = {
            turn: updatedGame.currentTurn,
            year: gameInstance.turnManager.getCurrentYear(),
          };
          logger.debug('Sending TURN_START packet', {
            gameId: connection.gameId,
            turnData,
            gameInstanceTurn: gameInstance.currentTurn,
            dbTurn: updatedGame.currentTurn,
          });
          // Notify all players that turn advanced
          io.to(`game:${connection.gameId}`).emit('packet', {
            type: PacketType.TURN_START,
            data: turnData,
          });
        } else {
          logger.warn('No game found for TURN_START', {
            gameId: connection.gameId,
            updatedGame: !!updatedGame,
            gameInstance: !!gameInstance,
          });
        }
      } else {
        logger.debug('Not sending TURN_START', {
          turnAdvanced,
          gameId: connection.gameId,
        });
      }

      handler.send(socket, PacketType.TURN_END_REPLY, {
        success: true,
        turnAdvanced,
      });

      logger.debug(`${connection.username} ended turn`, {
        gameId: connection.gameId,
        turnAdvanced,
      });
    } catch (error) {
      logger.error('Error ending turn:', error);
      handler.send(socket, PacketType.TURN_END_REPLY, {
        success: false,
        message: error instanceof Error ? error.message : 'Failed to end turn',
      });
    }
  }
}

import { Server, Socket } from 'socket.io';
import { logger } from '../../utils/logger';
import { PacketHandler } from '../PacketHandler';
import { BaseSocketHandler } from './BaseSocketHandler';
import {
  PacketType,
  UnitMoveSchema,
  UnitAttackSchema,
  UnitFortifySchema,
  UnitCreateSchema,
} from '../../types/packet';
import { GameManager } from '../../game/GameManager';

/**
 * Handles unit action packets: movement, attack, fortify, creation, pathfinding
 * Manages unit-related operations and interactions
 */
export class UnitActionHandler extends BaseSocketHandler {
  protected handledPacketTypes = [
    PacketType.UNIT_MOVE,
    PacketType.UNIT_MOVE_REPLY,
    PacketType.UNIT_ATTACK,
    PacketType.UNIT_ATTACK_REPLY,
    PacketType.UNIT_FORTIFY,
    PacketType.UNIT_FORTIFY_REPLY,
    PacketType.UNIT_CREATE,
    PacketType.UNIT_CREATE_REPLY,
    PacketType.UNIT_INFO,
  ];

  protected handlerName = 'UnitActionHandler';

  private activeConnections: Map<string, { userId?: string; username?: string; gameId?: string }>;
  private gameManager: GameManager;

  constructor(activeConnections: Map<string, any>, gameManager: GameManager) {
    super();
    this.activeConnections = activeConnections;
    this.gameManager = gameManager;
  }

  register(handler: PacketHandler, io: Server, socket: Socket): void {
    // Register packet handlers
    handler.register(
      PacketType.UNIT_MOVE,
      async (socket, data) => {
        await this.handleUnitMove(handler, socket, data, io);
      },
      UnitMoveSchema
    );

    handler.register(
      PacketType.UNIT_ATTACK,
      async (socket, data) => {
        await this.handleUnitAttack(handler, socket, data, io);
      },
      UnitAttackSchema
    );

    handler.register(
      PacketType.UNIT_FORTIFY,
      async (socket, data) => {
        await this.handleUnitFortify(handler, socket, data, io);
      },
      UnitFortifySchema
    );

    handler.register(
      PacketType.UNIT_CREATE,
      async (socket, data) => {
        await this.handleUnitCreate(handler, socket, data, io);
      },
      UnitCreateSchema
    );

    // Register socket event handlers
    this.registerSocketEvents(socket, io);

    logger.debug(`${this.handlerName} registered handlers for socket ${socket.id}`);
  }

  /**
   * Register non-packet socket events
   */
  private registerSocketEvents(socket: Socket, io: Server): void {
    // Handle unit_action event
    socket.on('unit_action', async (data, callback) => {
      await this.handleUnitActionEvent(socket, data, callback, io);
    });

    // Handle path_request event
    socket.on('path_request', async (data, callback) => {
      await this.handlePathRequestEvent(socket, data, callback);
    });
  }

  /**
   * Handle UNIT_MOVE packet
   */
  private async handleUnitMove(
    handler: PacketHandler,
    socket: Socket,
    data: any,
    _io: Server
  ): Promise<void> {
    const connection = this.getConnection(socket, this.activeConnections);
    if (!this.isAuthenticated(connection) || !this.isInGame(connection)) {
      handler.send(socket, PacketType.UNIT_MOVE_REPLY, {
        success: false,
        unitId: data.unitId,
        message: 'Not authenticated or not in a game',
      });
      return;
    }

    try {
      const game = await this.gameManager.getGame(connection.gameId!);
      if (!game || game.state !== 'active') {
        handler.send(socket, PacketType.UNIT_MOVE_REPLY, {
          success: false,
          unitId: data.unitId,
          message: 'Game is not active',
        });
        return;
      }

      const player = Array.from(game.players.values()).find(
        (p: any) => p.userId === connection.userId
      ) as any;
      if (!player) {
        handler.send(socket, PacketType.UNIT_MOVE_REPLY, {
          success: false,
          unitId: data.unitId,
          message: 'Player not found in game',
        });
        return;
      }

      // Execute the move immediately
      const moved = await this.gameManager.moveUnit(
        connection.gameId!,
        player.id,
        data.unitId,
        data.x,
        data.y
      );

      if (moved) {
        const gameInstance = this.gameManager.getGameInstance(connection.gameId!);
        const unit = gameInstance?.unitManager.getUnit(data.unitId);
        handler.send(socket, PacketType.UNIT_MOVE_REPLY, {
          success: true,
          unitId: data.unitId,
          newX: unit?.x,
          newY: unit?.y,
          movementLeft: unit?.movementLeft,
        });

        logger.debug('Unit moved successfully', {
          gameId: connection.gameId,
          playerId: player.id,
          unitId: data.unitId,
          newPosition: { x: data.x, y: data.y },
        });
      } else {
        handler.send(socket, PacketType.UNIT_MOVE_REPLY, {
          success: false,
          unitId: data.unitId,
          message: 'Move failed',
        });
      }
    } catch (error) {
      logger.error('Error processing unit move:', error);
      handler.send(socket, PacketType.UNIT_MOVE_REPLY, {
        success: false,
        unitId: data.unitId,
        message: error instanceof Error ? error.message : 'Failed to move unit',
      });
    }
  }

  /**
   * Handle UNIT_ATTACK packet
   */
  private async handleUnitAttack(
    handler: PacketHandler,
    socket: Socket,
    data: any,
    _io: Server
  ): Promise<void> {
    const connection = this.getConnection(socket, this.activeConnections);
    if (!this.isAuthenticated(connection) || !this.isInGame(connection)) {
      handler.send(socket, PacketType.UNIT_ATTACK_REPLY, {
        success: false,
        message: 'Not authenticated or not in a game',
      });
      return;
    }

    try {
      const game = await this.gameManager.getGame(connection.gameId!);
      if (!game || game.state !== 'active') {
        handler.send(socket, PacketType.UNIT_ATTACK_REPLY, {
          success: false,
          message: 'Game is not active',
        });
        return;
      }

      const player = Array.from(game.players.values()).find(
        (p: any) => p.userId === connection.userId
      ) as any;
      if (!player) {
        handler.send(socket, PacketType.UNIT_ATTACK_REPLY, {
          success: false,
          message: 'Player not found in game',
        });
        return;
      }

      const combatResult = await this.gameManager.attackUnit(
        connection.gameId!,
        player.id,
        data.attackerUnitId,
        data.defenderUnitId
      );

      handler.send(socket, PacketType.UNIT_ATTACK_REPLY, {
        success: true,
        combatResult,
      });

      logger.debug('Unit attack executed', {
        gameId: connection.gameId,
        playerId: player.id,
        attackerUnitId: data.attackerUnitId,
        defenderUnitId: data.defenderUnitId,
        combatResult,
      });
    } catch (error) {
      logger.error('Error processing unit attack:', error);
      handler.send(socket, PacketType.UNIT_ATTACK_REPLY, {
        success: false,
        message: error instanceof Error ? error.message : 'Failed to attack unit',
      });
    }
  }

  /**
   * Handle UNIT_FORTIFY packet
   */
  private async handleUnitFortify(
    handler: PacketHandler,
    socket: Socket,
    data: any,
    _io: Server
  ): Promise<void> {
    const connection = this.getConnection(socket, this.activeConnections);
    if (!this.isAuthenticated(connection) || !this.isInGame(connection)) {
      handler.send(socket, PacketType.UNIT_FORTIFY_REPLY, {
        success: false,
        unitId: data.unitId,
        message: 'Not authenticated or not in a game',
      });
      return;
    }

    try {
      const game = await this.gameManager.getGame(connection.gameId!);
      if (!game || game.state !== 'active') {
        handler.send(socket, PacketType.UNIT_FORTIFY_REPLY, {
          success: false,
          unitId: data.unitId,
          message: 'Game is not active',
        });
        return;
      }

      const player = Array.from(game.players.values()).find(
        (p: any) => p.userId === connection.userId
      ) as any;
      if (!player) {
        handler.send(socket, PacketType.UNIT_FORTIFY_REPLY, {
          success: false,
          unitId: data.unitId,
          message: 'Player not found in game',
        });
        return;
      }

      await this.gameManager.fortifyUnit(connection.gameId!, player.id, data.unitId);

      handler.send(socket, PacketType.UNIT_FORTIFY_REPLY, {
        success: true,
        unitId: data.unitId,
      });

      logger.debug('Unit fortified', {
        gameId: connection.gameId,
        playerId: player.id,
        unitId: data.unitId,
      });
    } catch (error) {
      logger.error('Error fortifying unit:', error);
      handler.send(socket, PacketType.UNIT_FORTIFY_REPLY, {
        success: false,
        unitId: data.unitId,
        message: error instanceof Error ? error.message : 'Failed to fortify unit',
      });
    }
  }

  /**
   * Handle UNIT_CREATE packet
   */
  private async handleUnitCreate(
    handler: PacketHandler,
    socket: Socket,
    data: any,
    _io: Server
  ): Promise<void> {
    const connection = this.getConnection(socket, this.activeConnections);
    if (!this.isAuthenticated(connection) || !this.isInGame(connection)) {
      handler.send(socket, PacketType.UNIT_CREATE_REPLY, {
        success: false,
        message: 'Not authenticated or not in a game',
      });
      return;
    }

    try {
      const game = await this.gameManager.getGame(connection.gameId!);
      if (!game || game.state !== 'active') {
        handler.send(socket, PacketType.UNIT_CREATE_REPLY, {
          success: false,
          message: 'Game is not active',
        });
        return;
      }

      const player = Array.from(game.players.values()).find(
        (p: any) => p.userId === connection.userId
      ) as any;
      if (!player) {
        handler.send(socket, PacketType.UNIT_CREATE_REPLY, {
          success: false,
          message: 'Player not found in game',
        });
        return;
      }

      const unitId = await this.gameManager.createUnit(
        connection.gameId!,
        player.id,
        data.unitType,
        data.x,
        data.y
      );

      handler.send(socket, PacketType.UNIT_CREATE_REPLY, {
        success: true,
        unitId,
      });

      logger.debug('Unit created', {
        gameId: connection.gameId,
        playerId: player.id,
        unitId,
        unitType: data.unitType,
        position: { x: data.x, y: data.y },
      });
    } catch (error) {
      logger.error('Error creating unit:', error);
      handler.send(socket, PacketType.UNIT_CREATE_REPLY, {
        success: false,
        message: error instanceof Error ? error.message : 'Failed to create unit',
      });
    }
  }

  /**
   * Handle unit_action socket event
   */
  private async handleUnitActionEvent(
    socket: Socket,
    data: any,
    callback: (response: any) => void,
    io: Server
  ): Promise<void> {
    const connection = this.getConnection(socket, this.activeConnections);
    if (!this.isInGame(connection)) {
      callback({ success: false, error: 'Not in a game' });
      return;
    }

    try {
      const gameInstance = this.gameManager.getGameInstance(connection.gameId!);
      if (!gameInstance) {
        callback({ success: false, error: 'Game instance not found' });
        return;
      }

      // Get player ID from user
      let playerId: string | undefined = undefined;
      if (connection.userId) {
        const playerIds = Array.from(gameInstance.players.keys());
        for (const pid of playerIds) {
          const player = gameInstance.players.get(pid);
          if (player && player.userId === connection.userId) {
            playerId = pid;
            break;
          }
        }
      }

      if (!playerId) {
        callback({ success: false, error: 'Player not found' });
        return;
      }

      // Execute the unit action
      const result = await gameInstance.unitManager.executeUnitAction(
        data.unitId,
        data.actionType,
        data.targetX,
        data.targetY
      );

      if (result.success) {
        // If unit was destroyed (e.g., settler founding city), broadcast destruction
        if (result.unitDestroyed) {
          io.to(`game:${connection.gameId}`).emit('unit_destroyed', {
            gameId: connection.gameId,
            unitId: data.unitId,
          });
        } else {
          // Broadcast unit state updates if unit still exists
          const updatedUnit = gameInstance.unitManager.getUnit(data.unitId);
          if (updatedUnit) {
            io.to(`game:${connection.gameId}`).emit('unit_update', {
              gameId: connection.gameId,
              unit: updatedUnit,
            });
          }
        }

        callback({ success: true, result });
        logger.info(`Unit action executed successfully`, {
          unitId: data.unitId,
          actionType: data.actionType,
          playerId,
        });
      } else {
        callback({ success: false, error: result.message });
        logger.warn(`Unit action failed`, {
          unitId: data.unitId,
          actionType: data.actionType,
          error: result.message,
          playerId,
        });
      }
    } catch (error) {
      logger.error('Error executing unit action:', error);
      callback({
        success: false,
        error: error instanceof Error ? error.message : 'Failed to execute unit action',
      });
    }
  }

  /**
   * Handle path_request socket event
   */
  private async handlePathRequestEvent(
    socket: Socket,
    data: any,
    callback: (response: any) => void
  ): Promise<void> {
    const connection = this.getConnection(socket, this.activeConnections);
    if (!this.isAuthenticated(connection) || !this.isInGame(connection)) {
      if (typeof callback === 'function') {
        callback({ success: false, error: 'Not authenticated or not in a game' });
      }
      return;
    }

    try {
      const gameInstance = this.gameManager.getGameInstance(connection.gameId!);
      if (!gameInstance) {
        if (typeof callback === 'function') {
          callback({ success: false, error: 'Game instance not found' });
        }
        return;
      }

      // Get player ID from user
      let playerId: string | undefined = undefined;
      const playerIds = Array.from(gameInstance.players.keys());
      for (const pid of playerIds) {
        const player = gameInstance.players.get(pid);
        if (player && player.userId === connection.userId) {
          playerId = pid;
          break;
        }
      }

      if (!playerId) {
        if (typeof callback === 'function') {
          callback({ success: false, error: 'Player not found' });
        }
        return;
      }

      // Request pathfinding from GameManager
      const pathResult = await this.gameManager.requestPath(
        playerId,
        data.unitId,
        data.targetX,
        data.targetY
      );

      if (typeof callback === 'function') {
        callback(pathResult);
      }

      // Also emit to the socket for the PathfindingService listener
      const responseWithId = {
        ...pathResult,
        unitId: data.unitId,
        targetX: data.targetX,
        targetY: data.targetY,
      };

      socket.emit('path_response', responseWithId);

      logger.debug('Path request processed', {
        gameId: connection.gameId,
        playerId,
        unitId: data.unitId,
        targetX: data.targetX,
        targetY: data.targetY,
        success: pathResult.success,
      });
    } catch (error) {
      logger.error('Error processing path request:', error);
      const errorResponse = {
        success: false,
        error: error instanceof Error ? error.message : 'Failed to process path request',
        unitId: data.unitId,
        targetX: data.targetX,
        targetY: data.targetY,
        path: null,
      };

      if (typeof callback === 'function') {
        callback(errorResponse);
      }

      socket.emit('path_response', errorResponse);
    }
  }
}

/**
 * Ruleset loader service for loading and validating JSON-based rulesets
 * Provides type-safe, validated access to ruleset data with synchronous loading
 */

import { readFileSync } from 'fs';
import { join } from 'path';
import {
  TerrainRulesetFileSchema,
  type TerrainRulesetFile,
  type TerrainRuleset,
  type TerrainType,
  type MapgenTerrainProperty,
  BuildingsRulesetFileSchema,
  type BuildingsRulesetFile,
  type BuildingTypeRuleset,
  TechsRulesetFileSchema,
  type TechsRulesetFile,
  type TechnologyRuleset,
  UnitsRulesetFileSchema,
  type UnitsRulesetFile,
  type UnitTypeRuleset,
  GovernmentsRulesetFileSchema,
  type GovernmentsRulesetFile,
  type GovernmentRuleset,
  GameRulesetFileSchema,
  type GameRulesetFile,
  type GameParameters,
  type Civstyle,
  type GameOptions,
  EffectsRulesetFileSchema,
  type EffectsRulesetFile,
  type Effect,
  NationsRulesetFileSchema,
  type NationsRulesetFile,
  type NationRuleset,
  type TraitRange,
  type NationsCompatibility,
  type Requirement,
} from './schemas';

export class RulesetLoader {
  private static instance: RulesetLoader;
  private terrainCache = new Map<string, TerrainRulesetFile>();
  private buildingsCache = new Map<string, BuildingsRulesetFile>();
  private techsCache = new Map<string, TechsRulesetFile>();
  private unitsCache = new Map<string, UnitsRulesetFile>();
  private governmentsCache = new Map<string, GovernmentsRulesetFile>();
  private gameRulesCache = new Map<string, GameRulesetFile>();
  private effectsCache = new Map<string, EffectsRulesetFile>();
  private nationsCache = new Map<string, NationsRulesetFile>();
  private readonly baseDir: string;

  constructor(baseDir?: string) {
    // Use apps/shared/data/rulesets as base directory
    // Default to the directory where this file is located
    this.baseDir = baseDir || __dirname;
  }

  static getInstance(baseDir?: string): RulesetLoader {
    if (!RulesetLoader.instance) {
      RulesetLoader.instance = new RulesetLoader(baseDir);
    }
    return RulesetLoader.instance;
  }

  /**
   * Load terrain ruleset for a specific ruleset variant (e.g., 'classic', 'civ2')
   */
  loadTerrainRuleset(rulesetName: string = 'classic'): TerrainRulesetFile {
    // Check cache first
    if (this.terrainCache.has(rulesetName)) {
      return this.terrainCache.get(rulesetName)!;
    }

    try {
      const filePath = join(this.baseDir, rulesetName, 'terrain.json');
      const fileContent = readFileSync(filePath, 'utf-8');
      const rawData = JSON.parse(fileContent);

      // Validate with Zod schema
      const validatedData = TerrainRulesetFileSchema.parse(rawData);

      // Cache the validated data
      this.terrainCache.set(rulesetName, validatedData);

      return validatedData;
    } catch (error) {
      if (error instanceof Error) {
        throw new Error(`Failed to load terrain ruleset '${rulesetName}': ${error.message}`);
      }
      throw new Error(`Failed to load terrain ruleset '${rulesetName}': Unknown error`);
    }
  }

  /**
   * Get all terrain definitions for a ruleset
   */
  getTerrains(rulesetName: string = 'classic'): Record<TerrainType, TerrainRuleset> {
    const rulesetFile = this.loadTerrainRuleset(rulesetName);
    return rulesetFile.terrains;
  }

  /**
   * Get a specific terrain definition
   */
  getTerrain(terrainType: TerrainType, rulesetName: string = 'classic'): TerrainRuleset {
    const terrains = this.getTerrains(rulesetName);
    const terrain = terrains[terrainType];

    if (!terrain) {
      throw new Error(`Terrain type '${terrainType}' not found in ruleset '${rulesetName}'`);
    }

    return terrain;
  }

  /**
   * Pick terrain based on weighted selection - synchronous version of original function
   * @reference apps/server/src/game/map/TerrainRuleset.ts:269-333
   */
  pickTerrain(
    target: MapgenTerrainProperty,
    prefer: MapgenTerrainProperty,
    avoid: MapgenTerrainProperty,
    random: () => number,
    rulesetName: string = 'classic'
  ): TerrainType {
    const terrains = this.getTerrains(rulesetName);

    let sum = 0;
    const validTerrains: Array<{ terrain: TerrainType; weight: number }> = [];

    // Find the total weight - exact copy of freeciv logic
    for (const [terrainName, ruleset] of Object.entries(terrains)) {
      if (ruleset.notGenerated) continue; // Skip TER_NOT_GENERATED terrains

      // Check avoid condition
      if (avoid !== 'MG_UNUSED' && (ruleset.properties?.[avoid] ?? 0) > 0) {
        continue;
      }

      // Check prefer condition
      if (prefer !== 'MG_UNUSED' && (ruleset.properties?.[prefer] ?? 0) === 0) {
        continue;
      }

      // Calculate weight
      let weight: number;
      if (target !== 'MG_UNUSED') {
        weight = ruleset.properties?.[target] ?? 0;
      } else {
        weight = 1;
      }

      if (weight > 0) {
        sum += weight;
        validTerrains.push({ terrain: terrainName as TerrainType, weight });
      }
    }

    // If no valid terrains found, drop requirements and try again
    if (sum === 0) {
      if (prefer !== 'MG_UNUSED') {
        // Drop prefer requirement
        return this.pickTerrain(target, 'MG_UNUSED', avoid, random, rulesetName);
      } else if (avoid !== 'MG_UNUSED') {
        // Drop avoid requirement
        return this.pickTerrain(target, prefer, 'MG_UNUSED', random, rulesetName);
      } else {
        // Drop target requirement
        return this.pickTerrain('MG_UNUSED', prefer, avoid, random, rulesetName);
      }
    }

    // Now pick - exact copy of freeciv selection
    let pick = Math.floor(random() * sum);
    for (const { terrain, weight } of validTerrains) {
      if (pick < weight) {
        return terrain;
      }
      pick -= weight;
    }

    // Fallback (should never reach here)
    return 'grassland';
  }

  /**
   * Get terrain properties for a given terrain type
   */
  getTerrainProperties(
    terrainType: TerrainType,
    rulesetName: string = 'classic'
  ): Partial<Record<MapgenTerrainProperty, number>> {
