describe('Phase 3: makeLand() Restructuring Compliance', () => {
  let mapManager: MapManager;
  const testPlayers = new Map<string, PlayerState>([
    [
      'player1',
      {
        id: 'player1',
        userId: 'user1',
        playerNumber: 1,
        civilization: 'Romans',
